import{_ as e,o as a,c as i,a as t}from"./app.436197f7.js";const f=JSON.parse('{"title":"\u63ED\u79D8\u524D\u7AEF\u8BBE\u8BA1\u6A21\u5F0F\uFF08\u4E0A\uFF09","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48","slug":"\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48","link":"#\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48","children":[]},{"level":2,"title":"\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219","slug":"\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219","link":"#\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219","children":[]},{"level":2,"title":"\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF","slug":"\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF","link":"#\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF","children":[]},{"level":2,"title":"\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60","slug":"\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60","link":"#\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]}],"relativePath":"advanced-core-knowledage-front-end/bian-cheng-si-wei-he-suan-fa/037-jie-mi-qian-duan-she-ji-mo-shi-shang.md","lastUpdated":1677316569000}'),r={name:"advanced-core-knowledage-front-end/bian-cheng-si-wei-he-suan-fa/037-jie-mi-qian-duan-she-ji-mo-shi-shang.md"},n=t('<h1 id="\u63ED\u79D8\u524D\u7AEF\u8BBE\u8BA1\u6A21\u5F0F\uFF08\u4E0A\uFF09" tabindex="-1">\u63ED\u79D8\u524D\u7AEF\u8BBE\u8BA1\u6A21\u5F0F\uFF08\u4E0A\uFF09 <a class="header-anchor" href="#\u63ED\u79D8\u524D\u7AEF\u8BBE\u8BA1\u6A21\u5F0F\uFF08\u4E0A\uFF09" aria-hidden="true">#</a></h1><p>\u8BBE\u8BA1\u6A21\u5F0F\u2014\u2014\u6211\u8BA4\u4E3A\u8FD9\u662F\u4E00\u4E2A\u4E00\u8A00\u96BE\u5C3D\u7684\u6982\u5FF5\u3002\u7EF4\u57FA\u767E\u79D1\u5BF9\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B9A\u4E49\u4E3A\uFF1A</p><blockquote><p>\u5728\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\uFF08Design Pattern\uFF09\u662F\u5BF9\u8F6F\u4EF6\u8BBE\u8BA1\u4E2D\u666E\u904D\u5B58\u5728\uFF08\u53CD\u590D\u51FA\u73B0\uFF09\u7684\u5404\u79CD\u95EE\u9898\uFF0C\u6240\u63D0\u51FA\u7684\u89E3\u51B3\u65B9\u6848\u3002\u8FD9\u4E2A\u672F\u8BED\u662F\u7531\u57C3\u91CC\u5E0C\xB7\u4F3D\u739B\uFF08Erich Gamma\uFF09\u7B49\u4EBA\u5728 1990 \u5E74\u4EE3\u4ECE\u5EFA\u7B51\u8BBE\u8BA1\u9886\u57DF\u5F15\u5165\u5230\u8BA1\u7B97\u673A\u79D1\u5B66\u7684\u3002\u8BBE\u8BA1\u6A21\u5F0F\u5E76\u4E0D\u662F\u76F4\u63A5\u7528\u6765\u5B8C\u6210\u4EE3\u7801\u7684\u7F16\u5199\uFF0C\u800C\u662F\u63CF\u8FF0\u5728\u5404\u79CD\u4E0D\u540C\u60C5\u51B5\u4E0B\uFF0C\u8981\u600E\u4E48\u89E3\u51B3\u95EE\u9898\u7684\u4E00\u79CD\u65B9\u6848\u3002</p></blockquote><p>\u4E3A\u4EC0\u4E48\u201C\u4E00\u8A00\u96BE\u5C3D\u201D\u5462\uFF1F\u9996\u5148\u4ECE\u8BBE\u8BA1\u6A21\u5F0F\u7684\u6982\u5FF5\u53EF\u4EE5\u770B\u51FA\uFF1A\u8FD9\u662F\u4E00\u5957\u7406\u8BBA\uFF0C\u5E72\u5DF4\u5DF4\u7684\u63CF\u8FF0\u5176\u6240\u6709\u5185\u5BB9\u5E76\u6CA1\u6709\u592A\u5927\u610F\u4E49\u3002\u6211\u4EEC\u4E0D\u4F1A\u5728\u9762\u8BD5\u4E2D\u63D0\u51FA\uFF1A\u201C\u8BF7\u4F60\u89E3\u91CA\u4E00\u4E0B\u8BBE\u8BA1\u6A21\u5F0F\u201D\u3001\u201C\u4F60\u4F1A\u591A\u5C11\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u201D\u8FD9\u79CD\u95EE\u9898\u3002\u8BBE\u8BA1\u6A21\u5F0F\u4E00\u822C\u8BA4\u4E3A\u6709 23 \u79CD\uFF0C <strong>\u8FD9 23 \u79CD\u8BBE\u8BA1\u6A21\u5F0F\u7684\u672C\u8D28\u662F\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\u539F\u5219\u7684\u5B9E\u9645\u8FD0\u7528\uFF0C\u662F\u5BF9\u7C7B\u7684\u5C01\u88C5\u6027\u3001\u7EE7\u627F\u6027\u548C\u591A\u6001\u6027\uFF0C\u4EE5\u53CA\u7C7B\u7684\u5173\u8054\u5173\u7CFB\u548C\u7EC4\u5408\u5173\u7CFB\u7684\u603B\u7ED3\u5E94\u7528\u3002</strong></p><p>\u90A3\u4E48\u5BF9\u4E8E JavaScript \u6216\u8005\u524D\u7AEF\u5F00\u53D1\u6765\u8BF4\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\u4F3C\u4E4E\u662F\u4E00\u4E2A\u6709\u4E9B\u9065\u8FDC\u7684\u6982\u5FF5\u3002\u6211\u4EEC\u5E94\u8BE5\u5982\u4F55\u4E86\u89E3\u5E76\u5B66\u4E60\u8BBE\u8BA1\u6A21\u5F0F\u5462\uFF1F</p><p><strong>\u6211\u8BA4\u4E3A\u8BBE\u8BA1\u6A21\u5F0F\u4E0D\u80FD\u505C\u7559\u5728\u7406\u8BBA\u4E0A\uFF0C\u800C\u662F\u5E94\u8BE5\u7ED3\u5408\u5230\u5B9E\u9645\u4EE3\u7801\u5F53\u4E2D</strong> \u3002\u56E0\u6B64\u6253\u7B97\u901A\u8FC7\u4E24\u8BB2\u5185\u5BB9\u6765\u4ECB\u7ECD\uFF1A\u672C\u8BB2\u5185\u5BB9\u5148\u4ECB\u7ECD\u57FA\u672C\u6982\u5FF5\uFF0C\u5206\u4EAB\u4E00\u4E9B\u7ECF\u5178\u7684\u8BBE\u8BA1\u6A21\u5F0F\u4E66\u7C4D\u4EE5\u53CA\u76F8\u5173\u7ECF\u9A8C\uFF0C\u4E5F\u8BB8\u7A0D\u5FAE\u6709\u4E9B\u201C\u65E0\u8DA3\u201D\uFF1B\u7B2C\u4E8C\u8BB2\u5185\u5BB9\u5C06\u6DF1\u5165\u7ED3\u5408\u524D\u7AEF\u5F00\u53D1\uFF0C\u6311\u9009\u90A3\u4E9B\u6211\u4EEC\u4E00\u76F4\u4F7F\u7528\u7684\u3001\u4F1A\u7528\u5230\u7684\u8BBE\u8BA1\u6A21\u5F0F\u8FDB\u884C\u8BB2\u89E3\u3002</p><p>\u76F8\u5173\u77E5\u8BC6\u70B9\u5982\u4E0B\uFF1A</p><p><img src="https://images.gitbook.cn/cb2e6d60-cbfe-11e9-a9bd-857608719494" alt="enter image description here"></p><h2 id="\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48" tabindex="-1">\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48 <a class="header-anchor" href="#\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u4EC0\u4E48" aria-hidden="true">#</a></h2><p>\u4E4B\u524D\u63D0\u5230\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\u662F\u4E00\u79CD\u7ECF\u9A8C\u603B\u7ED3\uFF0C\u5B83\u5C31\u662F\u4E00\u5957\u5175\u6CD5\uFF0C\u4E00\u5171\u5305\u542B\u4E86 23 \u4E2A\u5957\u8DEF\u3002\u6700\u7EC8\u76EE\u7684\u662F\u4E3A\u4E86\u66F4\u597D\u7684\u4EE3\u7801\u91CD\u7528\u6027\u3001\u53EF\u8BFB\u6027\u3001\u53EF\u9760\u6027\u3001\u53EF\u7EF4\u62A4\u6027\u3002</p><p>\u5728\u5E73\u5E38\u5F00\u53D1\u4E2D\uFF0C\u201C\u4E5F\u8BB8\u4F60\u4E0D\u77E5\u9053\uFF0C\u4F46\u662F\u5DF2\u7ECF\u5728\u4F7F\u7528\u8BBE\u8BA1\u6A21\u5F0F\u4E86\u201D\u3002\u5728\u4E4B\u524D\u8BFE\u7A0B\u5185\u5BB9\u7684\u5B66\u4E60\u4E2D\uFF0C\u6211\u4EEC\u5176\u5B9E\u4E5F\u6709\u6240\u63D0\u53CA\uFF0C\u6BD4\u5982\u5355\u4F8B\u6A21\u5F0F\uFF08\u7EC6\u5FC3\u7684\u8BFB\u8005\u8FD8\u80FD\u627E\u5230\u5355\u4F8B\u6A21\u5F0F\u5B9E\u73B0\u7684\u8BFE\u7A0B\u51FA\u5904\u5417\uFF1F\uFF09\u3001\u53D1\u5E03\u8BA2\u9605\u6A21\u5F0F\u3001\u539F\u578B\u6A21\u5F0F\u7B49\u3002</p><p>\u5982\u679C\u5230\u6B64\uFF0C\u4ECD\u7136\u4E0D\u660E\u767D\u8BBE\u8BA1\u6A21\u5F0F\u5230\u5E95\u662F\u6307\u4EC0\u4E48\uFF0C\u522B\u7740\u6025\uFF0C\u8BF7\u7EE7\u7EED\u9605\u8BFB\u4EE5\u4E0B\u5185\u5BB9\u3002</p><h2 id="\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219" tabindex="-1">\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219 <a class="header-anchor" href="#\u8BBE\u8BA1\u6A21\u5F0F\u539F\u5219" aria-hidden="true">#</a></h2><p>\u65E2\u7136\u662F\u4E00\u5957\u7406\u8BBA\uFF0C\u662F\u4E00\u79CD\u7EA6\u5B9A\u548C\u89C4\u8303\uFF0C\u90A3\u4E48\u8BBE\u8BA1\u6A21\u5F0F\u4E5F\u5C31\u6709\u81EA\u5DF1\u7684\u6A21\u5F0F\u539F\u5219\u3002\u603B\u4F53\u6765\u8BF4\uFF0C\u5176\u516D\u5927\u539F\u5219\u5305\u62EC\uFF1A</p><ul><li>\u5F00\u95ED\u539F\u5219</li><li>\u91CC\u6C0F\u66FF\u6362\u539F\u5219</li><li>\u4F9D\u8D56\u53CD\u8F6C\u539F\u5219</li><li>\u63A5\u53E3\u9694\u79BB\u539F\u5219</li><li>\u6700\u5C0F\u77E5\u9053\u539F\u5219</li><li>\u5408\u6210\u590D\u7528\u539F\u5219</li></ul><p>\u5982\u56FE\uFF1A</p><p><img src="https://images.gitbook.cn/8112c980-cbfc-11e9-93b3-c35630e1847c" alt="enter image description here"></p><p>\u6211\u4EEC\u6765\u9010\u4E00\u4E86\u89E3\uFF1A</p><ul><li>\u5F00\u95ED\u539F\u5219\uFF08Open Close Principle\uFF09</li></ul><p>\u7406\u89E3\u5F00\u95ED\u539F\u5219\uFF0C\u5C31\u8981\u4E86\u89E3\u5F00\u548C\u95ED\u3002 <strong>\u8FD9\u91CC\u7684\u5F00\u662F\u6307\u5BF9\u6269\u5C55\u5F00\u653E\uFF0C\u95ED\u662F\u8BF4\u5BF9\u4FEE\u6539\u5173\u95ED</strong> \u3002\u60F3\u60F3\u6211\u4EEC\u6709\u4E00\u5957\u5B9E\u73B0\u3001\u63D0\u4F9B\u4E00\u4E2A\u670D\u52A1\uFF0C\u8FD9\u6837\u7684\u7A0B\u5E8F\u9700\u8981\u80FD\u591F\u968F\u65F6\u8FDB\u884C\u6269\u5C55\u3001\u968F\u65F6\u652F\u6301\u7B2C\u4E09\u65B9\u7684\u81EA\u5B9A\u4E49\u914D\u7F6E\uFF0C\u4F46\u662F\u4E0D\u80FD\u53BB\u4FEE\u6539\u5DF2\u7528\u7684\u5B9E\u73B0\u4EE3\u7801\u3002</p><p>\u6BD4\u5982\u6211\u4EEC\u505A\u4E86\u4E00\u4E2A UI \u7EC4\u4EF6\u8F6E\u5B50\uFF0C\u4E1A\u52A1\u65B9\u5728\u4F7F\u7528\u65F6\u663E\u7136\u4E0D\u80FD\u591F\u4FEE\u6539\u6211\u4EEC\u7684\u4EE3\u7801\uFF0C\u4F46\u662F\u4ECD\u7136\u53EF\u4EE5\u8FDB\u884C\u6269\u5C55\u3002\u518D\u6BD4\u5982\u8457\u540D\u7684 Draft.js \u5E93\uFF0C\u5728\u5B9E\u73B0\u4E00\u4E2A\u7F16\u8F91\u5668\u65F6\uFF0C\u63D0\u4F9B\u4E86\u7075\u6D3B\u7684\u63D2\u4EF6\u673A\u5236\uFF0C\u5B9E\u73B0\u4E86\u70ED\u63D2\u62D4\u6548\u679C\uFF0C\u4F7F\u5F97\u6574\u4E2A\u7A0B\u5E8F\u7684\u6269\u5C55\u6027\u597D\uFF0C\u6613\u4E8E\u7EF4\u62A4\u548C\u5347\u7EA7\u3002\u751A\u81F3 Redux \u5E93\u3001Koa \u5E93\u7B49\u57FA\u672C\u6240\u6709\u5E93\u90FD\u6709\u5F00\u95ED\u539F\u5219\u7684\u4F53\u73B0\u3002</p><p>\u5BF9\u4E8E\u9762\u5411\u5BF9\u8C61\u7C7B\u578B\u7684\u8BED\u8A00\u6765\u8BF4\uFF0C\u60F3\u8981\u4E25\u683C\u9075\u5B88\u5F00\u95ED\u539F\u5219\uFF0C\u5F80\u5F80\u9700\u8981\u4F7F\u7528\u63A5\u53E3\u548C\u62BD\u8C61\u7C7B\uFF0C\u8FD9\u4E2A\u6211\u4EEC\u4F1A\u5728\u5177\u4F53\u8BBE\u8BA1\u4E2D\u518D\u6B21\u63D0\u5230\u3002</p><ul><li>\u91CC\u6C0F\u66FF\u6362\u539F\u5219\uFF08Liskov Substitution Principle\uFF09</li></ul><p>\u91CC\u6C0F\u4EE3\u6362\u539F\u5219\u5C31\u7A0D\u5FAE\u6709\u4E9B\u62BD\u8C61\uFF0C\u4F46\u5B83\u662F\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\u7684\u57FA\u672C\u539F\u5219\u4E4B\u4E00\u3002</p><blockquote><p>\u91CC\u6C0F\u4EE3\u6362\u539F\u5219\u8981\u6C42\uFF0C\u4EFB\u4F55\u57FA\u7C7B\u53EF\u4EE5\u53D1\u6325\u4F5C\u7528\u7684\u5730\u65B9\uFF0C\u5B50\u7C7B\u4E00\u5B9A\u53EF\u4EE5\u53D1\u6325\u4F5C\u7528\u3002</p></blockquote><p>\u8FD9\u53E5\u8BDD\u600E\u4E48\u7406\u89E3\u5462\uFF1F\u60F3\u60F3\u6211\u4EEC\u7684\u7EE7\u627F\u5B9E\u73B0\uFF0C\u91CC\u6C0F\u66FF\u6362\u539F\u5219\u5C31\u662F\u7EE7\u627F\u590D\u7528\u7684\u57FA\u7840\u3002\u53EA\u6709\u5F53\u6D3E\u751F\u7C7B\u53EF\u4EE5\u968F\u65F6\u66FF\u6362\u6389\u5176\u57FA\u7C7B\uFF0C\u540C\u65F6\u529F\u80FD\u4E0D\u88AB\u7834\u574F\uFF0C\u57FA\u7C7B\u7684\u65B9\u6CD5\u4ECD\u7136\u80FD\u88AB\u4F7F\u7528\uFF0C\u8FD9\u624D\u662F\u771F\u6B63\u7684\u7EE7\u627F\uFF0C\u7EE7\u627F\u624D\u80FD\u771F\u6B63\u5730\u5B9E\u73B0\u590D\u7528\uFF0C\u5F53\u7136\uFF0C\u6D3E\u751F\u7C7B\u4E5F\u9700\u8981\u968F\u65F6\u80FD\u591F\u5728\u57FA\u7C7B\u7684\u57FA\u7840\u4E0A\u589E\u52A0\u65B0\u7684\u884C\u4E3A\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u91CC\u6C0F\u4EE3\u6362\u539F\u5219\u662F\u5BF9\u5F00\u95ED\u539F\u5219\u7684\u8865\u5145\u3002</p><ul><li>\u4F9D\u8D56\u53CD\u8F6C\u539F\u5219\uFF08Dependence Inversion Principle\uFF09</li></ul><p>\u8BE5\u539F\u5219\u8981\u6C42\u9488\u5BF9\u63A5\u53E3\u7F16\u7A0B\uFF0C\u4F9D\u8D56\u4E8E\u62BD\u8C61\u3002\u66F4\u591A\u7406\u8BBA\u5185\u5BB9\u6211\u5E76\u4E0D\u6253\u7B97\u5C55\u5F00\uFF0C\u540E\u7EED\u5728\u7A0B\u5E8F\u8BBE\u8BA1\u4E2D\u4F1A\u7ED3\u5408\u5B9E\u4F8B\u63D0\u53CA\u3002</p><ul><li>\u63A5\u53E3\u9694\u79BB\u539F\u5219\uFF08Interface Segregation Principle\uFF09</li></ul><p>\u63A5\u53E3\u9694\u79BB\u7684\u610F\u601D\u6216\u8005\u76EE\u7684\u662F\u51CF\u5C11\u8026\u5408\u7684\u51FA\u73B0\u3002\u5728\u5927\u578B\u8F6F\u4EF6\u67B6\u6784\u4E2D\uFF0C\u4F7F\u7528\u591A\u4E2A\u76F8\u4E92\u9694\u79BB\u7684\u63A5\u53E3\uFF0C\u4E00\u5B9A\u6BD4\u4F7F\u7528\u5355\u4E2A\u5927\u800C\u5168\u7684\u63A5\u53E3\u8981\u597D\u3002</p><ul><li>\u6700\u5C11\u77E5\u9053\u539F\u5219\uFF0C\u53C8\u79F0\u8FEA\u7C73\u7279\u6CD5\u5219\uFF08Demeter Principle\uFF09</li></ul><p>\u6700\u5C11\u77E5\u9053\u987E\u540D\u601D\u4E49\uFF0C\u662F\u6307\uFF1A\u4E00\u4E2A\u7CFB\u7EDF\u7684\u529F\u80FD\u6A21\u5757\u5E94\u8BE5\u6700\u5927\u9650\u5EA6\u5730\u4E0D\u77E5\u6653\u5176\u4ED6\u6A21\u5757\u7684\u51FA\u73B0\uFF0C\u51CF\u5C11\u611F\u77E5\uFF0C\u6A21\u5757\u5E94\u76F8\u5BF9\u72EC\u7ACB\u3002</p><ul><li>\u5408\u6210\u590D\u7528\u539F\u5219\uFF08Composite Reuse Principle\uFF09</li></ul><p>\u5408\u6210\u590D\u7528\u539F\u5219\u662F\u6307\uFF1A\u5C3D\u91CF\u4F7F\u7528\u5408\u6210 / \u805A\u5408\u7684\u65B9\u5F0F\uFF0C\u800C\u4E0D\u662F\u4F7F\u7528\u7EE7\u627F\u3002\u8FD9\u662F\u5F88\u6709\u610F\u601D\u7684\u4E00\u70B9\uFF0C\u6211\u4EEC\u5728\u4E4B\u524D\u7684\u8BFE\u7A0B\u4E2D\u63D0\u5230\u8FC7\uFF1A\u57FA\u4E8E\u539F\u578B\u7684\u7EE7\u627F\u5728\u5F88\u591A\u7A0B\u5EA6\u4E0A\u201C\u4F18\u4E8E\u201D\u57FA\u4E8E\u7C7B\u7684\u7EE7\u627F\uFF0C\u539F\u56E0\u5C31\u5728\u4E8E\u57FA\u4E8E\u539F\u578B\u7684\u7EE7\u627F\u6A21\u5F0F\u4F53\u73B0\u4E86\u53EF\u7EC4\u5408\u6027\uFF0C\u80FD\u591F\u89C4\u907F\u201C\u5927\u7329\u7329\u548C\u9999\u8549\u201D\u7B49\u95EE\u9898\u7684\u51FA\u73B0\u3002\u7EC4\u5408\u662F\u975E\u5E38\u4F18\u79C0\u7684\u7F16\u7A0B\u601D\u60F3\uFF0C\u8FD9\u4E00\u70B9\u5728\u51FD\u6570\u5F0F\u7F16\u7A0B\u8303\u7574\u4E2D\u5F97\u5230\u4E86\u6700\u5927\u7A0B\u5EA6\u7684\u5370\u8BC1\u3002</p><h2 id="\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF" tabindex="-1">\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF <a class="header-anchor" href="#\u8BBE\u8BA1\u6A21\u5F0F\u7684\u4E09\u5927\u7C7B\u578B\u548C\u4E8C\u5341\u4E09\u79CD\u5957\u8DEF" aria-hidden="true">#</a></h2><p>\u8BBE\u8BA1\u6A21\u5F0F\u5E76\u6CA1\u6709\u4EC0\u4E48\u56F0\u96BE\u7684\uFF0C\u5927\u4F53\u4E0A\u6240\u6709\u7684\u8BBE\u8BA1\u6A21\u5F0F\u53EF\u4EE5\u5F52\u7ED3\u4E3A\u4E09\u5927\u7C7B\uFF1A</p><ul><li>\u521B\u5EFA\u578B</li><li>\u7ED3\u6784\u578B</li><li>\u884C\u4E3A\u578B</li></ul><p>\u5982\u56FE\uFF1A</p><p><img src="https://images.gitbook.cn/1190fdb0-cbfd-11e9-942c-915593d3f51c" alt="enter image description here"></p><p>\u5BF9\u4E8E Java \u6765\u8BF4\uFF0C\u5B83\u8FD8\u5305\u62EC\u4E86 J2EE \u7C7B\u578B\u8BBE\u8BA1\u6A21\u5F0F\u3002</p><p>\u6211\u4EEC\u5206\u522B\u6765\u770B\uFF1A</p><ul><li>\u521B\u5EFA\u578B\uFF08Creational Patterns\uFF09</li></ul><p>\u521B\u5EFA\u578B\u7684\u4E94\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u63D0\u4F9B\u4E86\u66F4\u52A0\u7075\u6D3B\u7684\u5BF9\u8C61\u521B\u5EFA\u65B9\u5F0F\uFF0C\u540C\u65F6\u53EF\u4EE5\u9690\u85CF\u521B\u5EFA\u7684\u5177\u4F53\u903B\u8F91\u3002\u4E0E\u76F4\u63A5\u4F7F\u7528 new \u8FD0\u7B97\u7B26\u5B9E\u4F8B\u5316\u5BF9\u8C61\u76F8\u6BD4\uFF0C\u8FD9\u4E9B\u6A21\u5F0F\u5177\u6709\u66F4\u5F3A\u7684\u7075\u6D3B\u6027\u4EE5\u53CA\u53EF\u5B9A\u5236\u6027\u3002</p><ul><li>\u7ED3\u6784\u578B\uFF08Structural Patterns\uFF09</li></ul><p>\u7ED3\u6784\u578B\u7684\u4E03\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u5173\u6CE8\u7C7B\u548C\u5BF9\u8C61\u7684\u7EC4\u5408\uFF0C\u7ED3\u5408\u7EE7\u627F\u7684\u6982\u5FF5\uFF0C\u8FD9\u4E9B\u8BBE\u8BA1\u6A21\u5F0F\u80FD\u4F7F\u5F97\u5BF9\u8C61\u5177\u6709\u66F4\u52A0\u7075\u6D3B\u7684\u529F\u80FD\u8BBE\u5B9A\u3002</p><ul><li>\u884C\u4E3A\u578B\uFF08Behavioral Patterns\uFF09</li></ul><p>\u884C\u4E3A\u578B\u7684\u5341\u4E00\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u805A\u7126\u4E8E\u5BF9\u8C61\u548C\u7C7B\u4E4B\u95F4\u7684\u901A\u4FE1\uFF0C\u8FD9\u662F\u6784\u5EFA\u5927\u578B\u7A0B\u5E8F\u67B6\u6784\u5FC5\u4E0D\u53EF\u5C11\u7684\u73AF\u8282\u3002</p><h2 id="\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60" tabindex="-1">\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60 <a class="header-anchor" href="#\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60" aria-hidden="true">#</a></h2><p>\u8BBE\u8BA1\u6A21\u5F0F\u4F7F\u4EE3\u7801\u7F16\u5199\u771F\u6B63\u5DE5\u7A0B\u5316\uFF0C\u6211\u4EEC\u8BF4\u8BBE\u8BA1\u6A21\u5F0F\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u7684\u57FA\u77F3\u8109\u7EDC\uFF0C\u5982\u540C\u5927\u53A6\u7684\u7ED3\u6784\u4E00\u6837\u3002\u5176\u5B9E\u6211\u8BA4\u4E3A\u6CA1\u6709\u5FC5\u8981\u523B\u610F\u5730\u53BB\u5B66\u4E60\u8BBE\u8BA1\u6A21\u5F0F\uFF0C\u56E0\u4E3A\u6709\u5173\u8BBE\u8BA1\u6A21\u5F0F\u7684\u601D\u60F3\u4E00\u5B9A\u662F\u5728\u5B9E\u9645\u5DE5\u7A0B\u5F00\u53D1\u4E2D\u6162\u6162\u4F53\u4F1A\u603B\u7ED3\u7684\u3002\u4F46\u662F\u8FD9\u9700\u8981\u5F00\u53D1\u8005\u505A\u5230\u201C\u975E\u5E38\u6709\u5FC3\u201D\uFF0C\u624D\u80FD\u591F\u81EA\u5DF1\u53BB\u6162\u6162\u79EF\u7D2F\uFF0C\u4E3A\u4E86\u80FD\u591F\u57F9\u517B\u8FD9\u79CD\u201C\u7528\u5FC3\u201D\uFF0C\u8BFB\u8005\u53BB\u4E13\u95E8\u4E86\u89E3\u8BBE\u8BA1\u6A21\u5F0F\u4F3C\u4E4E\u4E5F\u662F\u4E00\u79CD\u6377\u5F84\u548C\u65B9\u5F0F\u3002\u4E24\u8282\u8BFE\u7A0B\u7684\u8BBE\u7F6E\u8DB3\u4EE5\u5E2E\u52A9\u5927\u5BB6\u57F9\u517B\u8BBE\u8BA1\u6A21\u5F0F\u601D\u60F3\uFF0C\u540C\u65F6\u6211\u518D\u5206\u4EAB\u4E00\u4E9B\u5173\u4E8E\u8BBE\u8BA1\u6A21\u5F0F\u7684\u7ECF\u5178\u8D44\u6599\uFF1A</p><ul><li><a href="https://github.com/kamranahmedse/design-patterns-for-humans" target="_blank" rel="noreferrer">design-patterns-for-humans</a>\uFF1A\u8FD9\u662F\u4E00\u672C\u975E\u5E38\u8457\u540D\u7684\u8BBE\u8BA1\u6A21\u5F0F\u4E66 pdf</li><li><a href="https://github.com/guanguans/design-patterns-for-humans-cn" target="_blank" rel="noreferrer">design-patterns-for-humans-cn</a>\uFF1A\u4E0A\u672C\u4E66\u7684\u4E2D\u6587\u7248 pdf</li><li><a href="https://addyosmani.com/resources/essentialjsdesignpatterns/book/" target="_blank" rel="noreferrer">Learning JavaScript Design Patterns</a>\uFF1Aaddyosmani \u5927\u795E\u7684\u4E66 pdf</li><li><a href="https://design-patterns.readthedocs.io/zh_CN/latest/" target="_blank" rel="noreferrer">\u56FE\u8BF4\u8BBE\u8BA1\u6A21\u5F0F</a></li></ul><p>\u5176\u4E2D\u5F3A\u70C8\u63A8\u8350\u300ALearning JavaScript Design Patterns\u300B\uFF0C\u8FD9\u672C\u4E66\u5728\u7F51\u4E0A\u5F00\u6E90\u514D\u8D39\uFF0C\u5176\u4E2D\u7684\u5185\u5BB9\u793A\u4F8B\u90FD\u662F\u7528 JavaScript \u7F16\u5199\u7684\uFF0C\u800C\u4E14\u5728\u4EE3\u7801\u5B9E\u4F8B\u7F16\u5199\u5F53\u4E2D\u5256\u6790\u4E86\u5F88\u591A jQuery \u7B49\u7ECF\u5178\u201C\u8F6E\u5B50\u201D\u7684\u8BBE\u8BA1\u3002</p><p>\u540C\u65F6 GitHub \u4E0A\u4E5F\u6709\u4E00\u4E2A\u4E0D\u9519\u7684 repo\uFF1A<a href="https://github.com/DavidCai1993/JsPattern-ES6" target="_blank" rel="noreferrer">JsPattern- ES6</a>\uFF0C\u4F7F\u7528 ES6 \u91CD\u5199\u4E86\u300AJavaScript \u6A21\u5F0F\u300B\u4E00\u4E66\u4E2D\u7684\u6837\u4F8B\u3002</p><p>\u8FD8\u6709\u4E00\u4E2A\u201C\u795E\u5668\u201D\u662F\uFF1A<a href="http://loredanacirstea.github.io/es6-design-%20patterns/#composite" target="_blank" rel="noreferrer">es6-design-patterns</a>\uFF0C\u5982\u622A\u56FE\uFF1A</p><p><img src="https://images.gitbook.cn/765be890-cbfd-11e9-a9bd-857608719494" alt="enter image description here"></p><p>\u8FD9\u4E2A\u7F51\u7AD9\u901A\u8FC7 UML \u56FE\u89E3\u91CA\u8BBE\u8BA1\u6A21\u5F0F\uFF0C\u540C\u65F6\u914D\u4EE5\u53EF\u4EE5\u8FD0\u884C\u7684\u4EE3\u7801\u793A\u4F8B\uFF0C\u975E\u5E38\u65B9\u4FBF\u5BF9\u6BCF\u4E00\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u8FDB\u884C\u5B66\u4E60\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u672C\u8BB2\u4ECB\u7ECD\u4E86\u8BBE\u8BA1\u6A21\u5F0F\u7684\u57FA\u7840\u7406\u8BBA\u4EE5\u53CA\u5B66\u4E60\u65B9\u5F0F\uFF0C\u5176\u4E2D\u6709\u4E00\u4E9B\u6982\u5FF5\u8F83\u4E3A\u62BD\u8C61\uFF0C\u8BFB\u8005\u4E0D\u5FC5\u5B8C\u5168\u660E\u767D\uFF0C\u76F8\u4FE1\u901A\u8FC7\u4E0B\u4E00\u8BB2\u7684\u5B66\u4E60\uFF0C\u6211\u4EEC\u7ED3\u5408\u524D\u7AEF\u5B9E\u4F8B\uFF0C\u4E00\u5B9A\u4F1A\u6709\u66F4\u6DF1\u5165\u7684\u4F53\u4F1A\u3002</p>',58),l=[n];function p(s,o,c,d,h,u){return a(),i("div",null,l)}const m=e(r,[["render",p]]);export{f as __pageData,m as default};
