import{_ as e,o as a,c as l,a as n}from"./app.a9f825fd.js";const b=JSON.parse('{"title":"\u6027\u80FD\u4F18\u5316\u95EE\u9898\uFF0C\u8001\u53F8\u673A\u5982\u4F55\u89E3\u51B3\uFF08\u4E0A\uFF09","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5F00\u653E\u4F8B\u9898\u5B9E\u6218","slug":"\u5F00\u653E\u4F8B\u9898\u5B9E\u6218","link":"#\u5F00\u653E\u4F8B\u9898\u5B9E\u6218","children":[{"level":3,"title":"WebP \u56FE\u7247\u4F18\u5316","slug":"webp-\u56FE\u7247\u4F18\u5316","link":"#webp-\u56FE\u7247\u4F18\u5316","children":[]},{"level":3,"title":"\u6309\u9700\u52A0\u8F7D\u4F18\u5316","slug":"\u6309\u9700\u52A0\u8F7D\u4F18\u5316","link":"#\u6309\u9700\u52A0\u8F7D\u4F18\u5316","children":[]},{"level":3,"title":"\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316","slug":"\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316","link":"#\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316","children":[]}]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]},{"level":2,"title":"\u5206\u4EAB\u4EA4\u6D41","slug":"\u5206\u4EAB\u4EA4\u6D41","link":"#\u5206\u4EAB\u4EA4\u6D41","children":[]}],"relativePath":"advanced-core-knowledage-front-end/xing-neng-you-hua/033-xing-neng-you-hua-wen-ti-lao-si-ji-ru-he-jie-jue-shang.md","lastUpdated":1676386809000}'),p={name:"advanced-core-knowledage-front-end/xing-neng-you-hua/033-xing-neng-you-hua-wen-ti-lao-si-ji-ru-he-jie-jue-shang.md"},s=n(`<h1 id="\u6027\u80FD\u4F18\u5316\u95EE\u9898\uFF0C\u8001\u53F8\u673A\u5982\u4F55\u89E3\u51B3\uFF08\u4E0A\uFF09" tabindex="-1">\u6027\u80FD\u4F18\u5316\u95EE\u9898\uFF0C\u8001\u53F8\u673A\u5982\u4F55\u89E3\u51B3\uFF08\u4E0A\uFF09 <a class="header-anchor" href="#\u6027\u80FD\u4F18\u5316\u95EE\u9898\uFF0C\u8001\u53F8\u673A\u5982\u4F55\u89E3\u51B3\uFF08\u4E0A\uFF09" aria-hidden="true">#</a></h1><p>\u4E00\u76F4\u4EE5\u6765\uFF0C\u6027\u80FD\u4F18\u5316\u662F\u524D\u7AEF\u7684\u91CD\u8981\u8BFE\u9898\uFF0C\u4E0D\u4EC5\u5B9E\u5B9E\u5728\u5728\u5F71\u54CD\u4EA7\u54C1\u6027\u80FD\uFF0C\u5728\u9762\u8BD5\u73AF\u8282\u4E5F\u4F1A\u88AB\u53CD\u590D\u63D0\u53CA\u3002\u65E0\u8BBA\u5E94\u8058\u8005\u662F\u521D\u5165\u524D\u7AEF\u7684\u65B0\u624B\uFF0C\u8FD8\u662F\u5DE5\u4F5C\u7ECF\u9A8C\u4E30\u5BCC\u7684\u8001\u53F8\u673A\uFF0C\u9762\u8BD5\u5B98\u90FD\u80FD\u5728\u6027\u80FD\u65B9\u9762\u627E\u5230\u5408\u9002\u7684\u5207\u5165\u70B9\uFF0C\u5BF9\u5019\u9009\u4EBA\u8FDB\u884C\u8003\u5BDF\u3002\u4F5C\u4E3A\u7A0B\u5E8F\u5458\uFF0C\u5E94\u8BE5\u5982\u4F55\u5728\u5E73\u65F6\u5B66\u4E60\u3001\u5DE5\u4F5C\u4E2D\u79EF\u7D2F\u6027\u80FD\u4F18\u5316\u65B9\u9762\u7684\u7ECF\u9A8C\uFF0C\u4FDD\u969C\u4EA7\u54C1\u987A\u7545\u4F53\u9A8C\uFF1F\u4F5C\u4E3A\u9762\u8BD5\u8005\uFF0C\u5982\u4F55\u5728\u9762\u8BD5\u6D41\u7A0B\u4E2D\u51FA\u8272\u5730\u56DE\u7B54\u6027\u80FD\u76F8\u5173\u95EE\u9898\uFF1F</p><p><strong>\u524D\u7AEF\u6027\u80FD\u662F\u4E00\u4E2A\u592A\u8FC7\u5BBD\u6CDB\u7684\u8BDD\u9898\uFF0C\u8131\u79BB\u573A\u666F\u548C\u9700\u6C42\u8C08\u6027\u80FD\u5F80\u5F80\u6BEB\u65E0\u610F\u4E49</strong> \u3002\u6211\u76F8\u4FE1\u5F88\u5C11\u6709\u9762\u8BD5\u5B98\u4F1A\u76F4\u63A5\u628A\uFF1A\u201C\u524D\u7AEF\u5982\u4F55\u4F18\u5316\u6027\u80FD\uFF1F\u201D\u2014\u2014\u8FD9\u6837\u4E00\u4E2A\u7A7A\u67B6\u5B50\u95EE\u9898\u629B\u51FA\u7684\u3002\u4E5F\u4E0D\u4F1A\u6709\u6280\u672F\u7ECF\u7406\u76F4\u63A5\u4E22\u7ED9\u4F60\u201C\u628A\u4EA7\u54C1\u6027\u80FD\u63D0\u5347\u4E00\u4E9B\u201D\u8FD9\u6837\u7684\u9879\u76EE\u3002\u6BD5\u7ADF\u8FD9\u6837\u7684\u95EE\u9898\u8FC7\u5927\uFF0C\u6839\u672C\u8BA9\u4EBA\u65E0\u5904\u4E0B\u624B\u3002\u6211\u4EEC\u8FD8\u9700\u8981\u9488\u5BF9\u5177\u4F53\u573A\u666F\u548C\u74F6\u9888\u6765\u5206\u6790\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u679C\u771F\u7684\u6709\u9762\u8BD5\u5B98\u8FD9\u4E48\u95EE\u4E86\u5462\uFF1F</p><p>\u5982\u679C\u662F\u6211\uFF0C\u6211\u4E5F\u8BB8\u4F1A\u8FD9\u6837\u56DE\u7B54\uFF1A</p><blockquote><p>\u524D\u7AEF\u6027\u80FD\u6D89\u53CA\u65B9\u65B9\u9762\u9762\uFF0C\u4F18\u5316\u89D2\u5EA6\u5207\u5165\u70B9\u90FD\u6709\u6240\u4E0D\u540C\u3002\u6211\u8BA4\u4E3A\uFF0C\u4E3B\u8981\u53EF\u4EE5\u5206\u4E3A\uFF1A\u9875\u9762\u5DE5\u7A0B\u4F18\u5316\u548C\u4EE3\u7801\u7EC6\u8282\u4F18\u5316\u4E24\u5927\u65B9\u5411\u3002</p></blockquote><p><strong>\u9875\u9762\u5DE5\u7A0B\u4F18\u5316</strong> \u4ECE\u9875\u9762\u8BF7\u6C42\u5F00\u59CB\uFF0C\u6D89\u53CA\u7F51\u7EDC\u534F\u8BAE\u3001\u8D44\u6E90\u914D\u7F6E\u3001\u6D4F\u89C8\u5668\u6027\u80FD\u3001\u7F13\u5B58\u7B49\uFF1B <strong>\u4EE3\u7801\u7EC6\u8282\u4F18\u5316</strong> \u4E0A\u76F8\u5BF9\u96F6\u6563\uFF0C\u6BD4\u5982 JavaScript \u5BF9 DOM \u64CD\u4F5C\uFF0C\u5BBF\u4E3B\u73AF\u5883\u7684\u5355\u7EBF\u7A0B\u76F8\u5173\u5185\u5BB9\u7B49\u3002</p><p>\u4E5F\u6B63\u5982\u4E0A\u6240\u7B54\uFF0C\u672C\u8282\u8BFE\u7A0B\u4E5F\u4F1A\u57FA\u4E8E\u4EE5\u4E0B\u4E24\u4E2A\u5927\u65B9\u5411\u7684\u76F8\u5173\u77E5\u8BC6\u8FDB\u884C\u68B3\u7406\uFF1A</p><ul><li><strong>\u9875\u9762\u5DE5\u7A0B\u4F18\u5316</strong></li><li><strong>\u4EE3\u7801\u7EC6\u8282\u4F18\u5316</strong></li></ul><p>\u4E3A\u4E86\u66F4\u597D\u5730\u8FD8\u539F\u771F\u5B9E\u573A\u666F\uFF0C\u8FD9\u4E24\u65B9\u9762\u6211\u90FD\u5C06\u914D\u5408\u4E24\u7C7B\u9762\u8BD5\u9898\u76EE\u6765\u89E3\u6790\uFF1A</p><ul><li>\u5F00\u653E\u4F8B\u9898\u5B9E\u6218</li><li>\u4EE3\u7801\u4F8B\u9898\u5B9E\u6218</li></ul><p>\u8FD9\u4E2A\u4E3B\u9898\u7684\u77E5\u8BC6\u70B9\u5982\u4E0B\uFF1A</p><p><img src="https://images.gitbook.cn/a4889460-4ecc-11e9-8044-3de24c2bc492" alt=""></p><p>\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u901A\u8FC7 2 \u8282\u5185\u5BB9\u6765\u5B66\u4E60\u8FD9\u4E2A\u4E3B\u9898\u3002</p><h2 id="\u5F00\u653E\u4F8B\u9898\u5B9E\u6218" tabindex="-1">\u5F00\u653E\u4F8B\u9898\u5B9E\u6218 <a class="header-anchor" href="#\u5F00\u653E\u4F8B\u9898\u5B9E\u6218" aria-hidden="true">#</a></h2><p>\u5982\u4E0A\u5206\u6790\uFF0C\u9762\u8BD5\u5B98\u5F80\u5F80\u4F1A\u6839\u636E\u9762\u8BD5\u8005\u7684\u5B9E\u9645\u7ECF\u9A8C\u6216\u8005\u6027\u80FD\u7684\u67D0\u4E00\u7EC6\u5206\u65B9\u5411\uFF0C\u8FDB\u884C\u6DF1\u5EA6\u63D0\u95EE\uFF0C\u4EE5\u4E86\u89E3\u5019\u9009\u4EBA\u7684\u77E5\u8BC6\u50A8\u5907\u4EE5\u53CA\u5728\u4EE5\u5F80\u9879\u76EE\u4E2D\u7684\u8868\u73B0\u3002</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u9762\u8BD5\u8005\uFF0C\u5305\u62EC\u5728\u8682\u8681\u91D1\u670D\u3001\u963F\u91CC\u6DD8\u5B9D\u67D0\u56E2\u961F\u3001\u5934\u6761\u3001\u7F8E\u56E2\u4EE5\u53CA\u5176\u4ED6\u516C\u53F8\u7684\u591A\u6B21\u9762\u8BD5\u6D41\u7A0B\u4E2D\uFF0C\u6211\u66FE\u7ECF\u88AB\u95EE\u5230\u8FC7\uFF1A <strong>\u201C\u5728\u5E73\u65F6\u5DE5\u4F5C\u4E2D\u505A\u8FC7\u54EA\u4E9B\u6027\u80FD\u4F18\u5316\u65B9\u9762\u7684\u9879\u76EE\uFF1F\u201D</strong></p><p>\u6211\u662F\u8FD9\u6837\u56DE\u7B54\u7684\uFF1A</p><blockquote><p>\u56E0\u4E3A\u6211\u670D\u52A1\u7684\u662F\u6709\u4EBF\u7EA7\u6D41\u91CF\u7684 To C \u578B\u4EA7\u54C1\uFF0C\u56E0\u6B64\u5E73\u65F6\u5DE5\u4F5C\u4E2D\uFF0C\u5728\u6027\u80FD\u4F18\u5316\u65B9\u9762\u4E00\u76F4\u6301\u7EED\u8FDB\u884C\u63A2\u7D22\u548C\u8FED\u4EE3\u3002\u9664\u4E86\u4EE3\u7801\u7EC6\u8282\u65B9\u9762\u5916\uFF0C\u8F83\u5927\u578B\u5DE5\u7A0B\u4F18\u5316\u4E3B\u8981\u6709 WebP \u56FE\u7247\u683C\u5F0F\u66FF\u6362\u3001\u8D44\u6E90\u6253\u5305\u548C\u9006\u5411\u4EE3\u7801\u62C6\u5206\uFF08\u6309\u9700\u52A0\u8F7D\uFF09\u7B49\u3002</p></blockquote><h3 id="webp-\u56FE\u7247\u4F18\u5316" tabindex="-1">WebP \u56FE\u7247\u4F18\u5316 <a class="header-anchor" href="#webp-\u56FE\u7247\u4F18\u5316" aria-hidden="true">#</a></h3><p>\u56E0\u4E3A\u5E76\u4E0D\u77E5\u9053\u9762\u8BD5\u5B98\u9700\u8981\u8003\u5BDF\u7684\u7A0B\u5EA6\uFF0C\u4EE5\u4E0A\u56DE\u7B54\u53EF\u4EE5\u907F\u514D\u81EA\u5DF1\u201C\u4F83\u4F83\u800C\u8C08\u201D\u6D6A\u8D39\u65F6\u95F4\u7684\u5C34\u5C2C\u3002\u5728\u8FD9\u6837\u7684\u9762\u8BD5\u573A\u666F\u4E2D\uFF0C\u6211\u5F80\u5F80\u4F1A\u628A\u4E3B\u52A8\u6743\u4EA4\u7ED9\u9762\u8BD5\u5B98\u3002\u5927\u90E8\u5206\u9762\u8BD5\u5B98\u4F1A\u7EE7\u7EED\u8FFD\u95EE\uFF0C\u6BD4\u5982\u4ED6\u5BF9 WebP \u56FE\u7247\u683C\u5F0F\u4F18\u5316\u9879\u76EE\u611F\u5174\u8DA3\uFF0C <strong>\u90A3\u6211\u4F1A\u4ECE\u9879\u76EE\u7684\u7ACB\u9879\u3001\u5B9E\u65BD\u3001\u6536\u76CA\u7684\u89D2\u5EA6\u8FDB\u884C\u89E3\u7B54\uFF0C\u8868\u73B0\u4F5C\u4E3A\u4E00\u4E2A\u9879\u76EE\u8D1F\u8D23\u4EBA\u5BF9\u4F18\u5316\u9879\u76EE\u7684\u7406\u89E3</strong> \uFF1A</p><blockquote><p>\u6211\u4EEC\u7684\u4EA7\u54C1\u9875\u9762\u4E2D\uFF0C\u5F80\u5F80\u5B58\u5728\u5927\u91CF\u7684\u56FE\u7247\u5185\u5BB9\uFF0C\u56E0\u6B64\u56FE\u7247\u7684\u6027\u80FD\u4F18\u5316\u662F\u74F6\u9888\u548C\u91CD\u70B9\u3002\u9664\u4E86\u4F20\u7EDF\u7684\u56FE\u7247\u61D2\u52A0\u8F7D\u624B\u6BB5\u4EE5\u5916\uFF0C\u6211\u8C03\u7814\u5E76\u5B9E\u65BD\u4E86 WebP \u56FE\u7247\u683C\u5F0F\u7684\u66FF\u6362\u3002\u7531\u4E8E\u53EF\u80FD\u4F1A\u6709\u6F5C\u5728\u517C\u5BB9\u6027\u7684\u95EE\u9898\uFF0C\u56E0\u800C\u5177\u4F53\u505A\u6CD5\u662F\u5148\u8FDB\u884C\u517C\u5BB9\u6027\u55C5\u63A2\u3002\u8FD9\u4E00\u624B\u6BB5\u501F\u9274\u4E86\u793E\u533A\u4E00\u8D2F\u505A\u6CD5\uFF0C\u5229\u7528 <code>img</code> \u6807\u7B7E\u52A0\u8F7D\u4E00\u5F20 base64 \u7684 WebP \u56FE\u7247\uFF0C\u5E76\u5C06\u7ED3\u679C\u5B58\u5165 <code>localStorage</code> \u4E2D\u9632\u6B62\u91CD\u590D\u5224\u65AD\u3002\u5982\u679C\u8BE5\u7EC8\u7AEF\u652F\u6301\uFF0C\u5219\u518D\u5BF9\u56FE\u7247\u683C\u5F0F\u8FDB\u884C\u66FF\u6362\u3002\u8FD9\u4E2A\u517C\u5BB9\u6027\u55C5\u63A2\u8FC7\u7A0B\uFF0C\u4E5F\u5C01\u88C5\u6210 promise \u5316\u7684\u901A\u7528\u63A5\u53E3\u3002</p></blockquote><p>\u76F8\u5173\u4EE3\u7801\u7247\u6BB5\u5982\u4E0B\uFF1A</p><div class="language-text line-numbers-mode"><button class="copy"></button><span class="lang">text</span><pre><code><span class="line"><span style="color:#A6ACCD;">const supportWebp = () =&gt; new Promise(resolve =&gt; {</span></span>
<span class="line"><span style="color:#A6ACCD;">    const image = new Image()</span></span>
<span class="line"><span style="color:#A6ACCD;">    image.onerror = () =&gt; resolve(false)</span></span>
<span class="line"><span style="color:#A6ACCD;">    image.onload = () =&gt; resolve(image.width === 1)</span></span>
<span class="line"><span style="color:#A6ACCD;">    image.src = &#39;data:image/webp;base64,UklGRiQAAABXRUJQVlA4IBgAAAAwAQCdASoBAAEAAwA0JaQAA3AA/vuUAAA=&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">}).catch(() =&gt; false)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>\u8FD9\u65F6\u5019\uFF0C\u9762\u8BD5\u5B98\u5F80\u5F80\u4F1A\u8FDB\u4E00\u6B65\u5173\u5FC3\u9879\u76EE\u6536\u76CA\u60C5\u51B5\u3002\u8FD9\u5C31\u9700\u8981\u9762\u8BD5\u8005\u6839\u636E\u5B9E\u60C5\u4F5C\u7B54\uFF0C\u4ECD\u7136\u4EE5\u8FD9\u4E2A\u9879\u76EE\u4E3A\u4F8B\uFF1A</p><blockquote><p>\u5728\u5177\u4F53\u4E0A\u7EBF\u65F6\uFF0C\u6211\u5BF9 10% \u7684\u6D41\u91CF\u8FDB\u884C\u4E86\u5206\u7EC4\u5207\u5206\u30025% \u4E3A\u5BF9\u7167\u7EC4\uFF0C\u4ECD\u7136\u91C7\u7528\u4F20\u7EDF\u683C\u5F0F\uFF1B\u53E6\u5916 5% \u4E3A\u5B9E\u9A8C\u7EC4\uFF0C\u8FDB\u884C WebP \u683C\u5F0F\u8BD5\u9A8C\u3002 <strong>\u6700\u7EC8\u7ED3\u679C\u663E\u793A\u6536\u76CA\u975E\u5E38\u6709\u9650</strong> \u3002 \u4E3A\u6B64\u6211\u8FDB\u884C\u5206\u6790\uFF1A\u8BA4\u4E3A\u51FA\u73B0\u8FD1\u4F3C\u96F6\u6536\u76CA\u7684\u539F\u56E0\u662F\u56FE\u7247\u670D\u52A1\u7684\u7F13\u5B58\u95EE\u9898\u3002\u65B0\u8F6C\u6362\u7684\u4E00\u6279 WebP \u683C\u5F0F\u56FE\u7247\u7531\u4E8E\u6CA1\u6709\u7F13\u5B58\uFF0C\u56E0\u800C\u5728\u6027\u80FD\u4E0A\u6253\u4E86\u6298\u6263\u3002\u4E3A\u4E86\u9A8C\u8BC1\u731C\u60F3\uFF0C\u6211\u51B3\u5B9A\u7EE7\u7EED\u8FDB\u884C\u6269\u91CF\u8BD5\u9A8C\u5E76\u89C2\u5BDF\u7ED3\u679C\u3002\u679C\u7136\uFF0C\u540E\u7EED\u6392\u9664\u7F13\u5B58\u95EE\u9898\u4E4B\u540E\uFF0C\u6536\u76CA\u63D0\u5347 25%~30% \u5DE6\u53F3\u3002</p></blockquote><p>\u8FD9\u91CC\uFF0C\u6211\u4EEC\u5C31\u6D89\u53CA\u4E86\u5DE5\u7A0B\u4F18\u5316\u4E2D\u7684\u4E00\u4E2A\u91CD\u8981\u73AF\u8282\uFF1A\u7F51\u7EDC\u8BF7\u6C42\u548C\u7F13\u5B58\u3002\u8FD9\u4E9B\u5185\u5BB9\u6211\u4EEC\u4F1A\u5728\u540E\u7EED\u8BFE\u7A0B\u7F51\u7EDC\u73AF\u8282\u300A\u7B2C 8-1\u8BFE\uFF1A\u7F13\u5B58\u8C01\u90FD\u61C2\uFF0C\u4E00\u95EE\u90FD\u54D1\u53E3\u300B\u4E2D\u5177\u4F53\u5C55\u5F00\u3002</p><p>\u901A\u8FC7\u4EE5\u4E0A\u56DE\u7B54\uFF0C\u6211\u5982\u5B9E\u8BB2\u8FF0\u4E86\u51FA\u73B0\u7684\u975E\u9884\u671F case\uFF0C\u5E76\u8BF4\u660E\u9047\u89C1\u95EE\u9898\u65F6\uFF0C\u5982\u4F55\u8FDB\u884C\u5206\u6790\u8FDB\u800C\u89E3\u51B3\u95EE\u9898\u7684\u4E00\u7CFB\u5217\u8FC7\u7A0B\u3002\u8FD9\u6837\u7684\u56DE\u7B54\u80FD\u660E\u786E\u8868\u73B0\u51FA\u6211\u786E\u5B9E\u505A\u8FC7\u8BE5\u9879\u76EE\u5E76\u8FDB\u884C\u4E86\u601D\u8003\u5206\u6790\uFF0C\u6700\u7EC8\u843D\u5730\u3002\u8FD9\u7C7B\u601D\u8DEF\u4E5F\u66F4\u5BB9\u6613\u88AB\u9762\u8BD5\u5B98\u6240\u63A5\u53D7\u3002</p><p>\u66F4\u591A\u5173\u4E8E WebP \u7684\u597D\u6587\u7AE0\uFF1A</p><ul><li><a href="https://www.keycdn.com/support/webp-support" target="_blank" rel="noreferrer">WebP Support - It&#39;s More Than You Think</a></li><li><a href="http://www.uisdc.com/image-format-webp-introduction" target="_blank" rel="noreferrer">\u4ECE\u96F6\u5F00\u59CB\u5E26\u4F60\u8BA4\u8BC6\u6700\u65B0\u7684\u56FE\u7247\u683C\u5F0F WebP</a></li><li><a href="https://segmentfault.com/a/1190000007482148" target="_blank" rel="noreferrer">\u628A\u7F51\u7AD9\u7684\u56FE\u7247\u5347\u7EA7\u5230 WebP \u683C\u5F0F\u5427</a></li></ul><p>\u4E5F\u7531\u6B64\u770B\u51FA\uFF0C\u6027\u80FD\u4F18\u5316\u5176\u5B9E\u5E76\u4E0D\u96BE\u505A\uFF0C\u91CD\u8981\u7684\u662F\u89E3\u51B3\u95EE\u9898\u7684\u601D\u8DEF\uFF0C\u4EE5\u53CA\u89E3\u51B3\u8FC7\u7A0B\u4E2D\u5BF9\u4E8E\u9879\u76EE\u7684\u628A\u63A7\u548C\u63A8\u8350\u3002\u8FD9\u4E9B\u5185\u5BB9\u6211\u4EEC\u79F0\u4E4B\u4E3A\u201C\u8F6F\u7D20\u8D28\u201D\u3002</p><h3 id="\u6309\u9700\u52A0\u8F7D\u4F18\u5316" tabindex="-1">\u6309\u9700\u52A0\u8F7D\u4F18\u5316 <a class="header-anchor" href="#\u6309\u9700\u52A0\u8F7D\u4F18\u5316" aria-hidden="true">#</a></h3><p>\u5982\u679C\u9762\u8BD5\u5B98\u56F4\u7ED5\u7740\u521A\u624D\u5217\u4E3E\u7684\u201C\u8D44\u6E90\u6253\u5305\u548C\u9006\u5411\u62C6\u5206\uFF08\u6309\u9700\u52A0\u8F7D\uFF09\u201D\u65B9\u5411\u63D0\u95EE\uFF0C\u6211\u4ECD\u7136\u4F1A\u91C7\u7528\u540C\u6837\u7684\u201C\u601D\u8DEF\u201D\u8FDB\u884C\u56DE\u7B54\uFF1A</p><p>&gt; \u6211\u63A5\u624B\u9879\u76EE\u4E4B\u540E\uFF0C\u53D1\u73B0\u5386\u53F2\u539F\u6709\u7684\u8D44\u6E90\u6253\u5305\u914D\u7F6E\u5E76\u4E0D\u5408\u7406\uFF0C\u4E25\u91CD\u5F71\u54CD\u4E86\u6027\u80FD\u8868\u73B0\u3002\u56E0\u6B64\u501F\u52A9\u6784\u5EFA\u5DE5\u5177\uFF0C\u5BF9\u8D44\u6E90\u8FDB\u884C\u5408\u5E76\u6253\u5305\u3002\u4F46\u662F\uFF0C\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u6211\u7684\u7B56\u7565\u5E76\u4E0D\u662F\u5927\u5200\u9614\u65A7\u5730\u8FDB\u884C\u8D44\u6E90\u5408\u5E76\uFF0C\u56E0\u4E3A\u8FD9\u6837\u4F1A\u8BA9 bundle.js \u7684 size \u8D8A\u6765\u8D8A\u5927\uFF0C\u6240\u4EE5\u4E5F\u8FDB\u884C\u4E86\u9006\u5411\u8FC7\u7A0B\u3002</p><p>&gt;</p><blockquote><p>\u4EE5\u5B9E\u9645\u9875\u9762\u4E3A\u4F8B\uFF1A</p></blockquote><p>&gt;</p><blockquote><p><img src="https://images.gitbook.cn/6f5fc910-bff2-11e9-8e2c-3b4fd17ad6da" alt="enter image description here"></p></blockquote><p>&gt;</p><p>&gt; \u5982\u4E0A\uFF0C\u5F53\u70B9\u51FB\u5DE6\u56FE\u64AD\u653E\u6309\u94AE\u4E4B\u540E\uFF0C\u9875\u9762\u51FA\u73B0\u89C6\u9891\u5217\u8868\u6D6E\u5C42\uFF08\u53F3\u4FA7\u6240\u793A\uFF0C\u4ECD\u4E3A\u540C\u4E00\u9875\u9762\uFF0C\u7C7B\u4F3C\u5355\u9875\u5E94\u7528\uFF09\u3002\u89C6\u9891\u5217\u8868\u6D6E\u5C42\u5305\u542B\u4E86\u6EDA\u52A8\u5904\u7406\u3001\u89C6\u9891\u64AD\u653E\u7B49\u591A\u9879\u590D\u6742\u903B\u8F91\uFF0C\u56E0\u6B64\u5173\u4E8E\u8FD9\u4E2A\u6D6E\u5C42\u7684\u811A\u672C\u6211\u5E76\u6CA1\u6709\u8FDB\u884C\u6253\u5305\u5408\u5E76\uFF0C\u800C\u662F\u5355\u72EC\u8FDB\u884C\u62C6\u5206\u3002\u5F53\u7528\u6237\u70B9\u51FB\u6D6E\u5C42\u89E6\u53D1\u6309\u94AE\u540E\uFF0C\u518D\u6267\u884C\u5BF9\u8FD9\u4E00\u90E8\u5206\u811A\u672C\u7684\u8BF7\u6C42\u3002</p><p>\u5DE5\u7A0B\u5316\u6027\u80FD\u4F18\u5316\u4E0D\u4EC5\u9700\u8981\u505A\uFF0C\u8FD8\u8981\u7528\u6570\u636E\u8BC1\u660E\u505A\u6CD5\u7684\u5408\u7406\u6027\uFF1A</p><blockquote><p>\u540C\u65F6\uFF0C\u6211\u5BF9\u7528\u6237\u70B9\u51FB\u89E6\u53D1\u6309\u94AE\u7684\u6982\u7387\u8FDB\u884C\u7EDF\u8BA1\uFF0C\u53D1\u73B0\u8FDB\u5165\u9875\u9762\u7684\u7528\u6237\u53EA\u6709 10% \u5DE6\u53F3\u4F1A\u70B9\u51FB\u6309\u94AE\uFF0C\u4ECE\u800C\u89E6\u53D1\u89C6\u9891\u5217\u8868\u6D6E\u5C42\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5927\u90E8\u5206\u7528\u6237\uFF0890%\uFF09\u5E76\u4E0D\u4F1A\u770B\u5230\u8FD9\u4E00\u6D6E\u5C42\uFF0C\u5EF6\u8FDF\u6309\u9700\u52A0\u8F7D\u662F\u6709\u7EDF\u8BA1\u6570\u636E\u652F\u6301\u7684\u3002</p></blockquote><p>\u901A\u8FC7\u8FD9\u4E2A\u6848\u4F8B\uFF0C\u6211\u4EEC\u53D1\u73B0\u6027\u80FD\u4F18\u5316\u5176\u5B9E\u662F\u4E00\u4E2A\u5F00\u653E\u5F0F\u95EE\u9898\uFF0C\u975E\u5E38\u4F9D\u8D56\u5B9E\u8DF5\u3002\u8BFB\u8005\u53EF\u6839\u636E\u4E0A\u9762\u7684\u4F8B\u5B50\uFF0C\u7ED3\u5408\u81EA\u5DF1\u7684\u9879\u76EE\u8FDB\u884C\u56DE\u7B54\u3002</p><p>\u867D\u7136\u6CA1\u6709\u6D89\u53CA\u4EE3\u7801\u5B9E\u65BD\uFF0C\u4F46\u662F\u5EFA\u7ACB\u8D77\u9879\u76EE\u610F\u8BC6\u548C\u65B9\u5411\u610F\u8BC6\uFF0C\u8FD9\u5728\u5DE5\u7A0B\u5316\u6027\u80FD\u4E0A\u975E\u5E38\u96BE\u80FD\u53EF\u8D35\u3002\u4E0A\u9762\u63D0\u5230\u7684\u501F\u52A9\u6784\u5EFA\u5DE5\u5177\u8FDB\u884C\u201C\u6309\u9700\u52A0\u8F7D\u201D\u7B49\u5185\u5BB9\uFF0C\u524D\u9762\u7AE0\u8282\u5982\u300Awebpack \u5DE5\u7A0B\u5E08 &gt; \u524D\u7AEF\u5DE5\u7A0B\u5E08\u300B\u90FD\u4F1A\u5177\u4F53\u4ECE\u4EE3\u7801\u89D2\u5EA6\u7ED9\u51FA\u793A\u4F8B\u3002</p><p>\u5F53\u7136\u4E0A\u8FF0\u4E3E\u4F8B\u7684\u6309\u9700\u52A0\u8F7D\uFF0C\u6211\u4EEC\u5E76\u4E0D\u662F\u4F7F\u7528\u6210\u719F\u7684 webpack \u5DE5\u5177\u94FE\uFF0C\u800C\u662F\u91C7\u7528\u516C\u53F8\u5185\u90E8\u5C01\u88C5\u7684\u5DE5\u7A0B\u5316\u5DE5\u5177\u3002\u5728\u51E0\u5E74\u524D\uFF0C\u8FD9\u6837\u7684\u65B9\u6848\u5E76\u4E0D\u6210\u719F\uFF0C\u56E0\u6B64\u6211\u5199\u4E86\u4E00\u4E9B\u6309\u9700\u52A0\u8F7D\u7684\u5DEE\u8DDD\uFF0C\u914D\u5408\u81EA\u5DF1\u7684\u5DE5\u7A0B\u5316\u5DE5\u5177\u4F7F\u7528\u3002\u8FD9\u4E00\u65B9\u9762\u5185\u5BB9\uFF0C\u5F88\u591A\u9762\u8BD5\u5B98\u4F1A\u5F88\u611F\u5174\u8DA3\uFF0C\u8BDD\u9898\u4E5F\u53EF\u4EE5\u5EF6\u4F38\u5230 FIS \u548C webpack \u7684\u6BD4\u8F83\uFF0C\u5DE5\u7A0B\u5316\u5DE5\u5177\u7684\u8BBE\u8BA1\u7B49\u8BDD\u9898\u3002</p><h3 id="\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316" tabindex="-1">\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316 <a class="header-anchor" href="#\u8BB2\u4E0D\u5B8C\u7684\u5DE5\u7A0B\u5316\u4F18\u5316" aria-hidden="true">#</a></h3><p>\u6B64\u5916\uFF0C\u5DE5\u7A0B\u4F18\u5316\u65B9\u5411\u8FD8\u5305\u62EC\uFF1A</p><ul><li>\u56FE\u7247\u61D2\u52A0\u8F7D</li><li>\u96EA\u78A7\u56FE</li><li>\u5408\u7406\u8BBE\u7F6E\u7F13\u5B58\u7B56\u7565</li><li>\u4F7F\u7528 prefetch / preload \u9884\u52A0\u8F7D\u7B49\u65B0\u7279\u6027</li><li>\u4EE5 tree shaking \u624B\u6BB5\u4E3A\u4E3B\u7684\u4EE3\u7801\u7626\u8EAB</li></ul><p>\u8FD9\u91CC\u4E0D\u518D\u4E00\u4E00\u4E3E\u4F8B\uFF0C\u6B22\u8FCE\u8BA2\u9605\u6B64\u8BFE\u7A0B\u7684\u540C\u5B66\u7ED3\u5408\u81EA\u5DF1\u7684\u7ECF\u5386\u5728\u8BC4\u8BBA\u533A\u7559\u8A00\u8BA8\u8BBA\u6216\u8005\u76F4\u63A5\u5411\u6211\u63D0\u95EE\uFF0C\u6211\u4F1A\u5C3D\u91CF\u62FF\u51FA\u65F6\u95F4\u8DDF\u5927\u5BB6\u4E00\u8D77\u8FDB\u884C\u9879\u76EE\u5206\u6790\u3001\u63CF\u8FF0\u3002</p><p>\u4EE5\u4E0A\u662F <strong>\u5DE5\u7A0B\u5316</strong> \u6027\u80FD\u7684\u5B9E\u9645\u9898\u76EE\uFF0C\u603B\u7ED3\u4E00\u4E0B\uFF1A\u5DE5\u7A0B\u5E08\u9700\u8981\u5BF9\u65E5\u5E38\u9879\u76EE\u8FDB\u884C\u6DF1\u5165\u603B\u7ED3\uFF0C\u7ED3\u5408\u4EA7\u54C1\u89D2\u5EA6\u3001\u7814\u53D1\u89D2\u5EA6\u63CF\u8FF0\u3002\u5728\u9762\u8BD5\u524D\u5C31\u9700\u8981\u505A\u5230\u201C\u5FC3\u4E2D\u6709\u6570\uFF0C\u80F8\u6709\u6210\u7AF9\u201D\u3002</p><p>\u53E6\u5916\uFF0C\u5728\u5177\u4F53\u7684\u5B9E\u73B0\u65B9\u5411\u4E0A\uFF0C\u5173\u4E8E\u6027\u80FD\u4F18\u5316\u7684\u5207\u5165\u70B9\u4E5F\u6709\u5F88\u591A\u3002\u6BD4\u5982\uFF1A</p><ul><li>\u52A8\u753B\u6027\u80FD\u65B9\u5411</li><li>\u64CD\u4F5C DOM \u65B9\u5411</li><li>\u6D4F\u89C8\u5668\u52A0\u8F7D\u3001\u6E32\u67D3\u6027\u80FD\u65B9\u5411</li><li>\u6027\u80FD\u6D4B\u91CF\u3001\u76D1\u63A7\u65B9\u5411</li></ul><p>\u8FD9\u4E9B\u65B9\u5411\u5E76\u4E0D\u662F\u76F8\u4E92\u72EC\u7ACB\u7684\uFF0C\u5B83\u4EEC\u5F7C\u6B64\u4F9D\u5B58\uFF0C\u6BD4\u5982 <strong>\u52A8\u753B\u6027\u80FD\u65B9\u5411\u4E0E\u6D4F\u89C8\u5668\u6E32\u67D3\u6027\u80FD\u606F\u606F\u76F8\u5173</strong> \u3002\u8FD9\u91CC\u6211\u7528\u4E00\u9053\u7ECF\u5178\u7684\u9762\u8BD5\u9898\u6765\u5206\u6790\uFF1A <strong>\u201C\u5982\u679C\u53D1\u73B0\u9875\u9762\u52A8\u753B\u6548\u679C\u5361\u987F\uFF0C\u4F60\u4F1A\u4ECE\u54EA\u4E9B\u89D2\u5EA6\u89E3\u51B3\u95EE\u9898\uFF1F\u201D</strong></p><p>\u9996\u5148\u4ECE\u52A8\u753B\u5B9E\u73B0\u5165\u624B\uFF1A</p><blockquote><ul><li>\u4E00\u822C CSS3 \u52A8\u753B\u4F1A\u6BD4\u57FA\u4E8E JavaScript \u5B9E\u73B0\u7684\u52A8\u753B\u6548\u7387\u8981\u9AD8\uFF0C\u56E0\u6B64\u4F18\u5148\u4F7F\u7528 CSS3 \u5B9E\u73B0\u6548\u679C\uFF08\u8FD9\u4E00\u70B9\u5E76\u4E0D\u7EDD\u5BF9\uFF09 <ul><li>\u5728\u4F7F\u7528 CSS3 \u5B9E\u73B0\u52A8\u753B\u65F6\uFF0C\u8003\u8651\u5F00\u542F GPU \u52A0\u901F\uFF08\u8FD9\u4E00\u70B9\u4E5F\u5E76\u4E0D\u603B\u662F\u6B63\u5411\u6548\u679C\uFF09</li><li>\u4F18\u5148\u4F7F\u7528\u6D88\u8017\u6700\u4F4E\u7684 transform \u548C opacity \u4E24\u4E2A\u5C5E\u6027</li><li>\u4F7F\u7528 will-change \u5C5E\u6027</li><li>\u72EC\u7ACB\u5408\u6210\u5C42\uFF0C\u51CF\u5C11\u7ED8\u5236\u533A\u57DF</li><li>\u5BF9\u4E8E\u53EA\u80FD\u4F7F\u7528 JavaScript \u5B9E\u73B0\u52A8\u753B\u6548\u679C\u7684\u60C5\u51B5\uFF0C\u8003\u8651 requestAnimationFrame\u3001requestIdleCallback API</li><li>\u6279\u91CF\u8FDB\u884C\u6837\u5F0F\u53D8\u6362\uFF0C\u51CF\u5C11\u5E03\u5C40\u6296\u52A8</li></ul></li></ul></blockquote><p>&gt;</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u4E0A\u9762\u6BCF\u4E00\u70B9\u7684\u80CC\u540E\u90FD\u5305\u542B\u7740\u5F88\u591A\u77E5\u8BC6\u70B9\uFF0C\u4F8B\u5982\uFF1A</p><ul><li>\u5982\u4F55\u7406\u89E3 requestAnimationFrame \u548C 60 fps</li><li>\u5982\u4F55\u5B9E\u73B0 requestAnimationFrame polyfill</li><li>\u54EA\u4E9B\u64CD\u4F5C\u4F1A\u89E6\u53D1\u6D4F\u89C8\u5668 reflow\uFF08\u91CD\u6392\uFF09\u6216\u8005 repaint\uFF08\u91CD\u7ED8\uFF09</li><li>\u5BF9\u4E8E\u7ED9\u51FA\u7684\u4EE3\u7801\uFF0C\u5982\u4F55\u8FDB\u884C\u4F18\u5316</li><li>\u5982\u4F55\u5B9E\u73B0\u6EDA\u52A8\u65F6\u7684\u8282\u6D41\u3001\u9632\u6296\u51FD\u6570</li></ul><p>\u8FD9\u4E9B\u95EE\u9898\uFF0C\u6211\u4EEC\u4F1A\u62FF\u51FA\u5176\u4E2D\u51E0\u4E2A\u5728\u4E0B\u4E00\u8282\u5206\u6790\u3002\u540C\u65F6\uFF0C\u8BA2\u9605\u8BFE\u7A0B\u7684\u670B\u53CB\u4EEC\uFF0C\u90FD\u53EF\u4EE5\u5728\u4E0B\u8282\u8BFE\u672B\u5C3E\u5F69\u86CB\u533A\u83B7\u5F97\u6211\u5728\u8FD9\u4E9B\u4E3B\u9898\u65B9\u5411\u4E0A\u6536\u85CF\u7684\u6587\u7AE0\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u5DE5\u7A0B\u5316\u4F18\u5316\u662F\u4E00\u4E2A\u592A\u5927\u7684\u8BDD\u9898\u4E86\uFF0C\u672C\u8282\u8BFE\u6211\u4EEC\u53EA\u662F\u5728\u201C\u5927\u9762\u4E0A\u201D\u8FDB\u884C\u65B9\u5411\u6307\u5F15\uFF0C\u4EE5\u9762\u8BD5\u4E3A\u89D2\u5EA6\u629B\u7816\u5F15\u7389\uFF0C\u66F4\u591A\u5177\u4F53\u4EE3\u7801\u7EC6\u8282\u5B9E\u4F8B\uFF0C\u4E0B\u8282\u8BFE\u6211\u4EEC\u4F1A\u7EE7\u7EED\u63A2\u8BA8\u3002</p><p>\u53E6\u5916\uFF0C\u6B63\u56E0\u4E3A\u8FD9\u4E2A\u8BDD\u9898\u7684\u7279\u6B8A\u6027\uFF0C\u4E00\u5343\u4E2A\u9879\u76EE\uFF0C\u5C31\u6709\u4E00\u5343\u4E2A\u4F18\u5316\u573A\u666F\uFF0C\u4E5F\u6B22\u8FCE\u5927\u5BB6\u62FF\u51FA\u6765\u9879\u76EE\u4E2D\u7684\u5177\u4F53\u60C5\u51B5\uFF0C\u8DDF\u6211\u8BA8\u8BBA\u3002</p><h2 id="\u5206\u4EAB\u4EA4\u6D41" tabindex="-1">\u5206\u4EAB\u4EA4\u6D41 <a class="header-anchor" href="#\u5206\u4EAB\u4EA4\u6D41" aria-hidden="true">#</a></h2><p>\u8BF7\u5927\u5BB6\u7559\u8A00\u5206\u4EAB\u300C\u6027\u80FD\u4F18\u5316\u300D\u65B9\u9762\u7684\u7ECF\u9A8C\u5FC3\u5F97\uFF0C\u4E5F\u53EF\u4EE5\u7EE7\u7EED\u5B66\u4E60\u4E0B\u4E00\u8282\u7B49\u5168\u90E8\u5B8C\u7ED3\u8FD9\u4E2A\u4E3B\u9898\u4E4B\u540E\u518D\u5206\u4EAB\u3002\u9605\u8BFB\u6587\u7AE0\u8FC7\u7A0B\u4E2D\u6709\u4EFB\u4F55\u7591\u95EE\u968F\u65F6\u53EF\u4EE5\u8DDF\u5176\u4ED6\u5C0F\u4F19\u4F34\u8BA8\u8BBA\uFF0C\u6216\u8005\u76F4\u63A5\u5411\u4F5C\u8005 LucasHC \u63D0\u95EE\uFF08\u4F5C\u8005\u770B\u5230\u540E\u4F1A\u62BD\u7A7A\u89E3\u7B54\uFF09\u3002 <strong>\u4F60\u7684\u5206\u4EAB\u4E0D\u4EC5\u5E2E\u52A9\u4ED6\u4EBA\uFF0C\u66F4\u4F1A\u63D0\u5347\u81EA\u5DF1\u3002</strong></p><p>\u540C\u65F6\uFF0C\u8BF4\u8BF4\u81EA\u5DF1\u6700\u60F3\u4E86\u89E3\u7684\u4E3B\u9898\uFF0C\u8BFE\u7A0B\u5185\u5BB9\u4F1A\u6839\u636E\u5927\u5BB6\u7684\u610F\u89C1\u548C\u5EFA\u8BAE\u8FED\u4EE3\u548C\u5B8C\u5584\u3002</p><p>\u6B64\u5916\uFF0C\u6211\u4EEC\u4E3A\u672C\u8BFE\u7A0B\u4ED8\u8D39\u8BFB\u8005\u521B\u5EFA\u4E86\u300A\u524D\u7AEF\u5F00\u53D1\u6838\u5FC3\u77E5\u8BC6\u8FDB\u9636\u300B\u5FAE\u4FE1\u4EA4\u6D41\u7FA4\uFF0C\u4EE5\u65B9\u4FBF\u66F4\u6709\u9488\u5BF9\u6027\u5730\u8BA8\u8BBA\u8BFE\u7A0B\u76F8\u5173\u95EE\u9898\u3002\uFF08\u5165\u7FA4\u8BF7\u5230\u7B2C1-2\u8BFE\u672B\u5C3E\u626B\u63CF\u4E8C\u7EF4\u7801\uFF0C\u82E5\u5931\u6548\u8BF7\u52A0 GitChat \u5C0F\u52A9\u624B\u4F3D\u5229\u7565\u7684\u5FAE\u4FE1\uFF0CID \u4E3A GitChatty6\uFF0C\u5E76\u6CE8\u660E\u300C\u524D\u7AEF\u6838\u5FC3\u300D\u3002\uFF09</p>`,66),i=[s];function t(r,o,c,u,d,g){return a(),l("div",null,i)}const m=e(p,[["render",t]]);export{b as __pageData,m as default};
