import{_ as e,o as r,c as t,a}from"./app.b476fd3b.js";const T="/vitepress-demo/assets/7808b195c921e0685958c20509855d4a.180ccfa3.png",H=JSON.parse('{"title":"06 | HTTP \u6709\u54EA\u4E9B\u7279\u70B9\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7075\u6D3B\u53EF\u6269\u5C55","slug":"\u7075\u6D3B\u53EF\u6269\u5C55","link":"#\u7075\u6D3B\u53EF\u6269\u5C55","children":[]},{"level":2,"title":"\u53EF\u9760\u4F20\u8F93","slug":"\u53EF\u9760\u4F20\u8F93","link":"#\u53EF\u9760\u4F20\u8F93","children":[]},{"level":2,"title":"\u5E94\u7528\u5C42\u534F\u8BAE","slug":"\u5E94\u7528\u5C42\u534F\u8BAE","link":"#\u5E94\u7528\u5C42\u534F\u8BAE","children":[]},{"level":2,"title":"\u8BF7\u6C42 - \u5E94\u7B54","slug":"\u8BF7\u6C42-\u5E94\u7B54","link":"#\u8BF7\u6C42-\u5E94\u7B54","children":[]},{"level":2,"title":"\u65E0\u72B6\u6001","slug":"\u65E0\u72B6\u6001","link":"#\u65E0\u72B6\u6001","children":[]},{"level":2,"title":"\u5176\u4ED6\u7279\u70B9","slug":"\u5176\u4ED6\u7279\u70B9","link":"#\u5176\u4ED6\u7279\u70B9","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]},{"level":2,"title":"\u8BFE\u4E0B\u4F5C\u4E1A","slug":"\u8BFE\u4E0B\u4F5C\u4E1A","link":"#\u8BFE\u4E0B\u4F5C\u4E1A","children":[]},{"level":2,"title":"\u8BFE\u5916\u5C0F\u8D34\u58EB","slug":"\u8BFE\u5916\u5C0F\u8D34\u58EB","link":"#\u8BFE\u5916\u5C0F\u8D34\u58EB","children":[]}],"relativePath":"http-protocol/03/06.md","lastUpdated":1676558697000}'),n={name:"http-protocol/03/06.md"},i=a('<h1 id="_06-http-\u6709\u54EA\u4E9B\u7279\u70B9\uFF1F" tabindex="-1">06 | HTTP \u6709\u54EA\u4E9B\u7279\u70B9\uFF1F <a class="header-anchor" href="#_06-http-\u6709\u54EA\u4E9B\u7279\u70B9\uFF1F" aria-hidden="true">#</a></h1><p>\u901A\u8FC7\u57FA\u7840\u7BC7\u524D\u51E0\u8BB2\u7684\u5B66\u4E60\uFF0C\u4F60\u5E94\u8BE5\u5DF2\u7ECF\u77E5\u9053\u4E86 HTTP \u534F\u8BAE\u7684\u57FA\u672C\u77E5\u8BC6\uFF0C\u4E86\u89E3\u5B83\u7684\u62A5\u6587\u7ED3\u6784\uFF0C\u8BF7\u6C42\u5934\u3001\u54CD\u5E94\u5934\u4EE5\u53CA\u5185\u90E8\u7684\u8BF7\u6C42\u65B9\u6CD5\u3001URI \u548C\u72B6\u6001\u7801\u7B49\u7EC6\u8282\u3002</p><p>\u4F60\u4F1A\u4E0D\u4F1A\u6709\u79CD\u7591\u60D1\uFF1AHTTP \u534F\u8BAE\u597D\u50CF\u4E5F\u633A\u7B80\u5355\u7684\u554A\uFF0C\u51ED\u4EC0\u4E48\u5B83\u5C31\u80FD\u7EDF\u6CBB\u4E92\u8054\u7F51\u8FD9\u4E48\u591A\u5E74\u5462\uFF1F</p><p>\u6240\u4EE5\u63A5\u4E0B\u6765\u7684\u8FD9\u4E24\u8BB2\uFF0C\u6211\u4F1A\u8DDF\u4F60\u804A\u804A HTTP \u534F\u8BAE\u7684\u7279\u70B9\u3001\u4F18\u70B9\u548C\u7F3A\u70B9\u3002\u65E2\u8981\u770B\u5230\u5B83\u597D\u7684\u4E00\u9762\uFF0C\u4E5F\u8981\u6B63\u89C6\u5B83\u4E0D\u597D\u7684\u4E00\u9762\uFF0C\u53EA\u6709\u5168\u65B9\u4F4D\u3001\u591A\u89D2\u5EA6\u4E86\u89E3 HTTP\uFF0C\u624D\u80FD\u5B9E\u73B0\u201C\u626C\u957F\u907F\u77ED\u201D\uFF0C\u66F4\u597D\u5730\u5229\u7528 HTTP\u3002</p><p>\u4ECA\u5929\u8FD9\u8282\u8BFE\u4E3B\u8981\u8BF4\u7684\u662F HTTP \u534F\u8BAE\u7684\u7279\u70B9\uFF0C\u4F46\u4E0D\u4F1A\u8BB2\u5B83\u4EEC\u7684\u597D\u574F\uFF0C\u8FD9\u4E9B\u7279\u70B9\u5373\u6709\u53EF\u80FD\u662F\u4F18\u70B9\uFF0C\u4E5F\u6709\u53EF\u80FD\u662F\u7F3A\u70B9\uFF0C\u4F60\u53EF\u4EE5\u8FB9\u542C\u8FB9\u601D\u8003\u3002</p><p><img src="'+T+'" alt="img"></p><h2 id="\u7075\u6D3B\u53EF\u6269\u5C55" tabindex="-1">\u7075\u6D3B\u53EF\u6269\u5C55 <a class="header-anchor" href="#\u7075\u6D3B\u53EF\u6269\u5C55" aria-hidden="true">#</a></h2><p>\u9996\u5148\uFF0C HTTP \u534F\u8BAE\u662F\u4E00\u4E2A <strong>\u7075\u6D3B\u53EF\u6269\u5C55</strong> \u7684\u4F20\u8F93\u534F\u8BAE\u3002</p><p>HTTP \u534F\u8BAE\u6700\u521D\u8BDE\u751F\u7684\u65F6\u5019\u5C31\u6BD4\u8F83\u7B80\u5355\uFF0C\u672C\u7740\u5F00\u653E\u7684\u7CBE\u795E\u53EA\u89C4\u5B9A\u4E86\u62A5\u6587\u7684\u57FA\u672C\u683C\u5F0F\uFF0C\u6BD4\u5982\u7528\u7A7A\u683C\u5206\u9694\u5355\u8BCD\uFF0C\u7528\u6362\u884C\u5206\u9694\u5B57\u6BB5\uFF0C<code>header+body</code> \u7B49\uFF0C\u62A5\u6587\u91CC\u7684\u5404\u4E2A\u7EC4\u6210\u90E8\u5206\u90FD\u6CA1\u6709\u505A\u4E25\u683C\u7684\u8BED\u6CD5\u8BED\u4E49\u9650\u5236\uFF0C\u53EF\u4EE5\u7531\u5F00\u53D1\u8005\u4EFB\u610F\u5B9A\u5236\u3002</p><p>\u6240\u4EE5\uFF0CHTTP \u534F\u8BAE\u5C31\u968F\u7740\u4E92\u8054\u7F51\u7684\u53D1\u5C55\u4E00\u540C\u6210\u957F\u8D77\u6765\u4E86\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0CHTTP \u534F\u8BAE\u9010\u6E10\u589E\u52A0\u4E86\u8BF7\u6C42\u65B9\u6CD5\u3001\u7248\u672C\u53F7\u3001\u72B6\u6001\u7801\u3001\u5934\u5B57\u6BB5\u7B49\u7279\u6027\u3002\u800C body \u4E5F\u4E0D\u518D\u9650\u4E8E\u6587\u672C\u5F62\u5F0F\u7684 TXT \u6216 HTML\uFF0C\u800C\u662F\u80FD\u591F\u4F20\u8F93\u56FE\u7247\u3001\u97F3\u9891\u89C6\u9891\u7B49\u4EFB\u610F\u6570\u636E\uFF0C\u8FD9\u4E9B\u90FD\u662F\u6E90\u4E8E\u5B83\u7684\u7075\u6D3B\u53EF\u6269\u5C55\u7684\u7279\u70B9\u3002</p><p>\u800C\u90A3\u4E9B RFC \u6587\u6863\uFF0C\u5B9E\u9645\u4E0A\u4E5F\u53EF\u4EE5\u7406\u89E3\u4E3A\u662F\u5BF9\u5DF2\u6709\u6269\u5C55\u7684 <strong>\u627F\u8BA4\u548C\u6807\u51C6\u5316</strong>\uFF0C\u5B9E\u73B0\u4E86 \u300C\u4ECE\u5B9E\u8DF5\u4E2D\u6765\uFF0C\u5230\u5B9E\u8DF5\u4E2D\u53BB\u300D\u7684\u826F\u6027\u5FAA\u73AF\u3002</p><p>\u4E5F\u6B63\u662F\u56E0\u4E3A\u8FD9\u4E2A\u7279\u70B9\uFF0CHTTP \u624D\u80FD\u5728\u4E09\u5341\u5E74\u7684\u5386\u53F2\u957F\u6CB3\u4E2D\u300C\u5C79\u7ACB\u4E0D\u5012\u300D\uFF0C\u4ECE\u6700\u521D\u7684\u4F4E\u901F\u5B9E\u9A8C\u7F51\u7EDC\u53D1\u5C55\u5230\u73B0\u5728\u7684\u904D\u5E03\u5168\u7403\u7684\u9AD8\u901F\u4E92\u8054\u7F51\uFF0C\u59CB\u7EC8\u4FDD\u6301\u7740\u65FA\u76DB\u7684\u751F\u547D\u529B\u3002</p><h2 id="\u53EF\u9760\u4F20\u8F93" tabindex="-1">\u53EF\u9760\u4F20\u8F93 <a class="header-anchor" href="#\u53EF\u9760\u4F20\u8F93" aria-hidden="true">#</a></h2><p>\u7B2C\u4E8C\u4E2A\u7279\u70B9\uFF0C HTTP \u534F\u8BAE\u662F\u4E00\u4E2A\u201C\u53EF\u9760\u201D\u7684\u4F20\u8F93\u534F\u8BAE\u3002</p><p>\u8FD9\u4E2A\u7279\u70B9\u663E\u800C\u6613\u89C1\uFF0C\u56E0\u4E3A HTTP \u534F\u8BAE\u662F\u57FA\u4E8E TCP/IP \u7684\uFF0C\u800C TCP \u672C\u8EAB\u662F\u4E00\u4E2A <strong>\u53EF\u9760</strong> \u7684\u4F20\u8F93\u534F\u8BAE\uFF0C\u6240\u4EE5 HTTP \u81EA\u7136\u4E5F\u5C31\u7EE7\u627F\u4E86\u8FD9\u4E2A\u7279\u6027\uFF0C\u80FD\u591F\u5728\u8BF7\u6C42\u65B9\u548C\u5E94\u7B54\u65B9\u4E4B\u95F4\u201C\u53EF\u9760\u201D\u5730\u4F20\u8F93\u6570\u636E\u3002</p><p>\u5B83\u7684\u5177\u4F53\u505A\u6CD5\u4E0E TCP/UDP \u5DEE\u4E0D\u591A\uFF0C\u90FD\u662F\u5BF9\u5B9E\u9645\u4F20\u8F93\u7684\u6570\u636E\uFF08entity\uFF09\u505A\u4E86\u4E00\u5C42\u5305\u88C5\uFF0C\u52A0\u4E0A\u4E00\u4E2A\u5934\uFF0C\u7136\u540E\u8C03\u7528 Socket API\uFF0C\u901A\u8FC7 TCP/IP \u534F\u8BAE\u6808\u53D1\u9001\u6216\u8005\u63A5\u6536\u3002</p><p>\u4E0D\u8FC7\u6211\u4EEC\u5FC5\u987B\u6B63\u786E\u5730\u7406\u89E3 <strong>\u53EF\u9760</strong> \u7684\u542B\u4E49\uFF0CHTTP \u5E76\u4E0D\u80FD 100% \u4FDD\u8BC1\u6570\u636E\u4E00\u5B9A\u80FD\u591F\u53D1\u9001\u5230\u53E6\u4E00\u7AEF\uFF0C\u5728\u7F51\u7EDC\u7E41\u5FD9\u3001\u8FDE\u63A5\u8D28\u91CF\u5DEE\u7B49\u6076\u52A3\u7684\u73AF\u5883\u4E0B\uFF0C\u4E5F\u6709\u53EF\u80FD\u6536\u53D1\u5931\u8D25\u3002 <strong>\u53EF\u9760</strong> \u53EA\u662F\u5411\u4F7F\u7528\u8005\u63D0\u4F9B\u4E86\u4E00\u4E2A <strong>\u627F\u8BFA</strong> \uFF0C\u4F1A\u5728\u4E0B\u5C42\u7528\u591A\u79CD\u624B\u6BB5 <strong>\u5C3D\u91CF</strong> \u4FDD\u8BC1\u6570\u636E\u7684\u5B8C\u6574\u9001\u8FBE\u3002</p><p>\u5F53\u7136\uFF0C\u5982\u679C\u9047\u5230\u5149\u7EA4\u88AB\u610F\u5916\u6316\u65AD\u8FD9\u6837\u7684\u6781\u7AEF\u60C5\u51B5\uFF0C\u5373\u4F7F\u662F\u795E\u4ED9\u4E5F\u4E0D\u80FD\u53D1\u9001\u6210\u529F\u3002\u6240\u4EE5\uFF0C\u53EF\u9760\u4F20\u8F93\u662F\u6307\u5728\u7F51\u7EDC\u57FA\u672C\u6B63\u5E38\u7684\u60C5\u51B5\u4E0B\u6570\u636E\u6536\u53D1\u5FC5\u5B9A\u6210\u529F\uFF0C\u501F\u7528\u8FD0\u7EF4\u91CC\u7684\u672F\u8BED\uFF0C\u5927\u6982\u5C31\u662F \u300C3 \u4E2A 9\u300D\u6216\u8005\u300C4 \u4E2A 9\u300D\u7684\u7A0B\u5EA6\u5427\u3002</p><h2 id="\u5E94\u7528\u5C42\u534F\u8BAE" tabindex="-1">\u5E94\u7528\u5C42\u534F\u8BAE <a class="header-anchor" href="#\u5E94\u7528\u5C42\u534F\u8BAE" aria-hidden="true">#</a></h2><p>\u7B2C\u4E09\u4E2A\u7279\u70B9\uFF0CHTTP \u534F\u8BAE\u662F\u4E00\u4E2A\u5E94\u7528\u5C42\u7684\u534F\u8BAE\u3002</p><p>\u8FD9\u4E2A\u7279\u70B9\u4E5F\u662F\u4E0D\u8A00\u81EA\u660E\u7684\uFF0C\u4F46\u5374\u5F88\u91CD\u8981\u3002</p><p>\u5728 TCP/IP \u8BDE\u751F\u540E\u7684\u51E0\u5341\u5E74\u91CC\uFF0C\u867D\u7136\u51FA\u73B0\u4E86\u8BB8\u591A\u7684\u5E94\u7528\u5C42\u534F\u8BAE\uFF0C\u4F46\u5B83\u4EEC\u90FD\u4EC5\u5173\u6CE8\u5F88\u5C0F\u7684\u5E94\u7528\u9886\u57DF\uFF0C\u5C40\u9650\u5728\u5F88\u5C11\u7684\u5E94\u7528\u573A\u666F\u3002\u4F8B\u5982 FTP \u53EA\u80FD\u4F20\u8F93\u6587\u4EF6\u3001SMTP \u53EA\u80FD\u53D1\u9001\u90AE\u4EF6\u3001SSH \u53EA\u80FD\u8FDC\u7A0B\u767B\u5F55\u7B49\uFF0C\u5728\u901A\u7528\u7684\u6570\u636E\u4F20\u8F93\u65B9\u9762 <strong>\u5B8C\u5168\u4E0D\u80FD\u6253</strong> \u3002</p><p>\u6240\u4EE5 HTTP \u51ED\u501F\u7740 <strong>\u53EF\u643A\u5E26\u4EFB\u610F\u5934\u5B57\u6BB5\u548C\u5B9E\u4F53\u6570\u636E\u7684\u62A5\u6587\u7ED3\u6784</strong> \uFF0C\u4EE5\u53CA\u8FDE\u63A5\u63A7\u5236\u3001\u7F13\u5B58\u4EE3\u7406\u7B49\u65B9\u4FBF\u6613\u7528\u7684\u7279\u6027\uFF0C\u4E00\u51FA\u73B0\u5C31\u6280\u538B\u7FA4\u96C4\uFF0C\u8FC5\u901F\u6210\u4E3A\u4E86\u5E94\u7528\u5C42\u91CC\u7684\u660E\u661F\u534F\u8BAE\u3002\u53EA\u8981\u4E0D\u592A\u82DB\u6C42\u6027\u80FD\uFF0CHTTP \u51E0\u4E4E\u53EF\u4EE5\u4F20\u9012\u4E00\u5207\u4E1C\u897F\uFF0C\u6EE1\u8DB3\u5404\u79CD\u9700\u6C42\uFF0C\u79F0\u5F97\u4E0A\u662F\u4E00\u4E2A\u4E07\u80FD\u7684\u534F\u8BAE\u3002</p><p>\u5957\u7528\u4E00\u4E2A\u7F51\u4E0A\u6D41\u884C\u7684\u6BB5\u5B50\uFF0CHTTP \u5B8C\u5168\u53EF\u4EE5\u7528\u5F00\u73A9\u7B11\u7684\u53E3\u543B\u8BF4\uFF1A\u4E0D\u8981\u8BEF\u4F1A\uFF0C\u6211\u4E0D\u662F\u9488\u5BF9 FTP\uFF0C\u6211\u662F\u8BF4\u5728\u5EA7\u7684\u5E94\u7528\u5C42\u5404\u4F4D\uFF0C\u90FD\u662F\u5783\u573E\u3002</p><h2 id="\u8BF7\u6C42-\u5E94\u7B54" tabindex="-1">\u8BF7\u6C42 - \u5E94\u7B54 <a class="header-anchor" href="#\u8BF7\u6C42-\u5E94\u7B54" aria-hidden="true">#</a></h2><p>\u7B2C\u56DB\u4E2A\u7279\u70B9\uFF0CHTTP \u534F\u8BAE\u4F7F\u7528\u7684\u662F\u8BF7\u6C42 - \u5E94\u7B54\u901A\u4FE1\u6A21\u5F0F\u3002</p><p>\u8FD9\u4E2A\u8BF7\u6C42 - \u5E94\u7B54\u6A21\u5F0F\u662F HTTP \u534F\u8BAE\u6700\u6839\u672C\u7684\u901A\u4FE1\u6A21\u578B\uFF0C\u901A\u4FD7\u6765\u8BB2\u5C31\u662F <strong>\u4E00\u53D1\u4E00\u6536\u3001\u6709\u6765\u6709\u53BB</strong> \uFF0C\u5C31\u50CF\u662F\u5199\u4EE3\u7801\u65F6\u7684\u51FD\u6570\u8C03\u7528\uFF0C\u53EA\u8981\u586B\u597D\u8BF7\u6C42\u5934\u91CC\u7684\u5B57\u6BB5\uFF0C\u8C03\u7528\u540E\u5C31\u4F1A\u6536\u5230\u7B54\u590D\u3002</p><p>\u8BF7\u6C42 - \u5E94\u7B54\u6A21\u5F0F\u4E5F\u660E\u786E\u4E86 HTTP \u534F\u8BAE\u91CC\u901A\u4FE1\u53CC\u65B9\u7684\u5B9A\u4F4D\uFF0C<strong>\u6C38\u8FDC\u662F\u8BF7\u6C42\u65B9\u5148\u53D1\u8D77\u8FDE\u63A5\u548C\u8BF7\u6C42</strong> \uFF0C<strong>\u662F\u4E3B\u52A8\u7684</strong> \uFF0C\u800C\u5E94\u7B54\u65B9\u53EA\u6709\u5728\u6536\u5230\u8BF7\u6C42\u540E\u624D\u80FD\u7B54\u590D\uFF0C\u662F\u88AB\u52A8\u7684\uFF0C\u5982\u679C\u6CA1\u6709\u8BF7\u6C42\u65F6\u4E0D\u4F1A\u6709\u4EFB\u4F55\u52A8\u4F5C\u3002</p><p>\u5F53\u7136\uFF0C\u8BF7\u6C42\u65B9\u548C\u5E94\u7B54\u65B9\u7684\u89D2\u8272\u4E5F\u4E0D\u662F\u7EDD\u5BF9\u7684\uFF0C\u5728\u6D4F\u89C8\u5668 - \u670D\u52A1\u5668\u7684\u573A\u666F\u91CC\uFF0C\u901A\u5E38\u670D\u52A1\u5668\u90FD\u662F\u5E94\u7B54\u65B9\uFF0C\u4F46\u5982\u679C\u5C06\u5B83\u7528\u4F5C\u4EE3\u7406\u8FDE\u63A5\u540E\u7AEF\u670D\u52A1\u5668\uFF0C\u90A3\u4E48\u5B83\u5C31\u53EF\u80FD\u540C\u65F6\u626E\u6F14\u8BF7\u6C42\u65B9\u548C\u5E94\u7B54\u65B9\u7684\u89D2\u8272\u3002</p><p>HTTP \u7684\u8BF7\u6C42 - \u5E94\u7B54\u6A21\u5F0F\u4E5F\u6070\u597D\u5951\u5408\u4E86\u4F20\u7EDF\u7684 C/S\uFF08Client/Server\uFF09\u7CFB\u7EDF\u67B6\u6784\uFF0C\u8BF7\u6C42\u65B9\u4F5C\u4E3A\u5BA2\u6237\u7AEF\u3001\u5E94\u7B54\u65B9\u4F5C\u4E3A\u670D\u52A1\u5668\u3002\u6240\u4EE5\uFF0C\u968F\u7740\u4E92\u8054\u7F51\u7684\u53D1\u5C55\u5C31\u51FA\u73B0\u4E86 B/S\uFF08Browser/Server\uFF09\u67B6\u6784\uFF0C\u7528\u8F7B\u91CF\u7EA7\u7684\u6D4F\u89C8\u5668\u4EE3\u66FF\u7B28\u91CD\u7684\u5BA2\u6237\u7AEF\u5E94\u7528\uFF0C\u5B9E\u73B0\u96F6\u7EF4\u62A4\u7684\u7626\u5BA2\u6237\u7AEF\uFF0C\u800C\u670D\u52A1\u5668\u5219\u6448\u5F03\u79C1\u6709\u901A\u4FE1\u534F\u8BAE\u8F6C\u800C\u4F7F\u7528 HTTP \u534F\u8BAE\u3002</p><p>\u6B64\u5916\uFF0C\u8BF7\u6C42 - \u5E94\u7B54\u6A21\u5F0F\u4E5F\u5B8C\u5168\u7B26\u5408 RPC\uFF08Remote Procedure Call\uFF09\u7684\u5DE5\u4F5C\u6A21\u5F0F\uFF0C\u53EF\u4EE5\u628A HTTP \u8BF7\u6C42\u5904\u7406\u5C01\u88C5\u6210\u8FDC\u7A0B\u51FD\u6570\u8C03\u7528\uFF0C\u5BFC\u81F4\u4E86 WebService\u3001RESTful \u548C gPRC \u7B49\u7684\u51FA\u73B0\u3002</p><h2 id="\u65E0\u72B6\u6001" tabindex="-1">\u65E0\u72B6\u6001 <a class="header-anchor" href="#\u65E0\u72B6\u6001" aria-hidden="true">#</a></h2><p>\u7B2C\u4E94\u4E2A\u7279\u70B9\uFF0CHTTP \u534F\u8BAE\u662F\u65E0\u72B6\u6001\u7684\u3002</p><p>\u8FD9\u4E2A\u6240\u8C13\u7684 <strong>\u72B6\u6001</strong> \u5E94\u8BE5\u600E\u4E48\u7406\u89E3\u5462\uFF1F</p><p>\u72B6\u6001\u5176\u5B9E\u5C31\u662F\u5BA2\u6237\u7AEF\u6216\u8005\u670D\u52A1\u5668\u91CC\u4FDD\u5B58\u7684\u4E00\u4E9B\u6570\u636E\u6216\u8005\u6807\u5FD7\uFF0C\u8BB0\u5F55\u4E86\u901A\u4FE1\u8FC7\u7A0B\u4E2D\u7684\u4E00\u4E9B\u53D8\u5316\u4FE1\u606F\u3002</p><p>\u4F60\u4E00\u5B9A\u77E5\u9053\uFF0CTCP \u534F\u8BAE\u662F\u6709\u72B6\u6001\u7684\uFF0C\u4E00\u5F00\u59CB\u5904\u4E8E CLOSED \u72B6\u6001\uFF0C\u8FDE\u63A5\u6210\u529F\u540E\u662F ESTABLISHED \u72B6\u6001\uFF0C\u65AD\u5F00\u8FDE\u63A5\u540E\u662F FIN-WAIT \u72B6\u6001\uFF0C\u6700\u540E\u53C8\u662F CLOSED \u72B6\u6001\u3002</p><p>\u8FD9\u4E9B\u72B6\u6001\u5C31\u9700\u8981 TCP \u5728\u5185\u90E8\u7528\u4E00\u4E9B\u6570\u636E\u7ED3\u6784\u53BB\u7EF4\u62A4\uFF0C\u53EF\u4EE5\u7B80\u5355\u5730\u60F3\u8C61\u6210\u662F\u4E2A\u6807\u5FD7\u91CF\uFF0C\u6807\u8BB0\u5F53\u524D\u6240\u5904\u7684\u72B6\u6001\uFF0C\u4F8B\u5982 0 \u662F CLOSED\uFF0C2 \u662F ESTABLISHED \u7B49\u7B49\u3002</p><p>\u518D\u6765\u770B HTTP\uFF0C\u90A3\u4E48\u5BF9\u6BD4\u4E00\u4E0B TCP \u5C31\u770B\u51FA\u6765\u4E86\uFF0C<strong>\u5728\u6574\u4E2A\u534F\u8BAE\u91CC\u6CA1\u6709\u89C4\u5B9A\u4EFB\u4F55\u7684\u72B6\u6001</strong> \uFF0C\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u6C38\u8FDC\u662F\u5904\u5728\u4E00\u79CD <strong>\u65E0\u77E5</strong> \u7684\u72B6\u6001\u3002\u5EFA\u7ACB\u8FDE\u63A5\u524D\u4E24\u8005\u4E92\u4E0D\u77E5\u60C5\uFF0C\u6BCF\u6B21\u6536\u53D1\u7684\u62A5\u6587\u4E5F\u90FD\u662F\u4E92\u76F8\u72EC\u7ACB\u7684\uFF0C\u6CA1\u6709\u4EFB\u4F55\u7684\u8054\u7CFB\u3002\u6536\u53D1\u62A5\u6587\u4E5F\u4E0D\u4F1A\u5BF9\u5BA2\u6237\u7AEF\u6216\u670D\u52A1\u5668\u4EA7\u751F\u4EFB\u4F55\u5F71\u54CD\uFF0C\u8FDE\u63A5\u540E\u4E5F\u4E0D\u4F1A\u8981\u6C42\u4FDD\u5B58\u4EFB\u4F55\u4FE1\u606F\u3002</p><p><strong>\u65E0\u72B6\u6001</strong> \u5F62\u8C61\u5730\u6765\u8BF4\u5C31\u662F\u6CA1\u6709\u8BB0\u5FC6\u80FD\u529B\u3002\u6BD4\u5982\uFF0C\u6D4F\u89C8\u5668\u53D1\u4E86\u4E00\u4E2A\u8BF7\u6C42\uFF0C\u8BF4\u300C\u6211\u662F\u5C0F\u660E\uFF0C\u8BF7\u7ED9\u6211 A \u6587\u4EF6\u3002\u300D\uFF0C\u670D\u52A1\u5668\u6536\u5230\u62A5\u6587\u540E\u5C31\u4F1A\u68C0\u67E5\u4E00\u4E0B\u6743\u9650\uFF0C\u770B\u5C0F\u660E\u786E\u5B9E\u53EF\u4EE5\u8BBF\u95EE A \u6587\u4EF6\uFF0C\u4E8E\u662F\u628A\u6587\u4EF6\u53D1\u56DE\u7ED9\u6D4F\u89C8\u5668\u3002\u63A5\u7740\u6D4F\u89C8\u5668\u8FD8\u60F3\u8981 B \u6587\u4EF6\uFF0C\u4F46\u670D\u52A1\u5668\u4E0D\u4F1A\u8BB0\u5F55\u521A\u624D\u7684\u8BF7\u6C42\u72B6\u6001\uFF0C\u4E0D\u77E5\u9053\u7B2C\u4E8C\u4E2A\u8BF7\u6C42\u548C\u7B2C\u4E00\u4E2A\u8BF7\u6C42\u662F\u540C\u4E00\u4E2A\u6D4F\u89C8\u5668\u53D1\u6765\u7684\uFF0C\u6240\u4EE5\u6D4F\u89C8\u5668\u5FC5\u987B\u8FD8\u5F97\u91CD\u590D\u4E00\u6B21\u81EA\u5DF1\u7684\u8EAB\u4EFD\u624D\u884C\uFF1A\u300C\u6211\u662F\u521A\u624D\u7684\u5C0F\u660E\uFF0C\u8BF7\u518D\u7ED9\u6211 B \u6587\u4EF6\u3002\u300D</p><p>\u6211\u4EEC\u53EF\u4EE5\u518D\u5BF9\u6BD4\u4E00\u4E0B UDP \u534F\u8BAE\uFF0C\u4E0D\u8FC7\u5B83\u662F\u65E0\u8FDE\u63A5\u4E5F\u65E0\u72B6\u6001\u7684\uFF0C\u987A\u5E8F\u53D1\u5305\u4E71\u5E8F\u6536\u5305\uFF0C\u6570\u636E\u5305\u53D1\u51FA\u53BB\u540E\u5C31\u4E0D\u7BA1\u4E86\uFF0C\u6536\u5230\u540E\u4E5F\u4E0D\u4F1A\u987A\u5E8F\u6574\u7406\u3002\u800C HTTP \u662F\u6709\u8FDE\u63A5\u65E0\u72B6\u6001\uFF0C\u987A\u5E8F\u53D1\u5305\u987A\u5E8F\u6536\u5305\uFF0C\u6309\u7167\u6536\u53D1\u7684\u987A\u5E8F\u7BA1\u7406\u62A5\u6587\u3002</p><p>\u4F46\u4E0D\u8981\u5FD8\u4E86 HTTP \u662F <strong>\u7075\u6D3B\u53EF\u6269\u5C55</strong> \u7684\uFF0C\u867D\u7136\u6807\u51C6\u91CC\u6CA1\u6709\u89C4\u5B9A\u72B6\u6001\uFF0C\u4F46\u5B8C\u5168\u80FD\u591F\u5728\u534F\u8BAE\u7684\u6846\u67B6\u91CC\u7ED9\u5B83\u6253\u4E2A\u8865\u4E01\uFF0C\u589E\u52A0\u8FD9\u4E2A\u7279\u6027\u3002</p><h2 id="\u5176\u4ED6\u7279\u70B9" tabindex="-1">\u5176\u4ED6\u7279\u70B9 <a class="header-anchor" href="#\u5176\u4ED6\u7279\u70B9" aria-hidden="true">#</a></h2><p>\u9664\u4E86\u4EE5\u4E0A\u7684\u4E94\u5927\u7279\u70B9\uFF0C\u5176\u5B9E HTTP \u534F\u8BAE\u8FD8\u53EF\u4EE5\u5217\u51FA\u975E\u5E38\u591A\u7684\u7279\u70B9\uFF0C\u4F8B\u5982\u4F20\u8F93\u7684\u5B9E\u4F53\u6570\u636E\u53EF\u7F13\u5B58\u53EF\u538B\u7F29\u3001\u53EF\u5206\u6BB5\u83B7\u53D6\u6570\u636E\u3001\u652F\u6301\u8EAB\u4EFD\u8BA4\u8BC1\u3001\u652F\u6301\u56FD\u9645\u5316\u8BED\u8A00\u7B49\u3002\u4F46\u8FD9\u4E9B\u5E76\u4E0D\u80FD\u7B97\u662F HTTP \u7684\u57FA\u672C\u7279\u70B9\uFF0C\u56E0\u4E3A\u8FD9\u90FD\u662F\u7531\u7B2C\u4E00\u4E2A <strong>\u7075\u6D3B\u53EF\u6269\u5C55</strong> \u7684\u7279\u70B9\u6240\u884D\u751F\u51FA\u6765\u7684\u3002</p><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><ol><li>HTTP \u662F\u7075\u6D3B\u53EF\u6269\u5C55\u7684\uFF0C\u53EF\u4EE5\u4EFB\u610F\u6DFB\u52A0\u5934\u5B57\u6BB5\u5B9E\u73B0\u4EFB\u610F\u529F\u80FD\uFF1B</li><li>HTTP \u662F\u53EF\u9760\u4F20\u8F93\u534F\u8BAE\uFF0C\u57FA\u4E8E TCP/IP \u534F\u8BAE\u201C\u5C3D\u91CF\u201D\u4FDD\u8BC1\u6570\u636E\u7684\u9001\u8FBE\uFF1B</li><li>HTTP \u662F\u5E94\u7528\u5C42\u534F\u8BAE\uFF0C\u6BD4 FTP\u3001SSH \u7B49\u66F4\u901A\u7528\u529F\u80FD\u66F4\u591A\uFF0C\u80FD\u591F\u4F20\u8F93\u4EFB\u610F\u6570\u636E\uFF1B</li><li>HTTP \u4F7F\u7528\u4E86\u8BF7\u6C42 - \u5E94\u7B54\u6A21\u5F0F\uFF0C\u5BA2\u6237\u7AEF\u4E3B\u52A8\u53D1\u8D77\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u88AB\u52A8\u56DE\u590D\u8BF7\u6C42\uFF1B</li><li>HTTP \u672C\u8D28\u4E0A\u662F\u65E0\u72B6\u6001\u7684\uFF0C\u6BCF\u4E2A\u8BF7\u6C42\u90FD\u662F\u4E92\u76F8\u72EC\u7ACB\u3001\u6BEB\u65E0\u5173\u8054\u7684\uFF0C\u534F\u8BAE\u4E0D\u8981\u6C42\u5BA2\u6237\u7AEF\u6216\u670D\u52A1\u5668\u8BB0\u5F55\u8BF7\u6C42\u76F8\u5173\u7684\u4FE1\u606F\u3002</li></ol><h2 id="\u8BFE\u4E0B\u4F5C\u4E1A" tabindex="-1">\u8BFE\u4E0B\u4F5C\u4E1A <a class="header-anchor" href="#\u8BFE\u4E0B\u4F5C\u4E1A" aria-hidden="true">#</a></h2><ol><li><p>\u5C31\u5982\u540C\u5F00\u5934\u6211\u8BB2\u7684\u90A3\u6837\uFF0C\u4F60\u80FD\u8BF4\u4E00\u4E0B\u4ECA\u5929\u5217\u51FA\u7684\u8FD9\u4E9B HTTP \u7684\u7279\u70B9\u4E2D\u54EA\u4E9B\u662F\u4F18\u70B9\uFF0C\u54EA\u4E9B\u662F\u7F3A\u70B9\u5417\uFF1F</p><p>\u7B14\u8005\u8BA4\u4E3A\uFF1A\u4EE5\u4E0A\u7279\u70B9\u5728\u65E2\u5B9A\u7684\u573A\u666F\u4E2D\u5C31\u662F\u4F18\u70B9\u4E5F\u53EF\u80FD\u662F\u7F3A\u70B9\uFF0C\u6BD4\u5982\u65E0\u72B6\u6001\uFF0C\u9700\u8981\u989D\u5916\u7684\u6765\u6253\u8865\u4E01\u5B9E\u73B0</p></li><li><p>\u4E0D\u540C\u7684\u5E94\u7528\u573A\u5408\u6709\u4E0D\u540C\u7684\u4FA7\u91CD\u65B9\u9762\uFF0C\u4F60\u89C9\u5F97\u54EA\u4E2A\u7279\u70B9\u5BF9\u4F60\u6765\u8BF4\u662F\u6700\u91CD\u8981\u7684\u5462\uFF1F</p><p>\u7B14\u8005\u8BA4\u4E3A\uFF1A\u7075\u6D3B\u53EF\u6269\u5C55\u662F\u6700\u91CD\u8981\u7684\uFF0C\u4E0D\u7136\u4E5F\u4E0D\u4F1A\u5C79\u7ACB\u4E09\u5341\u5E74\u4E0D\u5012\u4E86</p></li></ol><h2 id="\u8BFE\u5916\u5C0F\u8D34\u58EB" tabindex="-1">\u8BFE\u5916\u5C0F\u8D34\u58EB <a class="header-anchor" href="#\u8BFE\u5916\u5C0F\u8D34\u58EB" aria-hidden="true">#</a></h2><ul><li>\u5982\u679C\u8981 100% \u4FDD\u8BC1\u6570\u636E\u6536\u53D1\u6210\u529F\u5C31\u4E0D\u80FD\u4F7F\u7528 HTTP \u6216\u5219 TCP \u534F\u8BAE\u4E86\uFF0C\u800C\u662F\u8981\u7528\u5404\u79CD\u6D88\u606F\u4E2D\u95F4\u4EF6\uFF08MQ\uFF09\uFF0C\u5982 RabbitMQ\u3001ZeroMQ\u3001Kafka \u7B49</li><li>\u4EE5\u524D HTTP \u534F\u8BAE\u8FD8\u6709\u4E00\u4E2A <strong>\u65E0\u8FDE\u63A5</strong> \u7684\u7279\u70B9\uFF0C\u6307\u7684\u662F\u534F\u8BAE\u4E0D\u4FDD\u6301\u8FDE\u63A5\u72B6\u6001\uFF0C\u6BCF\u6B21\u8BF7\u6C42\u5E94\u7B54\u540E\u90FD\u4F1A\u5173\u95ED\u8FDE\u63A5\uFF0C\u8FD9\u5C31\u548C UDP \u51E0\u4E4E\u4E00\u6A21\u4E00\u6837\u4E86\u3002\u4F46\u8FD9\u6837\u5F88\u5F71\u54CD\u6027\u80FD\uFF0C\u5728 HTTP/1.1 \u91CC\u5C31\u6539\u6210\u4E86\u603B\u662F\u9ED8\u8BA4\u542F\u7528 keepalive \u957F\u8FDE\u63A5\u673A\u5236\uFF0C\u6240\u4EE5\u73B0\u5728\u7684 HTTP \u5DF2\u7ECF\u4E0D\u518D\u662F <strong>\u65E0\u8FDE\u63A5</strong> \u7684\u4E86</li><li>\u6CE8\u610F HTTP \u7684 <strong>\u65E0\u72B6\u6001</strong> \u7279\u70B9\u4E0E\u54CD\u5E94\u5934\u91CC\u7684 <strong>\u72B6\u6001\u7801</strong> \u662F\u5B8C\u5168\u4E0D\u76F8\u5173\u7684\u4E24\u4E2A\u6982\u5FF5\uFF0C\u72B6\u6001\u7801\u8868\u793A\u7684\u662F\u6B64\u6B21\u62A5\u6587\u5904\u7406\u7684\u7ED3\u679C\uFF0C\u5E76\u4E0D\u4F1A\u5BFC\u81F4\u670D\u52A1\u5668\u5185\u90E8\u72B6\u6001\u53D8\u5316</li></ul>',49),l=[i];function s(p,o,d,h,P,c){return r(),t("div",null,l)}const _=e(n,[["render",s]]);export{H as __pageData,_ as default};
