import{_ as e,o as n,c as s,a}from"./app.436197f7.js";const p="/vitepress-demo/assets/9f7b79c43c476890f03c2c716a20f301.aec84165.png",t="/vitepress-demo/assets/4f24aa3f53969b71baaf7d9c7cf68fd5.3adf1479.png",o="/vitepress-demo/assets/9fc3df52df7d6c11aa02b8013f8e9bc6.b1a0686f.png",l="/vitepress-demo/assets/3689312a970bae0e949b017ad45438df.b3f6784f.png",_=JSON.parse('{"title":"02 | OpenResty\uFF1A\u66F4\u7075\u6D3B\u7684 Web \u670D\u52A1\u5668","description":"","frontmatter":{},"headers":[{"level":2,"title":"OpenResty \u662F\u4EC0\u4E48\uFF1F","slug":"openresty-\u662F\u4EC0\u4E48\uFF1F","link":"#openresty-\u662F\u4EC0\u4E48\uFF1F","children":[]},{"level":2,"title":"\u52A8\u6001\u7684 Lua","slug":"\u52A8\u6001\u7684-lua","link":"#\u52A8\u6001\u7684-lua","children":[]},{"level":2,"title":"\u9AD8\u6548\u7387\u7684 Lua","slug":"\u9AD8\u6548\u7387\u7684-lua","link":"#\u9AD8\u6548\u7387\u7684-lua","children":[]},{"level":2,"title":"\u9636\u6BB5\u5F0F\u5904\u7406","slug":"\u9636\u6BB5\u5F0F\u5904\u7406","link":"#\u9636\u6BB5\u5F0F\u5904\u7406","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]},{"level":2,"title":"\u8BFE\u4E0B\u4F5C\u4E1A","slug":"\u8BFE\u4E0B\u4F5C\u4E1A","link":"#\u8BFE\u4E0B\u4F5C\u4E1A","children":[]}],"relativePath":"http-protocol/07/02.md","lastUpdated":1677316569000}'),r={name:"http-protocol/07/02.md"},i=a('<h1 id="_02-openresty\uFF1A\u66F4\u7075\u6D3B\u7684-web-\u670D\u52A1\u5668" tabindex="-1">02 | OpenResty\uFF1A\u66F4\u7075\u6D3B\u7684 Web \u670D\u52A1\u5668 <a class="header-anchor" href="#_02-openresty\uFF1A\u66F4\u7075\u6D3B\u7684-web-\u670D\u52A1\u5668" aria-hidden="true">#</a></h1><p>\u5728\u4E0A\u4E00\u8BB2\u91CC\uFF0C\u6211\u4EEC\u770B\u5230\u4E86\u9AD8\u6027\u80FD\u7684 Web \u670D\u52A1\u5668 Nginx\uFF0C\u5B83\u8D44\u6E90\u5360\u7528\u5C11\uFF0C\u5904\u7406\u80FD\u529B\u9AD8\uFF0C\u662F\u642D\u5EFA\u7F51\u7AD9\u7684\u9996\u9009\u3002</p><p>\u867D\u7136 Nginx \u6210\u4E3A\u4E86 Web \u670D\u52A1\u5668\u9886\u57DF\u65E0\u53EF\u4E89\u8BAE\u7684\u738B\u8005\uFF0C\u4F46\u5B83\u4E5F\u5E76\u4E0D\u662F\u6CA1\u6709\u7F3A\u70B9\u7684\uFF0C\u6BD5\u7ADF\u5B83\u5DF2\u7ECF 15 \u5C81\u4E86\u3002</p><p>\u4E00\u4E2A\u4EBA\u5F88\u96BE\u8D85\u8D8A\u65F6\u4EE3\uFF0C\u800C\u65F6\u4EE3\u5374\u53EF\u4EE5\u8F7B\u6613\u8D85\u8D8A\u6240\u6709\u4EBA\uFF0CNginx \u5F53\u521D\u8BBE\u8BA1\u65F6\u9488\u5BF9\u7684\u5E94\u7528\u573A\u666F\u5DF2\u7ECF\u53D1\u751F\u4E86\u53D8\u5316\uFF0C\u5B83\u7684\u4E00\u4E9B\u7F3A\u70B9\u4E5F\u5C31\u66B4\u9732\u51FA\u6765\u4E86\u3002</p><p>Nginx \u7684\u670D\u52A1\u7BA1\u7406\u601D\u8DEF\u5EF6\u7EED\u4E86\u5F53\u65F6\u7684\u6D41\u884C\u505A\u6CD5\uFF0C<strong>\u4F7F\u7528\u78C1\u76D8\u4E0A\u7684\u9759\u6001\u914D\u7F6E\u6587\u4EF6\uFF0C\u6240\u4EE5\u6BCF\u6B21\u4FEE\u6539\u540E\u5FC5\u987B\u91CD\u542F\u624D\u80FD\u751F\u6548</strong> \u3002</p><p>\u8FD9\u5728\u4E1A\u52A1\u9891\u7E41\u53D8\u52A8\u7684\u65F6\u5019\u662F\u975E\u5E38\u81F4\u547D\u7684\uFF08\u4F8B\u5982\u6D41\u884C\u7684\u5FAE\u670D\u52A1\u67B6\u6784\uFF09\uFF0C\u7279\u522B\u662F\u5BF9\u4E8E\u62E5\u6709\u6210\u5343\u4E0A\u4E07\u53F0\u670D\u52A1\u5668\u7684\u7F51\u7AD9\u6765\u8BF4\uFF0C\u4EC5\u4EC5\u589E\u52A0\u6216\u8005\u5220\u9664\u4E00\u884C\u914D\u7F6E\u5C31\u8981\u5206\u53D1\u3001\u91CD\u542F\u6240\u6709\u7684\u673A\u5668\uFF0C\u5BF9\u8FD0\u7EF4\u662F\u4E00\u4E2A\u975E\u5E38\u5927\u7684\u6311\u6218\uFF0C\u8981\u8017\u8D39\u5F88\u591A\u7684\u65F6\u95F4\u548C\u7CBE\u529B\uFF0C\u6210\u672C\u5F88\u9AD8\uFF0C\u5F88\u4E0D\u7075\u6D3B\uFF0C\u96BE\u4EE5\u201C\u968F\u9700\u5E94\u53D8\u201D\u3002</p><p>\u90A3\u4E48\uFF0C\u6709\u6CA1\u6709\u8FD9\u6837\u7684\u4E00\u4E2A Web \u670D\u52A1\u5668\uFF0C\u5B83\u6709 Nginx \u7684\u4F18\u70B9\u5374\u6CA1\u6709 Nginx \u7684\u7F3A\u70B9\uFF0C\u65E2\u8F7B\u91CF\u7EA7\u3001\u9AD8\u6027\u80FD\uFF0C\u53C8\u7075\u6D3B\u3001\u53EF\u52A8\u6001\u914D\u7F6E\u5462\uFF1F</p><p>\u8FD9\u5C31\u662F\u6211\u4ECA\u5929\u8981\u8BF4\u7684 OpenResty\uFF0C\u5B83\u662F\u4E00\u4E2A <strong>\u66F4\u597D\u66F4\u7075\u6D3B\u7684 Nginx</strong> \u3002</p><h2 id="openresty-\u662F\u4EC0\u4E48\uFF1F" tabindex="-1">OpenResty \u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#openresty-\u662F\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><p>\u5176\u5B9E\u4F60\u5BF9 OpenResty \u5E76\u4E0D\u964C\u751F\uFF0C\u8FD9\u4E2A\u4E13\u680F\u7684\u5B9E\u9A8C\u73AF\u5883\u5C31\u662F\u7528 OpenResty \u642D\u5EFA\u7684\uFF0C\u8FD9\u4E48\u591A\u8282\u8BFE\u7A0B\u4E0B\u6765\uFF0C\u4F60\u5E94\u8BE5\u6216\u591A\u6216\u5C11\u5BF9\u5B83\u6709\u4E86\u4E00\u4E9B\u5370\u8C61\u5427\u3002</p><p>OpenResty \u8BDE\u751F\u4E8E 2009 \u5E74\uFF0C\u5230\u73B0\u5728\u521A\u597D\u6EE1 10 \u5468\u5C81\u3002\u5B83\u7684\u521B\u9020\u8005\u662F\u5F53\u65F6\u5C31\u804C\u4E8E\u67D0\u5B9D\u7684\u795E\u7EA7\u7A0B\u5E8F\u5458 <strong>\u7AE0\u4EA6\u6625</strong>\uFF0C\u7F51\u540D\u53EB <code>agentzh</code> \u3002</p><p>OpenResty \u5E76\u4E0D\u662F\u4E00\u4E2A\u5168\u65B0\u7684 Web \u670D\u52A1\u5668\uFF0C\u800C\u662F\u57FA\u4E8E Nginx\uFF0C\u5B83\u5229\u7528\u4E86 Nginx \u6A21\u5757\u5316\u3001\u53EF\u6269\u5C55\u7684\u7279\u6027\uFF0C\u5F00\u53D1\u4E86\u4E00\u7CFB\u5217\u7684\u589E\u5F3A\u6A21\u5757\uFF0C\u5E76\u628A\u5B83\u4EEC\u6253\u5305\u6574\u5408\uFF0C\u5F62\u6210\u4E86\u4E00\u4E2A <strong>\u300C\u4E00\u7AD9\u5F0F\u300D\u7684 Web \u5F00\u53D1\u5E73\u53F0</strong> \u3002</p><p>\u867D\u7136 OpenResty \u7684\u6838\u5FC3\u662F Nginx\uFF0C\u4F46\u5B83\u53C8\u8D85\u8D8A\u4E86 Nginx\uFF0C<strong>\u5173\u952E\u5C31\u5728\u4E8E\u5176\u4E2D\u7684 ngx_lua \u6A21\u5757</strong> \uFF0C\u628A\u5C0F\u5DE7\u7075\u6D3B\u7684 Lua \u8BED\u8A00\u5D4C\u5165\u4E86 Nginx\uFF0C\u53EF\u4EE5\u7528\u811A\u672C\u7684\u65B9\u5F0F\u64CD\u4F5C Nginx \u5185\u90E8\u7684\u8FDB\u7A0B\u3001\u591A\u8DEF\u590D\u7528\u3001\u9636\u6BB5\u5F0F\u5904\u7406\u7B49\u5404\u79CD\u6784\u4EF6\u3002</p><p>\u811A\u672C\u8BED\u8A00\u7684\u597D\u5904\u4F60\u4E00\u5B9A\u77E5\u9053\uFF0C\u5B83\u4E0D\u9700\u8981\u7F16\u8BD1\uFF0C\u968F\u5199\u968F\u6267\u884C\uFF0C\u8FD9\u5C31\u514D\u53BB\u4E86 C \u8BED\u8A00\u7F16\u5199\u6A21\u5757\u6F2B\u957F\u7684\u5F00\u53D1\u5468\u671F\u3002\u800C\u4E14 OpenResty \u8FD8\u628A Lua \u81EA\u8EAB\u7684\u534F\u7A0B\u4E0E Nginx \u7684\u4E8B\u4EF6\u673A\u5236\u5B8C\u7F8E\u7ED3\u5408\u5728\u4E00\u8D77\uFF0C\u4F18\u96C5\u5730\u5B9E\u73B0\u4E86\u8BB8\u591A\u5176\u4ED6\u8BED\u8A00\u6240\u6CA1\u6709\u7684 <strong>\u540C\u6B65\u975E\u963B\u585E</strong> \u7F16\u7A0B\u8303\u5F0F\uFF0C\u80FD\u591F\u8F7B\u677E\u5F00\u53D1\u51FA\u9AD8\u6027\u80FD\u7684 Web \u5E94\u7528\u3002</p><p>\u76EE\u524D OpenResty \u6709\u4E24\u4E2A\u5206\u652F\uFF0C\u5206\u522B\u662F\u5F00\u6E90\u3001\u514D\u8D39\u7684 <code>OpenResty</code> \u548C\u95ED\u6E90\u3001\u5546\u4E1A\u4EA7\u54C1\u7684 <code>OpenResty+</code> \uFF0C\u8FD0\u4F5C\u65B9\u5F0F\u6709\u793E\u533A\u652F\u6301\u3001OpenResty \u57FA\u91D1\u4F1A\u3001OpenResty.Inc \u516C\u53F8\uFF0C\u8FD8\u6709\u5176\u4ED6\u7684\u4E00\u4E9B\u5916\u754C\u8D5E\u52A9\uFF08\u4F8B\u5982 Kong\u3001CloudFlare\uFF09\uFF0C\u6B63\u5728\u84EC\u52C3\u53D1\u5C55\u3002</p><p><img src="'+p+'" alt="img"></p><p>\u987A\u4FBF\u8BF4\u4E00\u4E0B OpenResty \u7684\u5B98\u65B9 logo\uFF0C\u662F\u4E00\u53EA\u5C55\u7FC5\u98DE\u7FD4\u7684\u6D77\u9E25\uFF0C\u9009\u62E9\u6D77\u9E25\u662F\u56E0\u4E3A <code>\u9E25</code> \u4E0E OpenResty \u7684\u53D1\u97F3\u76F8\u540C\u3002\u53E6\u5916\uFF0C\u8FD9\u4E2A logo \u7684\u5F62\u72B6\u4E5F\u50CF\u662F\u5DE6\u624B\u6BD4\u51FA\u7684\u4E00\u4E2A <code>OK</code> \u59FF\u52BF\uFF0C\u6B63\u597D\u4E5F\u662F\u4E00\u4E2A <code>O</code> \u3002</p><h2 id="\u52A8\u6001\u7684-lua" tabindex="-1">\u52A8\u6001\u7684 Lua <a class="header-anchor" href="#\u52A8\u6001\u7684-lua" aria-hidden="true">#</a></h2><p>\u521A\u624D\u8BF4\u4E86\uFF0COpenResty \u91CC\u7684\u4E00\u4E2A\u5173\u952E\u6A21\u5757\u662F ngx_lua\uFF0C\u5B83\u4E3A Nginx \u5F15\u5165\u4E86\u811A\u672C\u8BED\u8A00 Lua\u3002</p><p>Lua \u662F\u4E00\u4E2A\u6BD4\u8F83\u300C\u5C0F\u4F17\u300D\u7684\u8BED\u8A00\uFF0C\u867D\u7136\u5386\u53F2\u6BD4\u8F83\u60A0\u4E45\uFF0C\u4F46\u540D\u6C14\u5374\u6CA1\u6709 PHP\u3001Python\u3001JavaScript \u5927\uFF0C\u8FD9\u4E3B\u8981\u4E0E\u5B83\u7684\u81EA\u8EAB\u5B9A\u4F4D\u6709\u5173\u3002</p><p><img src="'+t+'" alt="img"></p><p>Lua \u7684\u8BBE\u8BA1\u76EE\u6807\u662F\u5D4C\u5165\u5230\u5176\u4ED6\u5E94\u7528\u7A0B\u5E8F\u91CC\u8FD0\u884C\uFF0C\u4E3A\u5176\u4ED6\u7F16\u7A0B\u8BED\u8A00\u5E26\u6765\u300C\u811A\u672C\u5316\u300D\u80FD\u529B\uFF0C\u6240\u4EE5\u5B83\u7684\u4E2A\u5934\u6BD4\u8F83\u5C0F\uFF0C\u529F\u80FD\u96C6\u6709\u9650\uFF0C\u4E0D\u8FFD\u6C42\u5927\u800C\u5168\uFF0C\u800C\u662F\u5C0F\u800C\u7F8E\uFF0C\u5927\u591A\u6570\u65F6\u95F4\u90FD\u201C\u9690\u533F\u201D\u5728\u5176\u4ED6\u5E94\u7528\u7A0B\u5E8F\u7684\u540E\u9762\uFF0C\u662F\u65E0\u540D\u82F1\u96C4\u3002</p><p>\u4F60\u6216\u8BB8\u73A9\u8FC7\u6216\u8005\u542C\u8BF4\u8FC7\u300A\u9B54\u517D\u4E16\u754C\u300B\u300A\u6124\u6012\u7684\u5C0F\u9E1F\u300B\u5427\uFF0C\u5B83\u4EEC\u5C31\u5728\u5185\u90E8\u5D4C\u5165\u4E86 Lua\uFF0C\u4F7F\u7528 Lua \u6765\u8C03\u7528\u5E95\u5C42\u63A5\u53E3\uFF0C\u5145\u5F53 \u300C\u80F6\u6C34\u8BED\u8A00\uFF08glue language\uFF09\u300D\uFF0C\u7F16\u5199\u6E38\u620F\u903B\u8F91\u811A\u672C\uFF0C\u63D0\u9AD8\u5F00\u53D1\u6548\u7387\u3002</p><p>OpenResty \u9009\u62E9 Lua \u4F5C\u4E3A\u300C\u5DE5\u4F5C\u8BED\u8A00\u300D\u4E5F\u662F\u57FA\u4E8E\u540C\u6837\u7684\u8003\u8651\u3002\u56E0\u4E3A Nginx C \u5F00\u53D1\u5B9E\u5728\u662F\u592A\u9EBB\u70E6\u4E86\uFF0C\u9650\u5236\u4E86 Nginx \u7684\u771F\u6B63\u5B9E\u529B\u3002\u800C Lua \u4F5C\u4E3A\u300C\u6700\u5FEB\u7684\u811A\u672C\u8BED\u8A00\u300D\u6070\u597D\u53EF\u4EE5\u6210\u4E3A Nginx \u7684\u5B8C\u7F8E\u642D\u6863\uFF0C\u65E2\u53EF\u4EE5\u7B80\u5316\u5F00\u53D1\uFF0C\u6027\u80FD\u4E0A\u53C8\u4E0D\u4F1A\u6709\u592A\u591A\u7684\u635F\u8017\u3002</p><p>\u4F5C\u4E3A\u811A\u672C\u8BED\u8A00\uFF0CLua \u8FD8\u6709\u4E00\u4E2A\u91CD\u8981\u7684 <strong>\u4EE3\u7801\u70ED\u52A0\u8F7D</strong> \u7279\u6027\uFF0C\u4E0D\u9700\u8981\u91CD\u542F\u8FDB\u7A0B\uFF0C\u5C31\u80FD\u591F\u4ECE\u78C1\u76D8\u3001Redis \u6216\u8005\u4EFB\u4F55\u5176\u4ED6\u5730\u65B9\u52A0\u8F7D\u6570\u636E\uFF0C\u968F\u65F6\u66FF\u6362\u5185\u5B58\u91CC\u7684\u4EE3\u7801\u7247\u6BB5\u3002\u8FD9\u5C31\u5E26\u6765\u4E86 <strong>\u52A8\u6001\u914D\u7F6E</strong> \uFF0C\u8BA9 OpenResty \u80FD\u591F\u6C38\u4E0D\u505C\u673A\uFF0C\u5728\u5FAE\u79D2\u3001\u6BEB\u79D2\u7EA7\u522B\u5B9E\u73B0\u914D\u7F6E\u548C\u4E1A\u52A1\u903B\u8F91\u7684\u5B9E\u65F6\u66F4\u65B0\uFF0C\u6BD4\u8D77 Nginx \u79D2\u7EA7\u7684\u91CD\u542F\u662F\u4E00\u4E2A\u6781\u5927\u7684\u8FDB\u6B65\u3002</p><p>\u4F60\u53EF\u4EE5\u770B\u4E00\u4E0B\u5B9E\u9A8C\u73AF\u5883\u7684 <code>www/lua</code> \u76EE\u5F55\uFF0C\u91CC\u9762\u5B58\u653E\u4E86\u6211\u5199\u7684\u4E00\u4E9B\u6D4B\u8BD5 HTTP \u7279\u6027\u7684 Lua \u811A\u672C\uFF0C\u4EE3\u7801\u90FD\u975E\u5E38\u7B80\u5355\u6613\u61C2\uFF0C\u5C31\u50CF\u662F\u666E\u901A\u7684\u82F1\u8BED\u9605\u8BFB\u7406\u89E3\uFF0C\u8FD9\u4E5F\u662F Lua \u7684\u53E6\u4E00\u4E2A\u4F18\u52BF\uFF1A\u6613\u5B66\u4E60\u3001\u6613\u4E0A\u624B\u3002</p><h2 id="\u9AD8\u6548\u7387\u7684-lua" tabindex="-1">\u9AD8\u6548\u7387\u7684 Lua <a class="header-anchor" href="#\u9AD8\u6548\u7387\u7684-lua" aria-hidden="true">#</a></h2><p>OpenResty \u80FD\u591F\u9AD8\u6548\u8FD0\u884C\u7684\u4E00\u5927\u79D8\u6280\u662F\u5B83\u7684 <strong>\u540C\u6B65\u975E\u963B\u585E</strong> \u7F16\u7A0B\u8303\u5F0F\uFF0C\u5982\u679C\u4F60\u8981\u5F00\u53D1 OpenResty \u5E94\u7528\u5C31\u5FC5\u987B\u65F6\u523B\u94ED\u8BB0\u4E8E\u5FC3\u3002</p><p>\u540C\u6B65\u975E\u963B\u585E\u672C\u8D28\u4E0A\u8FD8\u662F\u4E00\u79CD <strong>\u591A\u8DEF\u590D\u7528</strong> \uFF0C\u6211\u62FF\u4E0A\u4E00\u8BB2\u7684 Nginx epoll \u6765\u5BF9\u6BD4\u89E3\u91CA\u4E00\u4E0B\u3002</p><p><strong>epoll \u662F\u64CD\u4F5C\u7CFB\u7EDF\u7EA7\u522B\u7684\u591A\u8DEF\u590D\u7528</strong> \uFF0C\u8FD0\u884C\u5728\u5185\u6838\u7A7A\u95F4\u3002\u800C OpenResty \u7684\u540C\u6B65\u975E\u963B\u585E\u5219\u662F\u57FA\u4E8E Lua \u5185\u5EFA\u7684 <strong>\u534F\u7A0B</strong> \uFF0C\u662F\u5E94\u7528\u7A0B\u5E8F\u7EA7\u522B\u7684\u591A\u8DEF\u590D\u7528\uFF0C\u8FD0\u884C\u5728\u7528\u6237\u7A7A\u95F4\uFF0C\u6240\u4EE5\u5B83\u7684\u8D44\u6E90\u6D88\u8017\u8981\u66F4\u5C11\u3002</p><p>OpenResty \u91CC\u6BCF\u4E00\u6BB5 Lua \u7A0B\u5E8F\u90FD\u7531\u534F\u7A0B\u6765\u8C03\u5EA6\u8FD0\u884C\u3002\u548C Linux \u7684 epoll \u4E00\u6837\uFF0C\u6BCF\u5F53\u53EF\u80FD\u53D1\u751F\u963B\u585E\u7684\u65F6\u5019\u534F\u7A0B\u5C31\u4F1A\u7ACB\u523B\u5207\u6362\u51FA\u53BB\uFF0C\u6267\u884C\u5176\u4ED6\u7684\u7A0B\u5E8F\u3002\u8FD9\u6837\u5355\u4E2A\u5904\u7406\u6D41\u7A0B\u662F\u963B\u585E\u7684\uFF0C\u4F46\u6574\u4E2A OpenResty \u5374\u662F\u975E\u963B\u585E\u7684\uFF0C\u591A\u4E2A\u7A0B\u5E8F\u90FD\u590D\u7528\u5728\u4E00\u4E2A Lua \u865A\u62DF\u673A\u91CC\u8FD0\u884C\u3002</p><p><img src="'+o+`" alt="img"></p><p>\u4E0B\u9762\u7684\u4EE3\u7801\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u4F8B\u5B50\uFF0C\u8BFB\u53D6 POST \u53D1\u9001\u7684 body \u6570\u636E\uFF0C\u7136\u540E\u518D\u53D1\u56DE\u5BA2\u6237\u7AEF\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#A6ACCD;">ngx.</span><span style="color:#FFCB6B;">req</span><span style="color:#A6ACCD;">.</span><span style="color:#82AAFF;">read_body</span><span style="color:#A6ACCD;">()                  </span><span style="color:#676E95;">-- \u540C\u6B65\u975E\u963B\u585E (1)</span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> data </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> ngx.</span><span style="color:#FFCB6B;">req</span><span style="color:#A6ACCD;">.</span><span style="color:#82AAFF;">get_body_data</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"><span style="color:#89DDFF;">if</span><span style="color:#A6ACCD;"> data </span><span style="color:#89DDFF;">then</span></span>
<span class="line"><span style="color:#A6ACCD;">    ngx.</span><span style="color:#82AAFF;">print</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">body: </span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">, data)        </span><span style="color:#676E95;">-- \u540C\u6B65\u975E\u963B\u585E (2)</span></span>
<span class="line"><span style="color:#89DDFF;">end</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>\u4EE3\u7801\u4E2D\u7684 <code>ngx.req.read_body</code> \u548C <code>ngx.print</code> \u5206\u522B\u662F\u6570\u636E\u7684\u6536\u53D1\u52A8\u4F5C\uFF0C\u53EA\u6709\u6536\u5230\u6570\u636E\u624D\u80FD\u53D1\u9001\u6570\u636E\uFF0C\u6240\u4EE5\u662F <strong>\u540C\u6B65</strong> \u7684\u3002</p><p>\u4F46\u5373\u4F7F\u56E0\u4E3A\u7F51\u7EDC\u539F\u56E0\u6CA1\u6536\u5230\u6216\u8005\u53D1\u4E0D\u51FA\u53BB\uFF0COpenResty \u4E5F\u4E0D\u4F1A\u5728\u8FD9\u91CC\u963B\u585E\u5E72\u7B49\u7740\uFF0C\u800C\u662F\u505A\u4E2A\u8BB0\u53F7\uFF0C\u628A\u7B49\u5F85\u7684\u8FD9\u6BB5 CPU \u65F6\u95F4\u7528\u6765\u5904\u7406\u5176\u4ED6\u7684\u8BF7\u6C42\uFF0C\u7B49\u7F51\u7EDC\u53EF\u8BFB\u6216\u8005\u53EF\u5199\u65F6\u518D\u56DE\u6765\u63A5\u7740\u8FD0\u884C\u3002</p><p>\u5047\u8BBE\u6536\u53D1\u6570\u636E\u7684\u7B49\u5F85\u65F6\u95F4\u662F 10 \u6BEB\u79D2\uFF0C\u800C\u771F\u6B63 CPU \u5904\u7406\u7684\u65F6\u95F4\u662F 0.1 \u6BEB\u79D2\uFF0C\u90A3\u4E48 OpenResty \u5C31\u53EF\u4EE5\u5728\u8FD9 10 \u6BEB\u79D2\u5185\u540C\u65F6\u5904\u7406 100 \u4E2A\u8BF7\u6C42\uFF0C\u800C\u4E0D\u662F\u628A\u8FD9 100 \u4E2A\u8BF7\u6C42\u963B\u585E\u6392\u961F\uFF0C\u7528 1000 \u6BEB\u79D2\u6765\u5904\u7406\u3002</p><p>\u9664\u4E86\u540C\u6B65\u975E\u963B\u585E\uFF0COpenResty \u8FD8\u9009\u7528\u4E86 <strong>LuaJIT</strong> \u4F5C\u4E3A Lua \u8BED\u8A00\u7684\u8FD0\u884C\u65F6\uFF08Runtime\uFF09\uFF0C\u8FDB\u4E00\u6B65\u6316\u6F5C\u589E\u6548\u3002</p><p>LuaJIT \u662F\u4E00\u4E2A\u9AD8\u6548\u7684 Lua \u865A\u62DF\u673A\uFF0C\u652F\u6301 JIT\uFF08Just In Time\uFF09\u6280\u672F\uFF0C\u53EF\u4EE5\u628A Lua \u4EE3\u7801\u5373\u65F6\u7F16\u8BD1\u6210\u672C\u5730\u673A\u5668\u7801\uFF0C\u8FD9\u6837\u5C31\u6D88\u9664\u4E86\u811A\u672C\u8BED\u8A00\u89E3\u91CA\u8FD0\u884C\u7684\u52A3\u52BF\uFF0C\u8BA9 Lua \u811A\u672C\u8DD1\u5F97\u548C\u539F\u751F C \u4EE3\u7801\u4E00\u6837\u5FEB\u3002</p><p>\u53E6\u5916\uFF0CLuaJIT \u8FD8\u4E3A Lua \u8BED\u8A00\u6DFB\u52A0\u4E86\u4E00\u4E9B\u7279\u522B\u7684\u589E\u5F3A\uFF0C\u6BD4\u5982\u4E8C\u8FDB\u5236\u4F4D\u8FD0\u7B97\u5E93 bit\uFF0C\u5185\u5B58\u4F18\u5316\u5E93 table\uFF0C\u8FD8\u6709 FFI\uFF08Foreign Function Interface\uFF09\uFF0C\u8BA9 Lua \u76F4\u63A5\u8C03\u7528\u5E95\u5C42 C \u51FD\u6570\uFF0C\u6BD4\u539F\u751F\u7684\u538B\u6808\u8C03\u7528\u5FEB\u5F88\u591A\u3002</p><h2 id="\u9636\u6BB5\u5F0F\u5904\u7406" tabindex="-1">\u9636\u6BB5\u5F0F\u5904\u7406 <a class="header-anchor" href="#\u9636\u6BB5\u5F0F\u5904\u7406" aria-hidden="true">#</a></h2><p>\u548C Nginx \u4E00\u6837\uFF0COpenResty \u4E5F\u4F7F\u7528\u6D41\u6C34\u7EBF\u6765\u5904\u7406 HTTP \u8BF7\u6C42\uFF0C\u5E95\u5C42\u7684\u8FD0\u884C\u57FA\u7840\u662F Nginx \u7684\u9636\u6BB5\u5F0F\u5904\u7406\uFF0C\u4F46\u5B83\u53C8\u6709\u81EA\u5DF1\u7684\u7279\u8272\u3002</p><p>Nginx \u7684\u6D41\u6C34\u7EBF\u662F\u7531\u4E00\u4E2A\u4E2A C \u6A21\u5757\u7EC4\u6210\u7684\uFF0C\u53EA\u80FD\u5728\u9759\u6001\u6587\u4EF6\u91CC\u914D\u7F6E\uFF0C\u5F00\u53D1\u56F0\u96BE\uFF0C\u914D\u7F6E\u9EBB\u70E6\uFF08\u76F8\u5BF9\u800C\u8A00\uFF09\u3002\u800C OpenResty \u7684\u6D41\u6C34\u7EBF\u5219\u662F\u7531\u4E00\u4E2A\u4E2A\u7684 Lua \u811A\u672C\u7EC4\u6210\u7684\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u4ECE\u78C1\u76D8\u4E0A\u52A0\u8F7D\uFF0C\u4E5F\u53EF\u4EE5\u4ECE Redis\u3001MySQL \u91CC\u52A0\u8F7D\uFF0C\u800C\u4E14\u7F16\u5199\u3001\u8C03\u8BD5\u7684\u8FC7\u7A0B\u975E\u5E38\u65B9\u4FBF\u5FEB\u6377\u3002</p><p>\u4E0B\u9762\u6211\u753B\u4E86\u4E00\u5F20\u56FE\uFF0C\u5217\u51FA\u4E86 OpenResty \u7684\u9636\u6BB5\uFF0C\u6BD4\u8D77 Nginx\uFF0COpenResty \u7684\u9636\u6BB5\u66F4\u6CE8\u91CD\u5BF9 HTTP \u8BF7\u6C42\u54CD\u5E94\u62A5\u6587\u7684\u52A0\u5DE5\u548C\u5904\u7406\u3002</p><p><img src="`+l+'" alt="img"></p><p>OpenResty \u91CC\u6709\u51E0\u4E2A\u9636\u6BB5\u4E0E Nginx \u662F\u76F8\u540C\u7684\uFF0C\u6BD4\u5982 rewrite\u3001access\u3001content\u3001filter\uFF0C\u8FD9\u4E9B\u90FD\u662F\u6807\u51C6\u7684 HTTP \u5904\u7406\u3002</p><p>\u5728\u8FD9\u51E0\u4E2A\u9636\u6BB5\u91CC\u53EF\u4EE5\u7528 <code>xxx_by_lua</code> \u6307\u4EE4\u5D4C\u5165 Lua \u4EE3\u7801\uFF0C\u6267\u884C\u91CD\u5B9A\u5411\u8DF3\u8F6C\u3001\u8BBF\u95EE\u63A7\u5236\u3001\u4EA7\u751F\u54CD\u5E94\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u8FC7\u6EE4\u62A5\u6587\u7B49\u529F\u80FD\u3002\u56E0\u4E3A Lua \u7684\u811A\u672C\u8BED\u8A00\u7279\u6027\uFF0C\u4E0D\u7528\u8003\u8651\u5185\u5B58\u5206\u914D\u3001\u8D44\u6E90\u56DE\u6536\u91CA\u653E\u7B49\u5E95\u5C42\u7684\u7EC6\u8282\u95EE\u9898\uFF0C\u53EF\u4EE5\u4E13\u6CE8\u4E8E\u7F16\u5199\u975E\u5E38\u590D\u6742\u7684\u4E1A\u52A1\u903B\u8F91\uFF0C\u6BD4 C \u6A21\u5757\u7684\u5F00\u53D1\u6548\u7387\u9AD8\u5F88\u591A\uFF0C\u5373\u6613\u4E8E\u6269\u5C55\u53C8\u6613\u4E8E\u7EF4\u62A4\u3002</p><p>OpenResty \u91CC\u8FD8\u6709\u4E24\u4E2A\u4E0D\u540C\u4E8E Nginx \u7684\u7279\u6B8A\u9636\u6BB5\u3002</p><p>\u4E00\u4E2A\u662F <strong>init \u9636\u6BB5</strong> \uFF0C\u5B83\u53C8\u5206\u6210 <code>master init</code> \u548C <code>worker init</code> \uFF0C\u5728 master \u8FDB\u7A0B\u548C worker \u8FDB\u7A0B\u542F\u52A8\u7684\u65F6\u5019\u8FD0\u884C\u3002\u8FD9\u4E2A\u9636\u6BB5\u8FD8\u6CA1\u6709\u5F00\u59CB\u63D0\u4F9B\u670D\u52A1\uFF0C\u6240\u4EE5\u6162\u4E00\u70B9\u4E5F\u6CA1\u5173\u7CFB\uFF0C\u53EF\u4EE5\u8C03\u7528\u4E00\u4E9B\u963B\u585E\u7684\u63A5\u53E3\u521D\u59CB\u5316\u670D\u52A1\u5668\uFF0C\u6BD4\u5982\u8BFB\u53D6\u78C1\u76D8\u3001MySQL\uFF0C\u52A0\u8F7D\u9ED1\u767D\u540D\u5355\u6216\u8005\u6570\u636E\u6A21\u578B\uFF0C\u7136\u540E\u653E\u8FDB\u5171\u4EAB\u5185\u5B58\u91CC\u4F9B\u8FD0\u884C\u65F6\u4F7F\u7528\u3002</p><p>\u53E6\u4E00\u4E2A\u662F <strong>ssl \u9636\u6BB5</strong>\uFF0C\u8FD9\u7B97\u5F97\u4E0A\u662F OpenResty \u7684\u4E00\u5927\u521B\u4E3E\uFF0C\u53EF\u4EE5\u5728 TLS \u63E1\u624B\u65F6\u52A8\u6001\u52A0\u8F7D\u8BC1\u4E66\uFF0C\u6216\u8005\u53D1\u9001 <code>OCSP Stapling</code> \u3002</p><p>\u8FD8\u8BB0\u5F97 <a href="./../05/07.html">\u6211\u5E94\u8BE5\u8FC1\u79FB\u5230 HTTPS \u5417?</a> \u91CC\u8BF4\u7684 <code>SNI \u6269\u5C55</code> \u5417\uFF1FNginx \u53EF\u4EE5\u4F9D\u636E\u300C\u670D\u52A1\u5668\u540D\u79F0\u6307\u793A\u6765\u9009\u62E9\u8BC1\u4E66\u5B9E\u73B0 HTTPS \u865A\u62DF\u4E3B\u673A\u300D\uFF0C\u4F46\u9759\u6001\u914D\u7F6E\u5F88\u4E0D\u7075\u6D3B\uFF0C\u8981\u7F16\u5199\u5F88\u591A\u96F7\u540C\u7684\u914D\u7F6E\u5757\u3002\u867D\u7136\u540E\u6765 Nginx \u589E\u52A0\u4E86\u53D8\u91CF\u652F\u6301\uFF0C\u4F46\u5B83\u6BCF\u6B21\u63E1\u624B\u90FD\u8981\u8BFB\u78C1\u76D8\uFF0C\u6548\u7387\u5F88\u4F4E\u3002</p><p>\u800C\u5728 OpenResty \u91CC\u5C31\u53EF\u4EE5\u4F7F\u7528\u6307\u4EE4 <code>ssl_certificate_by_lua</code> \uFF0C\u7F16\u5199 Lua \u811A\u672C\uFF0C\u8BFB\u53D6 SNI \u540D\u5B57\u540E\uFF0C\u76F4\u63A5\u4ECE\u5171\u4EAB\u5185\u5B58\u6216\u8005 Redis \u91CC\u83B7\u53D6\u8BC1\u4E66\u3002\u4E0D\u4EC5\u6CA1\u6709\u8BFB\u76D8\u963B\u585E\uFF0C\u800C\u4E14\u8BC1\u4E66\u4E5F\u662F\u5B8C\u5168\u52A8\u6001\u53EF\u914D\u7F6E\u7684\uFF0C\u65E0\u9700\u4FEE\u6539\u914D\u7F6E\u6587\u4EF6\u5C31\u80FD\u591F\u8F7B\u677E\u652F\u6301\u5927\u91CF\u7684 HTTPS \u865A\u62DF\u4E3B\u673A\u3002</p><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><ol><li>Nginx \u4F9D\u8D56\u4E8E\u78C1\u76D8\u4E0A\u7684\u9759\u6001\u914D\u7F6E\u6587\u4EF6\uFF0C\u4FEE\u6539\u540E\u5FC5\u987B\u91CD\u542F\u624D\u80FD\u751F\u6548\uFF0C\u7F3A\u4E4F\u7075\u6D3B\u6027\uFF1B</li><li>OpenResty \u57FA\u4E8E Nginx\uFF0C\u6253\u5305\u4E86\u5F88\u591A\u6709\u7528\u7684\u6A21\u5757\u548C\u5E93\uFF0C\u662F\u4E00\u4E2A\u9AD8\u6027\u80FD\u7684 Web \u5F00\u53D1\u5E73\u53F0\uFF1B</li><li>OpenResty \u7684\u5DE5\u4F5C\u8BED\u8A00\u662F Lua\uFF0C\u5B83\u5C0F\u5DE7\u7075\u6D3B\uFF0C\u6267\u884C\u6548\u7387\u9AD8\uFF0C\u652F\u6301\u201C\u4EE3\u7801\u70ED\u52A0\u8F7D\u201D\uFF1B</li><li>OpenResty \u7684\u6838\u5FC3\u7F16\u7A0B\u8303\u5F0F\u662F <strong>\u540C\u6B65\u975E\u963B\u585E</strong> \uFF0C\u4F7F\u7528\u534F\u7A0B\uFF0C\u4E0D\u9700\u8981\u5F02\u6B65\u56DE\u8C03\u51FD\u6570\uFF1B</li><li>OpenResty \u4E5F\u4F7F\u7528\u9636\u6BB5\u5F0F\u5904\u7406\u7684\u5DE5\u4F5C\u6A21\u5F0F\uFF0C\u4F46\u56E0\u4E3A\u5728\u9636\u6BB5\u91CC\u6267\u884C\u7684\u90FD\u662F Lua \u4EE3\u7801\uFF0C\u6240\u4EE5\u975E\u5E38\u7075\u6D3B\uFF0C\u914D\u5408 Redis \u7B49\u5916\u90E8\u6570\u636E\u5E93\u80FD\u591F\u5B9E\u73B0\u5404\u79CD\u52A8\u6001\u914D\u7F6E\u3002</li></ol><h2 id="\u8BFE\u4E0B\u4F5C\u4E1A" tabindex="-1">\u8BFE\u4E0B\u4F5C\u4E1A <a class="header-anchor" href="#\u8BFE\u4E0B\u4F5C\u4E1A" aria-hidden="true">#</a></h2><ol><li>\u8C08\u4E00\u4E0B\u8FD9\u4E9B\u5929\u4F60\u5BF9\u5B9E\u9A8C\u73AF\u5883\u91CC OpenResty \u7684\u611F\u60F3\u548C\u8BA4\u8BC6\u3002</li><li>\u4F60\u89C9\u5F97 Nginx \u548C OpenResty \u7684\u201C\u9636\u6BB5\u5F0F\u5904\u7406\u201D\u6709\u4EC0\u4E48\u597D\u5904\uFF1F\u5BF9\u4F60\u7684\u5B9E\u9645\u5DE5\u4F5C\u6709\u6CA1\u6709\u542F\u53D1\uFF1F</li></ol>',56),c=[i];function d(g,u,y,h,b,x){return n(),s("div",null,c)}const O=e(r,[["render",d]]);export{_ as __pageData,O as default};
