import{_ as s,o as n,c as a,a as e}from"./app.a9f825fd.js";const p="/vitepress-demo/assets/e8369d077454e5b92e3722e7090551a3.8b1e86a7.png",l="/vitepress-demo/assets/add929f8439c64f29db720d30f7de548.07ab57b1.png",b=JSON.parse('{"title":"03 | WAF\uFF1A\u4FDD\u62A4\u6211\u4EEC\u7684\u7F51\u7EDC\u670D\u52A1","description":"","frontmatter":{},"headers":[{"level":2,"title":"Web \u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1","slug":"web-\u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1","link":"#web-\u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1","children":[]},{"level":2,"title":"\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899","slug":"\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899","link":"#\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899","children":[]},{"level":2,"title":"\u5168\u9762\u7684 WAF \u89E3\u51B3\u65B9\u6848","slug":"\u5168\u9762\u7684-waf-\u89E3\u51B3\u65B9\u6848","link":"#\u5168\u9762\u7684-waf-\u89E3\u51B3\u65B9\u6848","children":[]},{"level":2,"title":"\u5C0F\u7ED3","slug":"\u5C0F\u7ED3","link":"#\u5C0F\u7ED3","children":[]},{"level":2,"title":"\u8BFE\u4E0B\u4F5C\u4E1A","slug":"\u8BFE\u4E0B\u4F5C\u4E1A","link":"#\u8BFE\u4E0B\u4F5C\u4E1A","children":[]}],"relativePath":"http-protocol/07/03.md","lastUpdated":1676385096000}'),o={name:"http-protocol/07/03.md"},r=e('<h1 id="_03-waf\uFF1A\u4FDD\u62A4\u6211\u4EEC\u7684\u7F51\u7EDC\u670D\u52A1" tabindex="-1">03 | WAF\uFF1A\u4FDD\u62A4\u6211\u4EEC\u7684\u7F51\u7EDC\u670D\u52A1 <a class="header-anchor" href="#_03-waf\uFF1A\u4FDD\u62A4\u6211\u4EEC\u7684\u7F51\u7EDC\u670D\u52A1" aria-hidden="true">#</a></h1><p>\u5728\u524D\u4E9B\u5929\u7684\u5B89\u5168\u7BC7\u91CC\uFF0C\u6211\u8C08\u5230\u4E86 HTTPS\uFF0C\u5B83\u4F7F\u7528\u4E86 SSL/TLS \u534F\u8BAE\uFF0C\u52A0\u5BC6\u6574\u4E2A\u901A\u4FE1\u8FC7\u7A0B\uFF0C\u80FD\u591F\u9632\u6B62\u6076\u610F\u7A83\u542C\u548C\u7A9C\u6539\uFF0C\u4FDD\u62A4\u6211\u4EEC\u7684\u6570\u636E\u5B89\u5168\u3002</p><p>\u4F46 HTTPS \u53EA\u662F\u7F51\u7EDC\u5B89\u5168\u4E2D\u5F88\u5C0F\u7684\u4E00\u90E8\u5206\uFF0C<strong>\u4EC5\u4EC5\u4FDD\u8BC1\u4E86\u300C\u901A\u4FE1\u94FE\u8DEF\u5B89\u5168\u300D</strong> \uFF0C\u8BA9\u7B2C\u4E09\u65B9\u65E0\u6CD5\u5F97\u77E5\u4F20\u8F93\u7684\u5185\u5BB9\u3002\u5728\u901A\u4FE1\u94FE\u8DEF\u7684\u4E24\u7AEF\uFF0C<strong>\u4E5F\u5C31\u662F\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\uFF0C\u5B83\u662F\u65E0\u6CD5\u63D0\u4F9B\u4FDD\u62A4\u7684</strong> \u3002</p><p>\u56E0\u4E3A HTTP \u662F\u4E00\u4E2A\u5F00\u653E\u7684\u534F\u8BAE\uFF0CWeb \u670D\u52A1\u90FD\u8FD0\u884C\u5728\u516C\u7F51\u4E0A\uFF0C\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u8BBF\u95EE\uFF0C\u6240\u4EE5\u5929\u7136\u5C31\u4F1A\u6210\u4E3A\u9ED1\u5BA2\u7684\u653B\u51FB\u76EE\u6807\u3002</p><p>\u800C\u4E14\u9ED1\u5BA2\u7684\u672C\u9886\u6BD4\u6211\u4EEC\u60F3\u8C61\u7684\u8FD8\u8981\u5927\u5F97\u591A\u3002\u867D\u7136\u4E0D\u80FD\u5728\u4F20\u8F93\u8FC7\u7A0B\u4E2D\u505A\u624B\u811A\uFF0C\u4F46\u4ED6\u4EEC\u8FD8\u53EF\u4EE5\u201C\u5047\u626E\u201D\u6210\u5408\u6CD5\u7684\u7528\u6237\u8BBF\u95EE\u7CFB\u7EDF\uFF0C\u7136\u540E\u4F3A\u673A\u641E\u7834\u574F\u3002</p><h2 id="web-\u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1" tabindex="-1">Web \u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1 <a class="header-anchor" href="#web-\u670D\u52A1\u9047\u5230\u7684\u5A01\u80C1" aria-hidden="true">#</a></h2><p>\u9ED1\u5BA2\u90FD\u6709\u54EA\u4E9B\u624B\u6BB5\u6765\u653B\u51FB Web \u670D\u52A1\u5462\uFF1F\u6211\u7ED9\u4F60\u5927\u6982\u5217\u51FA\u51E0\u79CD\u5E38\u89C1\u7684\u65B9\u5F0F\u3002</p><p>\u7B2C\u4E00\u79CD\u53EB <strong>DDoS</strong> \u653B\u51FB\uFF08distributed denial-of-service attack\uFF09\uFF0C\u6709\u65F6\u5019\u4E5F\u53EB\u300C\u6D2A\u6C34\u653B\u51FB\u300D\u3002</p><p>\u9ED1\u5BA2\u4F1A\u63A7\u5236\u8BB8\u591A\u50F5\u5C38\u8BA1\u7B97\u673A\uFF0C\u5411\u76EE\u6807\u670D\u52A1\u5668\u53D1\u8D77\u5927\u91CF\u65E0\u6548\u8BF7\u6C42\u3002\u56E0\u4E3A\u670D\u52A1\u5668\u65E0\u6CD5\u533A\u5206\u6B63\u5E38\u7528\u6237\u548C\u9ED1\u5BA2\uFF0C\u53EA\u80FD\u7167\u5355\u5168\u6536\uFF0C\u8FD9\u6837\u5C31\u6324\u5360\u4E86\u6B63\u5E38\u7528\u6237\u6240\u5E94\u6709\u7684\u8D44\u6E90\u3002\u5982\u679C\u9ED1\u5BA2\u7684\u653B\u51FB\u5F3A\u5EA6\u5F88\u5927\uFF0C\u5C31\u4F1A\u50CF\u6D2A\u6C34\u4E00\u6837\u5BF9\u7F51\u7AD9\u7684\u670D\u52A1\u80FD\u529B\u9020\u6210\u51B2\u51FB\uFF0C\u8017\u5C3D\u5E26\u5BBD\u3001CPU \u548C\u5185\u5B58\uFF0C\u5BFC\u81F4\u7F51\u7AD9\u5B8C\u5168\u65E0\u6CD5\u63D0\u4F9B\u6B63\u5E38\u670D\u52A1\u3002</p><p><code>DDoS</code> \u653B\u51FB\u65B9\u5F0F\u6BD4\u8F83\u7B80\u5355\u7C97\u66B4\uFF0C\u867D\u7136\u5F88\u6709\u6548\uFF0C\u4F46\u4E0D\u6D89\u53CA HTTP \u534F\u8BAE\u5185\u90E8\u7684\u7EC6\u8282\uFF0C\u6280\u672F\u542B\u91CF\u6BD4\u8F83\u4F4E\uFF0C\u4E0D\u8FC7\u4E0B\u9762\u8981\u8BF4\u7684\u51E0\u79CD\u624B\u6BB5\u5C31\u4E0D\u4E00\u6837\u4E86\u3002</p><p>\u7F51\u7AD9\u540E\u53F0\u7684 Web \u670D\u52A1\u7ECF\u5E38\u4F1A\u63D0\u53D6\u51FA HTTP \u62A5\u6587\u91CC\u7684\u5404\u79CD\u4FE1\u606F\uFF0C\u5E94\u7528\u4E8E\u4E1A\u52A1\uFF0C\u6709\u65F6\u4F1A\u7F3A\u4E4F\u4E25\u683C\u7684\u68C0\u67E5\u3002\u56E0\u4E3A HTTP \u62A5\u6587\u5728\u8BED\u4E49\u7ED3\u6784\u4E0A\u975E\u5E38\u677E\u6563\u3001\u7075\u6D3B\uFF0CURI \u91CC\u7684 query \u5B57\u7B26\u4E32\u3001\u5934\u5B57\u6BB5\u3001body \u6570\u636E\u90FD\u53EF\u4EE5\u4EFB\u610F\u8BBE\u7F6E\uFF0C\u8FD9\u5C31\u5E26\u6765\u4E86\u5B89\u5168\u9690\u60A3\uFF0C\u7ED9\u4E86\u9ED1\u5BA2 <strong>\u4EE3\u7801\u6CE8\u5165</strong> \u7684\u53EF\u80FD\u6027\u3002</p><p>\u9ED1\u5BA2\u53EF\u4EE5\u7CBE\u5FC3\u7F16\u5236 HTTP \u8BF7\u6C42\u62A5\u6587\uFF0C\u53D1\u9001\u7ED9\u670D\u52A1\u5668\uFF0C\u670D\u52A1\u7A0B\u5E8F\u5982\u679C\u6CA1\u6709\u505A\u9632\u5907\uFF0C\u5C31\u4F1A\u4E0A\u5F53\u53D7\u9A97\uFF0C\u6267\u884C\u9ED1\u5BA2\u8BBE\u5B9A\u7684\u4EE3\u7801\u3002</p><p><strong>SQL \u6CE8\u5165</strong>\uFF08SQL injection\uFF09\u5E94\u8BE5\u7B97\u662F\u6700\u8457\u540D\u7684\u4E00\u79CD <strong>\u4EE3\u7801\u6CE8\u5165</strong> \u653B\u51FB\u4E86\uFF0C\u5B83\u5229\u7528\u4E86\u670D\u52A1\u5668\u5B57\u7B26\u4E32\u62FC\u63A5\u5F62\u6210 SQL \u8BED\u53E5\u7684\u6F0F\u6D1E\uFF0C\u6784\u9020\u51FA\u975E\u6B63\u5E38\u7684 SQL \u8BED\u53E5\uFF0C\u83B7\u53D6\u6570\u636E\u5E93\u5185\u90E8\u7684\u654F\u611F\u4FE1\u606F\u3002</p><p>\u53E6\u4E00\u79CD <strong>HTTP \u5934\u6CE8\u5165</strong> \u653B\u51FB\u7684\u65B9\u5F0F\u4E5F\u662F\u7C7B\u4F3C\u7684\u539F\u7406\uFF0C\u5B83\u5728 <code>Host</code>\u3001<code>User-Agent</code>\u3001<code>X-Forwarded-For</code> \u7B49\u5B57\u6BB5\u91CC\u52A0\u5165\u4E86\u6076\u610F\u6570\u636E\u6216\u4EE3\u7801\uFF0C\u670D\u52A1\u7AEF\u7A0B\u5E8F\u5982\u679C\u89E3\u6790\u4E0D\u5F53\uFF0C\u5C31\u4F1A\u6267\u884C\u9884\u8BBE\u7684\u6076\u610F\u4EE3\u7801\u3002</p><p>\u5728\u4E4B\u524D\u7684 <a href="./../04/05.html">\u8BA9\u6211\u77E5\u9053\u4F60\u662F\u8C01\uFF1AHTTP \u7684 Cookie \u673A\u5236</a> \u91CC\uFF0C\u4E5F\u8BF4\u8FC7\u4E00\u79CD\u5229\u7528 Cookie \u7684\u653B\u51FB\u624B\u6BB5\uFF0C<strong>\u8DE8\u7AD9\u811A\u672C</strong>\uFF08XSS\uFF09\u653B\u51FB\uFF0C\u5B83\u5C5E\u4E8E <strong>JS \u4EE3\u7801\u6CE8\u5165</strong> \uFF0C\u5229\u7528 JavaScript \u811A\u672C\u83B7\u53D6\u672A\u8BBE\u9632\u7684 Cookie\u3002</p><h2 id="\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899" tabindex="-1">\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899 <a class="header-anchor" href="#\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899" aria-hidden="true">#</a></h2><p>\u9762\u5BF9\u8FD9\u4E48\u591A\u7684\u9ED1\u5BA2\u653B\u51FB\u624B\u6BB5\uFF0C\u6211\u4EEC\u5E94\u8BE5\u600E\u4E48\u9632\u5FA1\u5462\uFF1F</p><p>\u8FD9\u5C31\u8981\u7528\u5230 <strong>\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899</strong>\uFF08Web Application Firewall\uFF09\u4E86\uFF0C\u7B80\u79F0\u4E3A <strong>WAF</strong> \u3002</p><p>\u4F60\u53EF\u80FD\u5BF9\u4F20\u7EDF\u7684\u9632\u706B\u5899\u6BD4\u8F83\u719F\u6089\u3002\u4F20\u7EDF\u9632\u706B\u5899\u5DE5\u4F5C\u5728\u4E09\u5C42\u6216\u8005\u56DB\u5C42\uFF0C\u9694\u79BB\u4E86\u5916\u7F51\u548C\u5185\u7F51\uFF0C\u4F7F\u7528\u9884\u8BBE\u7684\u89C4\u5219\uFF0C\u53EA\u5141\u8BB8\u67D0\u4E9B\u7279\u5B9A IP \u5730\u5740\u548C\u7AEF\u53E3\u53F7\u7684\u6570\u636E\u5305\u901A\u8FC7\uFF0C\u62D2\u7EDD\u4E0D\u7B26\u5408\u6761\u4EF6\u7684\u6570\u636E\u6D41\u5165\u6216\u6D41\u51FA\u5185\u7F51\uFF0C\u5B9E\u8D28\u4E0A\u662F <strong>\u4E00\u79CD\u7F51\u7EDC\u6570\u636E\u8FC7\u6EE4\u8BBE\u5907</strong> \u3002</p><p>WAF \u4E5F\u662F\u4E00\u79CD\u9632\u706B\u5899\uFF0C\u4F46\u5B83\u5DE5\u4F5C\u5728\u4E03\u5C42\uFF0C\u770B\u5230\u7684\u4E0D\u4EC5\u662F IP \u5730\u5740\u548C\u7AEF\u53E3\u53F7\uFF0C\u8FD8\u80FD\u770B\u5230\u6574\u4E2A HTTP \u62A5\u6587\uFF0C\u6240\u4EE5\u5C31\u80FD\u591F\u5BF9\u62A5\u6587\u5185\u5BB9\u505A\u66F4\u6DF1\u5165\u7EC6\u81F4\u7684\u5BA1\u6838\uFF0C\u4F7F\u7528\u66F4\u590D\u6742\u7684\u6761\u4EF6\u3001\u89C4\u5219\u6765\u8FC7\u6EE4\u6570\u636E\u3002</p><p>\u8BF4\u767D\u4E86\uFF0CWAF \u5C31\u662F\u4E00\u79CD <strong>HTTP \u5165\u4FB5\u68C0\u6D4B\u548C\u9632\u5FA1\u7CFB\u7EDF</strong> \u3002</p><p><img src="'+p+`" alt="img"></p><p>WAF \u90FD\u80FD\u5E72\u4EC0\u4E48\u5462\uFF1F</p><p>\u901A\u5E38\u4E00\u6B3E\u4EA7\u54C1\u80FD\u591F\u79F0\u4E3A WAF\uFF0C\u8981\u5177\u5907\u4E0B\u9762\u7684\u4E00\u4E9B\u529F\u80FD\uFF1A</p><ul><li>IP \u9ED1\u540D\u5355\u548C\u767D\u540D\u5355\uFF0C\u62D2\u7EDD\u9ED1\u540D\u5355\u4E0A\u5730\u5740\u7684\u8BBF\u95EE\uFF0C\u6216\u8005\u53EA\u5141\u8BB8\u767D\u540D\u5355\u4E0A\u7684\u7528\u6237\u8BBF\u95EE\uFF1B</li><li>URI \u9ED1\u540D\u5355\u548C\u767D\u540D\u5355\uFF0C\u4E0E IP \u9ED1\u767D\u540D\u5355\u7C7B\u4F3C\uFF0C\u5141\u8BB8\u6216\u7981\u6B62\u5BF9\u67D0\u4E9B URI \u7684\u8BBF\u95EE\uFF1B</li><li>\u9632\u62A4 DDoS \u653B\u51FB\uFF0C\u5BF9\u7279\u5B9A\u7684 IP \u5730\u5740\u9650\u8FDE\u9650\u901F\uFF1B</li><li>\u8FC7\u6EE4\u8BF7\u6C42\u62A5\u6587\uFF0C\u9632\u5FA1\u201C\u4EE3\u7801\u6CE8\u5165\u201D\u653B\u51FB\uFF1B</li><li>\u8FC7\u6EE4\u54CD\u5E94\u62A5\u6587\uFF0C\u9632\u5FA1\u654F\u611F\u4FE1\u606F\u5916\u6CC4\uFF1B</li><li>\u5BA1\u8BA1\u65E5\u5FD7\uFF0C\u8BB0\u5F55\u6240\u6709\u68C0\u6D4B\u5230\u7684\u5165\u4FB5\u64CD\u4F5C\u3002</li></ul><p>\u542C\u8D77\u6765 WAF \u597D\u50CF\u5F88\u9AD8\u6DF1\uFF0C\u4F46\u5982\u679C\u4F60\u7406\u89E3\u4E86\u5B83\u7684\u5DE5\u4F5C\u539F\u7406\uFF0C\u5176\u5B9E\u4E5F\u4E0D\u96BE\u3002</p><p>\u5B83\u5C31\u50CF\u662F\u5E73\u65F6\u7F16\u5199\u7A0B\u5E8F\u65F6\u5FC5\u987B\u8981\u505A\u7684\u51FD\u6570\u5165\u53E3\u53C2\u6570\u68C0\u67E5\uFF0C\u62FF\u5230 HTTP \u8BF7\u6C42\u3001\u54CD\u5E94\u62A5\u6587\uFF0C\u7528\u5B57\u7B26\u4E32\u5904\u7406\u51FD\u6570\u770B\u770B\u6709\u6CA1\u6709\u5173\u952E\u5B57\u3001\u654F\u611F\u8BCD\uFF0C\u6216\u8005\u7528\u6B63\u5219\u8868\u8FBE\u5F0F\u505A\u4E00\u4E0B\u6A21\u5F0F\u5339\u914D\uFF0C\u547D\u4E2D\u4E86\u89C4\u5219\u5C31\u6267\u884C\u5BF9\u5E94\u7684\u52A8\u4F5C\uFF0C\u6BD4\u5982\u8FD4\u56DE 403/404\u3002</p><p>\u5982\u679C\u4F60\u6BD4\u8F83\u719F\u6089 Apache\u3001Nginx\u3001OpenResty\uFF0C\u53EF\u4EE5\u81EA\u5DF1\u6539\u6539\u914D\u7F6E\u6587\u4EF6\uFF0C\u5199\u70B9 JS \u6216\u8005 Lua \u4EE3\u7801\uFF0C\u5C31\u80FD\u591F\u5B9E\u73B0\u57FA\u672C\u7684 WAF \u529F\u80FD\u3002</p><p>\u6BD4\u5982\u8BF4\uFF0C\u5728 Nginx \u91CC\u5B9E\u73B0 IP \u5730\u5740\u9ED1\u540D\u5355\uFF0C\u53EF\u4EE5\u5229\u7528 <code>map</code> \u6307\u4EE4\uFF0C\u4ECE\u53D8\u91CF <code>$remote_addr</code> \u83B7\u53D6 IP \u5730\u5740\uFF0C\u5728\u9ED1\u540D\u5355\u4E0A\u5C31\u6620\u5C04\u4E3A\u503C 1\uFF0C\u7136\u540E\u5728 <code>if</code> \u6307\u4EE4\u91CC\u5224\u65AD\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#A6ACCD;">map $remote_addr $</span><span style="color:#82AAFF;">blocked</span><span style="color:#A6ACCD;"> {</span></span>
<span class="line"><span style="color:#A6ACCD;">    default       </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1.2.3.4</span><span style="color:#89DDFF;">&quot;     </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">5.6.7.8</span><span style="color:#89DDFF;">&quot;     </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#89DDFF;">if</span><span style="color:#A6ACCD;"> ($blocked) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">return</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">403</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">you are blocked.</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">;  </span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><p>Nginx \u7684\u914D\u7F6E\u6587\u4EF6\u53EA\u80FD\u9759\u6001\u52A0\u8F7D\uFF0C\u6539\u540D\u5355\u5FC5\u987B\u91CD\u542F\uFF0C\u6BD4\u8F83\u9EBB\u70E6\u3002\u5982\u679C\u6362\u6210 OpenResty \u5C31\u4F1A\u975E\u5E38\u65B9\u4FBF\uFF0C\u5728 access \u9636\u6BB5\u8FDB\u884C\u5224\u65AD\uFF0CIP \u5730\u5740\u5217\u8868\u53EF\u4EE5\u4F7F\u7528 cosocket \u8FDE\u63A5\u5916\u90E8\u7684 Redis\u3001MySQL \u7B49\u6570\u636E\u5E93\uFF0C\u5B9E\u73B0\u52A8\u6001\u66F4\u65B0\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> ip_addr </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> ngx.</span><span style="color:#FFCB6B;">var</span><span style="color:#A6ACCD;">.</span><span style="color:#FFCB6B;">remote_addr</span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> rds </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">redis</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">new</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"><span style="color:#89DDFF;">if</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">rds</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">get</span><span style="color:#A6ACCD;">(ip_addr) </span><span style="color:#89DDFF;">==</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">then</span><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#A6ACCD;">    ngx.</span><span style="color:#82AAFF;">exit</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">403</span><span style="color:#A6ACCD;">) </span></span>
<span class="line"><span style="color:#89DDFF;">end</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>\u770B\u4E86\u4E0A\u9762\u7684\u4E24\u4E2A\u4F8B\u5B50\uFF0C\u4F60\u662F\u4E0D\u662F\u6709\u79CD\u8DC3\u8DC3\u6B32\u8BD5\u7684\u51B2\u52A8\u4E86\uFF0C\u60F3\u81EA\u5DF1\u52A8\u624B\u5F00\u53D1\u4E00\u4E2A WAF\uFF1F</p><p>\u4E0D\u8FC7\u6211\u5FC5\u987B\u8981\u63D0\u9192\u4F60\uFF0C\u5728\u7F51\u7EDC\u5B89\u5168\u9886\u57DF\u5FC5\u987B\u65F6\u523B\u8BB0\u5F97 <strong>\u6728\u6876\u6548\u5E94</strong> \uFF08\u4E5F\u53EB\u77ED\u677F\u6548\u5E94\uFF09\u3002\u7F51\u7AD9\u7684\u6574\u4F53\u5B89\u5168\u4E0D\u5728\u4E8E\u4F60\u52A0\u56FA\u7684\u6700\u5F3A\u7684\u90A3\u4E2A\u65B9\u5411\uFF0C\u800C\u662F\u5728\u4E8E\u4F60\u53EF\u80FD\u90FD\u6CA1\u6709\u610F\u8BC6\u5230\u7684\u77ED\u677F\u3002\u9ED1\u5BA2\u5F80\u5F80\u4F1A\u907F\u91CD\u5C31\u8F7B\uFF0C\u53EA\u8981\u53D1\u73B0\u4E86\u7F51\u7AD9\u7684\u4E00\u4E2A\u5F31\u70B9\uFF0C\u5C31\u53EF\u4EE5\u4E00\u70B9\u7A81\u7834\uFF0C\u5176\u4ED6\u65B9\u9762\u7684\u5B89\u5168\u63AA\u65BD\u4E5F\u5C31\u90FD\u6210\u4E86\u65E0\u7528\u529F\u3002</p><p>\u6240\u4EE5\uFF0C\u4F7F\u7528 WAF \u6700\u597D <strong>\u4E0D\u8981\u91CD\u65B0\u53D1\u660E\u8F6E\u5B50</strong> \uFF0C\u800C\u662F\u4F7F\u7528\u73B0\u6709\u7684\u3001\u6BD4\u8F83\u6210\u719F\u7684\u3001\u7ECF\u8FC7\u5B9E\u9645\u8003\u9A8C\u7684 WAF \u4EA7\u54C1\u3002</p><h2 id="\u5168\u9762\u7684-waf-\u89E3\u51B3\u65B9\u6848" tabindex="-1">\u5168\u9762\u7684 WAF \u89E3\u51B3\u65B9\u6848 <a class="header-anchor" href="#\u5168\u9762\u7684-waf-\u89E3\u51B3\u65B9\u6848" aria-hidden="true">#</a></h2><p>\u8FD9\u91CC\u6211\u5C31\u8981\u9686\u91CD\u4ECB\u7ECD\u4E00\u4E0B WAF \u9886\u57DF\u91CC\u7684\u6700\u9876\u7EA7\u4EA7\u54C1\u4E86\uFF1AModSecurity\uFF0C\u5B83\u53EF\u4EE5\u8BF4\u662F WAF \u754C <strong>\u4E8B\u5B9E\u4E0A\u7684\u6807\u51C6</strong> \u3002</p><p>ModSecurity \u662F\u4E00\u4E2A\u5F00\u6E90\u7684\u3001\u751F\u4EA7\u7EA7\u7684 WAF \u5DE5\u5177\u5305\uFF0C\u5386\u53F2\u5F88\u60A0\u4E45\uFF0C\u6BD4 Nginx \u8FD8\u8981\u5927\u51E0\u5C81\u3002\u5B83\u5F00\u59CB\u4E8E\u4E00\u4E2A\u79C1\u4EBA\u9879\u76EE\uFF0C\u540E\u6765\u88AB\u5546\u4E1A\u516C\u53F8 Breach Security \u6536\u8D2D\uFF0C\u73B0\u5728\u5219\u662F\u7531 TrustWave \u516C\u53F8\u7684 SpiderLabs \u56E2\u961F\u8D1F\u8D23\u7EF4\u62A4\u3002</p><p>ModSecurity \u6700\u65E9\u662F Apache \u7684\u4E00\u4E2A\u6A21\u5757\uFF0C\u53EA\u80FD\u8FD0\u884C\u5728 Apache \u4E0A\u3002\u56E0\u4E3A\u5176\u54C1\u8D28\u51FA\u4F17\uFF0C\u5927\u53D7\u6B22\u8FCE\uFF0C\u540E\u6765\u7684 2.x \u7248\u6DFB\u52A0\u4E86 Nginx \u548C IIS \u652F\u6301\uFF0C\u4F46\u56E0\u4E3A\u5E95\u5C42\u67B6\u6784\u5B58\u5728\u5DEE\u5F02\uFF0C\u4E0D\u591F\u7A33\u5B9A\u3002</p><p>\u6240\u4EE5\uFF0C\u8FD9\u4E24\u5E74 SpiderLabs \u56E2\u961F\u5C31\u5F00\u53D1\u4E86\u5168\u65B0\u7684 3.0 \u7248\u672C\uFF0C\u79FB\u9664\u4E86\u5BF9 Apache \u67B6\u6784\u7684\u4F9D\u8D56\uFF0C\u4F7F\u7528\u65B0\u7684\u201C\u8FDE\u63A5\u5668\u201D\u6765\u96C6\u6210\u8FDB Apache \u6216\u8005 Nginx\uFF0C\u6BD4 2.x \u7248\u66F4\u52A0\u7A33\u5B9A\u548C\u5FEB\u901F\uFF0C\u8BEF\u62A5\u7387\u4E5F\u66F4\u4F4E\u3002</p><p>ModSecurity \u6709\u4E24\u4E2A\u6838\u5FC3\u7EC4\u4EF6\u3002\u7B2C\u4E00\u4E2A\u662F <strong>\u89C4\u5219\u5F15\u64CE</strong> \uFF0C\u5B83\u5B9E\u73B0\u4E86\u81EA\u5B9A\u4E49\u7684 <code>SecRule</code> \u8BED\u8A00\uFF0C\u6709\u81EA\u5DF1\u7279\u5B9A\u7684\u8BED\u6CD5\u3002\u4F46 <code>SecRule</code> \u4E3B\u8981\u57FA\u4E8E\u6B63\u5219\u8868\u8FBE\u5F0F\uFF0C\u8FD8\u662F\u4E0D\u591F\u7075\u6D3B\uFF0C\u6240\u4EE5\u540E\u6765\u4E5F\u5F15\u5165\u4E86 Lua\uFF0C\u5B9E\u73B0\u4E86\u811A\u672C\u5316\u914D\u7F6E\u3002</p><p>ModSecurity \u7684\u89C4\u5219\u5F15\u64CE\u4F7F\u7528 C++11 \u5B9E\u73B0\uFF0C\u53EF\u4EE5\u4ECE <a href="https://github.com/SpiderLabs/ModSecurity" target="_blank" rel="noreferrer">GitHub</a> \u4E0A\u4E0B\u8F7D\u6E90\u7801\uFF0C\u7136\u540E\u96C6\u6210\u8FDB Nginx\u3002\u56E0\u4E3A\u5B83\u6BD4\u8F83\u5E9E\u5927\uFF0C\u7F16\u8BD1\u5F88\u8D39\u65F6\u95F4\uFF0C\u6240\u4EE5\u6700\u597D\u7F16\u8BD1\u6210\u52A8\u6001\u6A21\u5757\uFF0C\u5728\u914D\u7F6E\u6587\u4EF6\u91CC\u7528\u6307\u4EE4 <code>load_module</code> \u52A0\u8F7D\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#A6ACCD;">load_module modules</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">ngx_http_modsecurity_module.</span><span style="color:#FFCB6B;">so</span><span style="color:#A6ACCD;">;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u53EA\u6709\u5F15\u64CE\u8FD8\u4E0D\u591F\uFF0C\u8981\u8BA9\u5F15\u64CE\u8FD0\u8F6C\u8D77\u6765\uFF0C\u8FD8\u9700\u8981\u5B8C\u5584\u7684\u9632\u5FA1\u89C4\u5219\uFF0C\u6240\u4EE5 ModSecurity \u7684\u7B2C\u4E8C\u4E2A\u6838\u5FC3\u7EC4\u4EF6\u5C31\u662F\u5B83\u7684 <strong>\u89C4\u5219\u96C6</strong> \u3002</p><p>ModSecurity \u6E90\u7801\u63D0\u4F9B\u4E00\u4E2A\u57FA\u672C\u7684\u89C4\u5219\u914D\u7F6E\u6587\u4EF6 <strong>modsecurity.conf-recommended</strong> \uFF0C\u4F7F\u7528\u524D\u8981\u628A\u5B83\u7684\u540E\u7F00\u6539\u6210 <code>conf</code> \u3002</p><p>\u6709\u4E86\u89C4\u5219\u96C6\uFF0C\u5C31\u53EF\u4EE5\u5728 Nginx \u914D\u7F6E\u6587\u4EF6\u91CC\u52A0\u8F7D\uFF0C\u7136\u540E\u542F\u52A8\u89C4\u5219\u5F15\u64CE\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#A6ACCD;">modsecurity on;</span></span>
<span class="line"><span style="color:#A6ACCD;">modsecurity_rules_file </span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">path</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">to</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">modsecurity.</span><span style="color:#FFCB6B;">conf</span><span style="color:#A6ACCD;">;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p><code>modsecurity.conf</code> \u6587\u4EF6\u9ED8\u8BA4\u53EA\u6709\u68C0\u6D4B\u529F\u80FD\uFF0C\u4E0D\u63D0\u4F9B\u5165\u4FB5\u963B\u65AD\uFF0C\u8FD9\u662F\u4E3A\u4E86\u9632\u6B62\u8BEF\u6740\u8BEF\u62A5\uFF0C\u628A <code>SecRuleEngine</code> \u540E\u9762\u6539\u6210 <code>On</code> \u5C31\u53EF\u4EE5\u5F00\u542F\u5B8C\u5168\u7684\u9632\u62A4\uFF1A</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">#</span><span style="color:#A6ACCD;">SecRuleEngine DetectionOnly</span></span>
<span class="line"><span style="color:#A6ACCD;">SecRuleEngine  On</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>\u57FA\u672C\u7684\u89C4\u5219\u96C6\u4E4B\u5916\uFF0CModSecurity \u8FD8\u989D\u5916\u63D0\u4F9B\u4E00\u4E2A\u66F4\u5B8C\u5584\u7684\u89C4\u5219\u96C6\uFF0C\u4E3A\u7F51\u7AD9\u63D0\u4F9B\u5168\u9762\u53EF\u9760\u7684\u4FDD\u62A4\u3002\u8FD9\u4E2A\u89C4\u5219\u96C6\u7684\u5168\u540D\u53EB <strong>OWASP ModSecurity \u6838\u5FC3\u89C4\u5219\u96C6</strong>\uFF08Open Web Application Security Project ModSecurity Core Rule Set\uFF09\uFF0C\u56E0\u4E3A\u540D\u5B57\u592A\u957F\u4E86\uFF0C\u6240\u4EE5\u6709\u65F6\u5019\u4F1A\u7B80\u79F0\u4E3A\u300C\u6838\u5FC3\u89C4\u5219\u96C6\u300D\u6216\u8005 <code>CRS</code> \u3002</p><p><img src="`+l+`" alt="img"></p><p>CRS \u4E5F\u662F\u5B8C\u5168\u5F00\u6E90\u3001\u514D\u8D39\u7684\uFF0C\u53EF\u4EE5\u4ECE GitHub \u4E0A\u4E0B\u8F7D\uFF1A</p><div class="language- line-numbers-mode"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">git clone https://github.com/SpiderLabs/owasp-modsecurity-crs.git</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5176\u4E2D\u6709\u4E00\u4E2A <strong>crs-setup.conf.example</strong> \u7684\u6587\u4EF6\uFF0C\u5B83\u662F CRS \u7684\u57FA\u672C\u914D\u7F6E\uFF0C\u53EF\u4EE5\u7528 <code>Include</code> \u547D\u4EE4\u6DFB\u52A0\u5230 <code>modsecurity.conf</code> \u91CC\uFF0C\u7136\u540E\u518D\u6DFB\u52A0 <code>rules</code> \u91CC\u7684\u5404\u79CD\u89C4\u5219\u3002</p><div class="language-lua line-numbers-mode"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#A6ACCD;">Include </span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">path</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">to</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">crs</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">setup.</span><span style="color:#FFCB6B;">conf</span></span>
<span class="line"><span style="color:#A6ACCD;">Include </span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">path</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">to</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">rules</span><span style="color:#676E95;">/*.conf</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>\u4F60\u5982\u679C\u6709\u5174\u8DA3\u53EF\u4EE5\u770B\u4E00\u4E0B\u8FD9\u4E9B\u914D\u7F6E\u6587\u4EF6\uFF0C\u91CC\u9762\u7528 <code>SecRule</code> \u5B9A\u4E49\u4E86\u5F88\u591A\u7684\u89C4\u5219\uFF0C\u57FA\u672C\u7684\u5F62\u5F0F\u662F <code>SecRule \u53D8\u91CF \u8FD0\u7B97\u7B26 \u52A8\u4F5C</code> \u3002\u4E0D\u8FC7 ModSecurity \u7684\u8FD9\u5957\u8BED\u6CD5\u81EA\u6210\u4E00\u4F53\uFF0C\u6BD4\u8F83\u590D\u6742\uFF0C\u8981\u5B8C\u5168\u638C\u63E1\u4E0D\u662F\u4E00\u671D\u4E00\u5915\u7684\u4E8B\u60C5\uFF0C\u6211\u5C31\u4E0D\u8BE6\u7EC6\u89E3\u91CA\u4E86\u3002</p><p>\u53E6\u5916\uFF0CModSecurity \u8FD8\u6709\u5F3A\u5927\u7684\u5BA1\u8BA1\u65E5\u5FD7\uFF08Audit Log\uFF09\u529F\u80FD\uFF0C\u8BB0\u5F55\u4EFB\u4F55\u53EF\u7591\u7684\u6570\u636E\uFF0C\u4F9B\u4E8B\u540E\u79BB\u7EBF\u5206\u6790\u3002\u4F46\u5728\u751F\u4EA7\u73AF\u5883\u4E2D\u4F1A\u9047\u5230\u5927\u91CF\u7684\u653B\u51FB\uFF0C\u65E5\u5FD7\u4F1A\u5FEB\u901F\u589E\u957F\uFF0C\u6D88\u8017\u78C1\u76D8\u7A7A\u95F4\uFF0C\u800C\u4E14\u5199\u78C1\u76D8\u4E5F\u4F1A\u5F71\u54CD Nginx \u7684\u6027\u80FD\uFF0C\u6240\u4EE5\u4E00\u822C\u5EFA\u8BAE\u628A\u5B83\u5173\u95ED\uFF1A</p><div class="language- line-numbers-mode"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">SecAuditEngine off  #RelevantOnly</span></span>
<span class="line"><span style="color:#A6ACCD;">SecAuditLog /var/log/modsec_audit.log</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="\u5C0F\u7ED3" tabindex="-1">\u5C0F\u7ED3 <a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a></h2><p>\u4ECA\u5929\u6211\u4EEC\u4E00\u8D77\u5B66\u4E60\u4E86\u201C\u7F51\u7EDC\u5E94\u7528\u9632\u706B\u5899\u201D\uFF0C\u4E5F\u5C31\u662F WAF\uFF0C\u4F7F\u7528\u5B83\u53EF\u4EE5\u52A0\u56FA Web \u670D\u52A1\u3002</p><ol><li>Web \u670D\u52A1\u901A\u5E38\u90FD\u8FD0\u884C\u5728\u516C\u7F51\u4E0A\uFF0C\u5BB9\u6613\u53D7\u5230 DDoS\u3001\u4EE3\u7801\u6CE8\u5165\u7B49\u5404\u79CD\u9ED1\u5BA2\u653B\u51FB\uFF0C\u5F71\u54CD\u6B63\u5E38\u7684\u670D\u52A1\uFF0C\u6240\u4EE5\u5FC5\u987B\u8981\u91C7\u53D6\u63AA\u65BD\u52A0\u4EE5\u4FDD\u62A4\uFF1B</li><li>WAF \u662F\u4E00\u79CD HTTP \u5165\u4FB5\u68C0\u6D4B\u548C\u9632\u5FA1\u7CFB\u7EDF\uFF0C\u5DE5\u4F5C\u5728\u4E03\u5C42\uFF0C\u4E3A Web \u670D\u52A1\u63D0\u4F9B\u5168\u9762\u7684\u9632\u62A4\uFF1B</li><li>ModSecurity \u662F\u4E00\u4E2A\u5F00\u6E90\u7684\u3001\u751F\u4EA7\u7EA7\u7684 WAF \u4EA7\u54C1\uFF0C\u6838\u5FC3\u7EC4\u6210\u90E8\u5206\u662F <strong>\u89C4\u5219\u5F15\u64CE</strong> \u548C <strong>\u89C4\u5219\u96C6</strong> \uFF0C\u4E24\u8005\u7684\u5173\u7CFB\u6709\u70B9\u50CF\u6740\u6BD2\u5F15\u64CE\u548C\u75C5\u6BD2\u7279\u5F81\u5E93\uFF1B</li><li>WAF \u5B9E\u8D28\u4E0A\u662F\u6A21\u5F0F\u5339\u914D\u4E0E\u6570\u636E\u8FC7\u6EE4\uFF0C\u6240\u4EE5\u4F1A\u6D88\u8017 CPU\uFF0C\u589E\u52A0\u4E00\u4E9B\u8BA1\u7B97\u6210\u672C\uFF0C\u964D\u4F4E\u670D\u52A1\u80FD\u529B\uFF0C\u4F7F\u7528\u65F6\u9700\u8981\u5728\u5B89\u5168\u4E0E\u6027\u80FD\u4E4B\u95F4\u627E\u5230\u4E00\u4E2A\u5E73\u8861\u70B9\u3002</li></ol><h2 id="\u8BFE\u4E0B\u4F5C\u4E1A" tabindex="-1">\u8BFE\u4E0B\u4F5C\u4E1A <a class="header-anchor" href="#\u8BFE\u4E0B\u4F5C\u4E1A" aria-hidden="true">#</a></h2><ol><li>HTTPS \u4E3A\u4EC0\u4E48\u4E0D\u80FD\u9632\u5FA1 DDoS\u3001\u4EE3\u7801\u6CE8\u5165\u7B49\u653B\u51FB\u5462\uFF1F</li><li>\u4F60\u8FD8\u77E5\u9053\u6709\u54EA\u4E9B\u624B\u6BB5\u80FD\u591F\u62B5\u5FA1\u7F51\u7EDC\u653B\u51FB\u5417\uFF1F</li></ol>`,63),c=[r];function t(i,d,u,A,C,y){return n(),a("div",null,c)}const g=s(o,[["render",t]]);export{b as __pageData,g as default};
