import{_ as a,o as e,c as r,a as t}from"./app.a9f825fd.js";const g=JSON.parse('{"title":"\u524D\u8A00","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5173\u4E8E\u4F5C\u8005","slug":"\u5173\u4E8E\u4F5C\u8005","link":"#\u5173\u4E8E\u4F5C\u8005","children":[]},{"level":2,"title":"FAQ","slug":"faq","link":"#faq","children":[]},{"level":2,"title":"\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4","slug":"\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4","link":"#\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4","children":[]}],"relativePath":"road-to-react-chinese/intro.md","lastUpdated":1676386809000}'),p={name:"road-to-react-chinese/intro.md"},c=t('<h1 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h1><p>\u300A\u901A\u5411 React \u4E4B\u8DEF\u300B\u65E8\u5728\u8BB2\u6388 React \u57FA\u7840\u77E5\u8BC6\u3002\u4F60\u5C06\u4F1A\u7528 React \u642D\u5EFA\u4E00\u4E2A\u771F\u5B9E\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u65E0\u9700\u5176\u4ED6\u590D\u6742\u7684\u5DE5\u5177\u3002\u4ECE\u9879\u76EE\u521D\u59CB\u5316\u5230\u670D\u52A1\u5668\u90E8\u7F72\uFF0C\u6211\u4F1A\u4E3A\u4F60\u8BE6\u7EC6\u8BB2\u89E3\u5176\u4E2D\u7684\u6BCF\u4E00\u6B65\uFF0C\u5E76\u5728\u6BCF\u4E00\u7AE0\u90FD\u9644\u6709\u53C2\u8003\u8D44\u6599\u548C\u7EC3\u4E60\u3002\u8BFB\u5B8C\u6B64\u4E66\u540E\u4F60\u5C06\u80FD\u591F\u642D\u5EFA\u81EA\u5DF1\u7684 React \u5E94\u7528\u3002\u6211\u548C\u793E\u533A\u4F1A\u6301\u7EED\u7EF4\u62A4\u548C\u66F4\u65B0\u8FD9\u4E9B\u8D44\u6599\u3002</p><p>\u5728\u6DF1\u5165\u5230\u5E7F\u9614\u7684 React \u751F\u6001\u7CFB\u7EDF\u4E4B\u524D\uFF0C\u5E94\u8BE5\u5148\u6253\u7A33\u57FA\u7840\u3002\u6211\u4F1A\u7528\u66F4\u5173\u6CE8 React \u81EA\u8EAB\u7684\u65B9\u5F0F\u6765\u8BB2\u89E3\u5B83\u7684\u6982\u5FF5\uFF0C\u5C3D\u91CF\u51CF\u5C11\u5DE5\u5177\u548C\u5916\u90E8\u72B6\u6001\u7BA1\u7406\u7684\u4F7F\u7528\uFF0C\u4ECE\u800C\u63D0\u4F9B\u66F4\u591A\u5173\u4E8E React \u81EA\u8EAB\u7684\u77E5\u8BC6\u3002\u6211\u4F1A\u901A\u8FC7\u4E00\u4E2A\u771F\u5B9E\u7684 React \u5E94\u7528\u6765\u8BB2\u89E3\u8FD9\u4E9B\u901A\u7528\u6982\u5FF5\u3001\u6A21\u5F0F\u548C\u6700\u4F73\u5B9E\u8DF5\u3002</p><p>\u5927\u4F53\u4E0A\u4F60\u5C06\u4F1A\u5B66\u5230\u5982\u4F55\u4ECE\u5934\u5F00\u59CB\u5199\u4E00\u4E2A React \u5E94\u7528\uFF0C\u5305\u542B\u5206\u9875\u3001\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u641C\u7D22\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u9AD8\u7EA7\u7684\u4EA4\u4E92\u65B9\u5F0F\uFF0C\u4F8B\u5982\u6392\u5E8F\u7B49\u7B49\u3002\u5E0C\u671B\u5728\u9605\u8BFB\u672C\u4E66\u7684\u8FC7\u7A0B\u4E2D\u80FD\u8BA9\u4F60\u611F\u53D7\u5230\u6211\u5BF9 React \u548C JavaScript \u7684\u70ED\u7231\uFF0C\u8BA9\u5B83\u4EEC\u4F34\u4F60\u4E00\u540C\u8D70\u8FDB React \u7684\u4E16\u754C\u3002</p><h2 id="\u5173\u4E8E\u4F5C\u8005" tabindex="-1">\u5173\u4E8E\u4F5C\u8005 <a class="header-anchor" href="#\u5173\u4E8E\u4F5C\u8005" aria-hidden="true">#</a></h2><p>\u6211\u662F\u4E00\u540D\u6765\u81EA\u5FB7\u56FD\u7684\u8F6F\u4EF6\u548C\u4E92\u8054\u7F51\u5DE5\u7A0B\u5E08\uFF0C\u81F4\u529B\u4E8E\u5B66\u4E60\u548C\u6559\u6388 JavaScript \u7F16\u7A0B\u3002\u5728\u83B7\u5F97\u8BA1\u7B97\u673A\u79D1\u5B66\u7855\u58EB\u5B66\u4F4D\u540E\uFF0C\u6211\u6CA1\u6709\u505C\u6B62\u8FC7\u81EA\u6211\u5B66\u4E60\u3002\u5728\u521D\u521B\u516C\u53F8\u7684\u90A3\u6BB5\u65E5\u5B50\u91CC\uFF0C\u65E0\u8BBA\u662F\u5DE5\u4F5C\u8FD8\u662F\u4E1A\u4F59\u65F6\u95F4\uFF0C\u6211\u6574\u5929\u90FD\u5728\u548C JavaScript \u6253\u4EA4\u9053\u3002\u8FD9\u6BB5\u7ECF\u5386\u4F7F\u6211\u79EF\u6512\u4E86\u5927\u91CF\u7ECF\u9A8C\uFF0C\u4E5F\u6700\u7EC8\u5F15\u9886\u6211\u8D70\u5411\u4E86 JavaScript \u4F20\u9053\u8005\u4E4B\u8DEF\u3002</p><p>\u6709\u51E0\u5E74\uFF0C\u6211\u548C\u4E00\u7FA4\u51FA\u7C7B\u62D4\u8403\u7684\u5DE5\u7A0B\u5E08\u4E00\u8D77\u5728\u4E00\u5BB6\u53EB\u505A Small Improvements \u7684\u516C\u53F8\u5DE5\u4F5C\uFF0C\u5F00\u53D1\u5927\u578B\u5E94\u7528\u7A0B\u5E8F\u3002\u516C\u53F8\u7ECF\u8425\u7684\u662F\u4E00\u6B3E SaaS \u4EA7\u54C1\uFF0C\u80FD\u591F\u8BA9\u5BA2\u6237\u63D0\u4F9B\u53CD\u9988\u7ED9\u5546\u5BB6\u3002\u4EA7\u54C1\u7684\u524D\u7AEF\u662F\u7528\u7684 JavaScript\uFF0C\u540E\u7AEF\u662F Java\u3002\u4EA7\u54C1\u6700\u521D\u7248\u672C\u7684\u524D\u7AEF\u4F7F\u7528 Java \u7684 Wicket \u6846\u67B6\u548C jQuery\u3002\u6CA1\u8FC7\u591A\u4E45\uFF0C\u7B2C\u4E00\u4EE3 SPA\uFF08\u5355\u9875\u9762\u5E94\u7528\uFF09\u5F00\u59CB\u6D41\u884C\u8D77\u6765\uFF0C\u516C\u53F8\u51B3\u5B9A\u628A\u524D\u7AEF\u8FC1\u79FB\u5230 Angular 1.x \u3002\u7136\u800C\u5728\u548C Angular \u7EA0\u7F20\u4E86\u4E24\u5E74\u591A\u4EE5\u540E\uFF0C\u6211\u4EEC\u6E10\u6E10\u53D1\u73B0\u5B83\u5E76\u4E0D\u662F\u7BA1\u7406\u590D\u6742\u591A\u53D8\u7684\u5E94\u7528\u72B6\u6001\u7684\u6700\u4F73\u65B9\u6848\uFF0C\u8F6C\u800C\u6295\u5165\u4E86 React \u548C Redux \u7684\u6000\u62B1\uFF0C\u8FD9\u4F7F\u5F97\u4EA7\u54C1\u5927\u83B7\u6210\u529F\u3002</p><p>\u6211\u5728\u516C\u53F8\u65F6\uFF0C\u4F1A\u5B9A\u671F\u5730\u5728\u6211\u7684\u7F51\u7AD9\u4E0A\u5199\u4E00\u4E9B\u5173\u4E8E Web \u5F00\u53D1\u7684\u6587\u7AE0\u3002\u611F\u8C22\u90A3\u65F6\u7684\u8BFB\u8005\u7ED9\u4E86\u6211\u5F88\u591A\u5F88\u68D2\u7684\u53CD\u9988\uFF0C\u8BA9\u6211\u7684\u5199\u4F5C\u548C\u6559\u5B66\u6C34\u5E73\u5F97\u5230\u4E86\u63D0\u5347\u3002\u5C31\u8FD9\u6837\u65E5\u79EF\u6708\u7D2F\uFF0C\u6211\u6E10\u6E10\u4E60\u5F97\u4E86\u6559\u7EC3\u6280\u5DE7\u3002\u6211\u53D1\u73B0\u6211\u65E9\u671F\u7684\u6587\u7AE0\u503E\u5411\u4E8E\u5305\u542B\u8FC7\u91CF\u7684\u4FE1\u606F\uFF0C\u8BA9\u5B66\u751F\u4E0D\u582A\u91CD\u8D1F\uFF0C\u6211\u6162\u6162\u5730\u5B66\u4F1A\u4E86\u6BCF\u6B21\u53EA\u5173\u6CE8\u4E00\u4E2A\u4E3B\u9898\u3002</p><p>\u5982\u4ECA\uFF0C\u6211\u4F5C\u4E3A\u81EA\u7531\u804C\u4E1A\u8005\u5728\u7EE7\u7EED\u8F6F\u4EF6\u5F00\u53D1\u5DE5\u7A0B\u5E08\u548C\u6559\u80B2\u8005\u7684\u5DE5\u4F5C\u3002\u901A\u8FC7\u8BBE\u5B9A\u660E\u786E\u7684\u76EE\u6807\u548C\u77ED\u671F\u53CD\u9988\u73AF\uFF0C\u6211\u80FD\u770B\u5230\u5B66\u751F\u4EEC\u53EF\u4EE5\u5F97\u5230\u5FEB\u901F\u6210\u957F\uFF0C\u6211\u7684\u6210\u5C31\u611F\u4E5F\u6765\u6E90\u4E8E\u6B64\u3002\u6211\u7684<a href="https://www.robinwieruch.de/about" target="_blank" rel="noreferrer">\u7F51\u7AD9</a>\u4E0A\u6709\u66F4\u591A\u5173\u4E8E\u6211\u4E2A\u4EBA\u7684\u4FE1\u606F\uFF0C\u4EE5\u53CA\u5982\u4F55\u652F\u6301\u6216\u548C\u6211\u4E00\u8D77\u5DE5\u4F5C\u7684\u65B9\u5F0F\u3002</p><h2 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-hidden="true">#</a></h2><p><strong>\u5982\u4F55\u83B7\u53D6\u66F4\u65B0\uFF1F</strong></p><p>\u6211\u901A\u8FC7\u4E24\u79CD\u65B9\u5F0F\u53D1\u5E03\u5185\u5BB9\u66F4\u65B0\uFF1A\u901A\u8FC7<a href="https://www.getrevue.co/profile/rwieruch" target="_blank" rel="noreferrer">\u90AE\u4EF6\u8BA2\u9605\u66F4\u65B0</a>\u6216\u8005<a href="https://twitter.com/rwieruch" target="_blank" rel="noreferrer">\u5728 Twitter \u4E0A\u5173\u6CE8\u6211</a>\u3002\u65E0\u8BBA\u662F\u54EA\u79CD\u65B9\u5F0F\uFF0C\u6211\u90FD\u7AED\u529B\u4FDD\u8BC1\u5185\u5BB9\u7684\u8D28\u91CF\u3002\u5F53\u6536\u5230\u672C\u4E66\u5185\u5BB9\u66F4\u65B0\u7684\u901A\u77E5\u65F6\uFF0C\u4F60\u5C31\u53EF\u4EE5\u4ECE\u6211\u7684\u7F51\u7AD9\u4E0A\u4E0B\u8F7D\u65B0\u7684\u7248\u672C\u3002</p><p><strong>\u5B66\u4E60\u8D44\u6599\u662F\u6700\u65B0\u7684\u5417\uFF1F</strong></p><p>\u4E00\u822C\u7F16\u7A0B\u76F8\u5173\u7684\u4E66\u7C4D\u5728\u5B83\u4EEC\u53D1\u5E03\u4E4B\u540E\u5F88\u5BB9\u6613\u8FC7\u65F6\u3002\u4F46\u662F\u672C\u4E66\u662F\u6211\u81EA\u5DF1\u51FA\u7248\u7684\uFF0C\u6240\u4EE5\u5F53\u4E66\u4E2D\u76F8\u5173\u7684\u5185\u5BB9\u6709\u65B0\u53D1\u5E03\u7684\u65F6\u5019\uFF0C\u6211\u53EF\u4EE5\u53CA\u65F6\u5730\u66F4\u65B0\u7248\u672C\u3002</p><p><strong>\u5982\u679C\u6211\u5728 Amazon \u4E0A\u5DF2\u7ECF\u8D2D\u4E70\u4E86\u6B64\u4E66\uFF0C\u53EF\u4EE5\u7D22\u53D6\u7535\u5B50\u7248\u5417\uFF1F</strong></p><p>\u5982\u679C\u4F60\u5DF2\u7ECF\u5728 Amazon \u4E0A\u8D2D\u4E70\u6B64\u4E66\uFF0C\u4F60\u53EF\u80FD\u5DF2\u7ECF\u770B\u5230\u6211\u7684\u7F51\u7AD9\u4E0A\u4E5F\u4E0A\u67B6\u4E86\u3002\u56E0\u4E3A\u6211\u4F1A\u7528 Amazon \u53D8\u73B0\u4E00\u90E8\u5206\u901A\u5E38\u6765\u8BF4\u514D\u8D39\u7684\u5185\u5BB9\uFF0C\u5728\u6B64\u6211\u8877\u5FC3\u611F\u8C22\u4F60\u7684\u652F\u6301\uFF0C\u5E76\u9080\u8BF7\u4F60\u6CE8\u518C<a href="https://www.robinwieruch.de/" target="_blank" rel="noreferrer">\u6211\u7684\u7F51\u7AD9</a>\u3002\u5F53\u521B\u5EFA\u597D\u8D26\u53F7\u4E4B\u540E\uFF0C\u8BF7\u628A\u4F60\u7684\u90AE\u7BB1\u548C Amazon \u7684\u8D2D\u4E70\u6536\u636E\u901A\u8FC7\u90AE\u4EF6\u53D1\u7ED9\u6211\uFF0C\u6211\u4F1A\u4E3A\u4F60\u5728\u7F51\u7AD9\u4E0A\u89E3\u9501\u76F8\u5E94\u5185\u5BB9\u3002\u5F53\u6709\u4E86\u8D26\u53F7\u4E4B\u540E\uFF0C\u4F60\u5C31\u53EF\u4EE5\u4E00\u76F4\u83B7\u5F97\u6700\u65B0\u7684\u5185\u5BB9\u3002</p><p>\u5982\u679C\u4F60\u8D2D\u4E70\u4E86\u8BE5\u4E66\u7684\u7EB8\u8D28\u7248\uFF0C\u8BF7\u5C3D\u53EF\u80FD\u5730\u7269\u5C3D\u5176\u7528\u3002\u6211\u7279\u610F\u628A\u4E66\u7684\u5C3A\u5BF8\u505A\u5F97\u5927\u4E00\u4E9B\uFF0C\u4EE5\u4FBF\u4E8E\u663E\u793A\u66F4\u591A\u4EE3\u7801\uFF0C\u4E5F\u65B9\u4FBF\u4F60\u5728\u9605\u8BFB\u7684\u540C\u65F6\u505A\u7B14\u8BB0\u3002</p><p><strong>\u5982\u679C\u9605\u8BFB\u672C\u4E66\u65F6\u6211\u9700\u8981\u5E2E\u52A9\u600E\u4E48\u529E\uFF1F</strong></p><p>\u6211\u4EEC\u6709\u4E00\u4E2A <a href="https://slack-the-road-to-learn-react.wieruch.com/" target="_blank" rel="noreferrer">Slack \u804A\u5929\u7EC4</a>\uFF0C\u4E13\u4E3A\u81EA\u5B66\u672C\u4E66\u7684\u4EBA\u800C\u521B\u5EFA\u3002\u4F60\u53EF\u4EE5\u52A0\u5165\u8FD9\u4E2A\u7FA4\u7EC4\u6765\u83B7\u53D6\u5E2E\u52A9\uFF0C\u6216\u8005\u5E2E\u52A9\u4ED6\u4EBA\uFF0C\u56E0\u4E3A\u5728\u5E2E\u52A9\u4ED6\u4EBA\u7684\u540C\u65F6\u4F60\u4E5F\u53EF\u4EE5\u52A0\u6DF1\u81EA\u5DF1\u7684\u7406\u89E3\u3002</p><p><strong>\u6211\u53EF\u4EE5\u4E3A\u5185\u5BB9\u6539\u8FDB\u505A\u8D21\u732E\u5417\uFF1F</strong></p><p>\u5982\u679C\u4F60\u6709\u4EFB\u4F55\u53CD\u9988\uFF0C\u8BF7\u4E0D\u8981\u72B9\u8C6B\u53D1\u9001\u90AE\u4EF6\u7ED9\u6211\uFF0C\u6211\u4F1A\u8BA4\u771F\u8003\u8651\u4F60\u7684\u5EFA\u8BAE\u3002\u4F46\u5982\u679C\u662F bug \u8FFD\u8E2A\u6216\u8005\u7591\u96BE\u89E3\u7B54\u7C7B\u7684\u90AE\u4EF6\u53EF\u80FD\u5C31\u4E0D\u8981\u671F\u671B\u6709\u56DE\u590D\u4E86\uFF0CSlack \u7FA4\u7EC4\u624D\u662F\u7528\u6765\u505A\u8FD9\u4E2A\u7684\u3002</p><p><strong>\u6211\u53EF\u4EE5\u600E\u6837\u652F\u6301\u8BE5\u9879\u76EE\uFF1F</strong></p><p>\u5982\u679C\u4F60\u53D1\u73B0\u6211\u7684\u8BFE\u7A0B\u5F88\u6709\u5E2E\u52A9\u5E76\u613F\u610F\u8D21\u732E\u5185\u5BB9\uFF0C\u8BF7\u5728\u6211\u7F51\u7AD9\u7684<a href="https://www.robinwieruch.de/about/" target="_blank" rel="noreferrer">\u5173\u4E8E\u9875\u9762</a>\u4E0A\u627E\u5230\u66F4\u591A\u5173\u4E8E\u5982\u4F55\u63D0\u4F9B\u652F\u6301\u7684\u4FE1\u606F\u3002\u5F53\u7136\u5982\u679C\u8BFB\u8005\u4EEC\u80FD\u5E2E\u6211\u591A\u591A\u5BA3\u4F20\u6211\u7684\u4E66\u5982\u4F55\u5E2E\u52A9\u4F60\u4EEC\u6210\u957F\uFF0C\u4E5F\u662F\u975E\u5E38\u6709\u5E2E\u52A9\u7684\uFF0C\u8FD9\u53EF\u4EE5\u8BA9\u66F4\u591A\u60F3\u63D0\u5347 web \u5F00\u53D1\u6280\u80FD\u7684\u4EBA\u63A5\u89E6\u5230\u6211\u3002\u7528\u4EE5\u4E0A\u4EFB\u4F55\u65B9\u6CD5\u63D0\u4F9B\u652F\u6301\u90FD\u53EF\u4EE5\u5E2E\u52A9\u6211\u66F4\u597D\u7684\u6253\u9020\u6709\u6DF1\u5EA6\u7684\u8BFE\u7A0B\u548C\u63D0\u4F9B\u514D\u8D39\u7684\u8D44\u6599\u3002</p><p><strong>\u662F\u4EC0\u4E48\u9A71\u4F7F\u4F60\u5199\u8FD9\u672C\u4E66\uFF1F</strong></p><p>\u6211\u4E00\u76F4\u90FD\u60F3\u8BB2\u6388\u8FD9\u4E2A\u8BDD\u9898\u3002\u6211\u7ECF\u5E38\u53D1\u73B0\u7F51\u4E0A\u7684\u4E00\u4E9B\u8D44\u6599\u6CA1\u6709\u66F4\u65B0\uFF0C\u53C8\u6216\u53EA\u66F4\u65B0\u5176\u4E2D\u7684\u4E00\u5C0F\u90E8\u5206\u3002\u6709\u65F6\u4EBA\u4EEC\u96BE\u4EE5\u627E\u5230\u6301\u7EED\u66F4\u65B0\u7684\u81EA\u5B66\u6750\u6599\u3002\u6211\u5E0C\u671B\u80FD\u63D0\u4F9B\u8FDE\u8D2F\u7684\u3001\u6301\u7EED\u66F4\u65B0\u7684\u5B66\u4E60\u4F53\u9A8C\u3002\u5E76\u4E14\u6211\u4E5F\u5E0C\u671B\u80FD\u591F\u901A\u8FC7\u8FD9\u4E2A\u9879\u76EE\u7ED9\u4E88\u90A3\u4E9B\u4E0D\u5E78\u7684\u4EBA\u4EE5\u5E2E\u52A9\uFF0C\u4E3A\u4ED6\u4EEC\u63D0\u4F9B\u514D\u8D39\u5185\u5BB9\u6216\u662F<a href="https://www.robinwieruch.de/giving-back-by-learning-react/" target="_blank" rel="noreferrer">\u5176\u4ED6\u5E2E\u52A9</a>\u3002</p><h2 id="\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4" tabindex="-1">\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4 <a class="header-anchor" href="#\u672C\u4E66\u7684\u9762\u5411\u4EBA\u7FA4" aria-hidden="true">#</a></h2><ul><li><p>JavaScript \u521D\u5B66\u8005\uFF0C\u4E86\u89E3\u57FA\u672C\u7684 JS\u3001CSS \u548C HTML \u77E5\u8BC6\u3002\u5982\u679C\u4F60\u521A\u5F00\u59CB\u63A5\u89E6 Web \u5F00\u53D1\uFF0C\u5BF9 JS\u3001CSS \u548C HTML \u6709\u57FA\u672C\u6982\u5FF5\uFF0C\u90A3\u4E48\u672C\u4E66\u53EF\u4EE5\u63D0\u4F9B\u6240\u6709\u5B66\u4E60 React \u9700\u8981\u7684\u5185\u5BB9\u3002\u5982\u679C\u4F60\u89C9\u5F97\u81EA\u5DF1\u7684 JavaScript \u6C34\u5E73\u8FD8\u4E0D\u591F\uFF0C\u90A3\u53EF\u4EE5\u5728\u7EE7\u7EED\u672C\u4E66\u4E4B\u524D\u4E86\u89E3\u66F4\u591A\u5173\u4E8E JavaScript \u7684\u77E5\u8BC6\u3002\u5F53\u7136\uFF0C\u4F60\u4E5F\u4F1A\u5728\u672C\u4E66\u7684\u53C2\u8003\u4E2D\u83B7\u5F97\u66F4\u591A\u57FA\u7840\u77E5\u8BC6\u7684\u76F8\u5173\u8D44\u6599\u3002</p></li><li><p>\u4ECE jQuery \u65F6\u4EE3\u6765\u7684 JavaScript \u8001\u624B\uFF1A\u5982\u679C\u4F60\u5728 jQuery\u3001MooTools \u548C Dojo \u7684\u5E74\u4EE3\u5927\u91CF\u4F7F\u7528\u8FC7 JavaScript\uFF0C\u53EF\u80FD\u4F1A\u548C\u5F53\u4E0B\u7684 JavaScript \u8D8B\u52BF\u6709\u70B9\u96BE\u4EE5\u63A5\u8F68\u3002\u7136\u800C\u5B83\u4EEC\u5E95\u5C42\u5176\u5B9E\u4ECE\u6765\u6CA1\u6709\u53D8\u8FC7\uFF0C\u4F9D\u7136\u662F JavaScript \u548C HTML\uFF0C\u6240\u4EE5\u672C\u4E66\u5E94\u8BE5\u53EF\u4EE5\u5E2E\u4F60\u7528\u6B63\u786E\u7684\u59FF\u52BF\u5F00\u59CB\u5B66\u4E60 React\u3002</p></li><li><p>JavaScript \u7231\u597D\u8005\uFF0C\u5DF2\u6709\u5176\u4ED6\u73B0\u4EE3\u5355\u9875\u9762\u5E94\u7528\u6846\u67B6\u7684\u7ECF\u9A8C\uFF1A\u5982\u679C\u4F60\u7528\u8FC7 Angular \u6216\u8005 Vue\uFF0C\u867D\u7136 React \u5E94\u7528\u5199\u8D77\u6765\u4F1A\u5F88\u4E0D\u4E00\u6837\uFF0C\u4F46\u8FD9\u4E9B\u6846\u67B6\u7684\u57FA\u7840\u6784\u4EF6\u90FD\u662F JavaScript \u548C HTML\u3002\u4E00\u65E6\u9002\u5E94\u4E86\u7528 React \u7684\u65B9\u5F0F\u53BB\u601D\u8003\u95EE\u9898\uFF0C\u8FD9\u5BF9\u4F60\u6765\u8BF4\u5C31\u4E0D\u662F\u4EC0\u4E48\u95EE\u9898\u4E86\u3002</p></li><li><p>\u638C\u63E1\u5176\u4ED6\u7F16\u7A0B\u8BED\u8A00\u7684\u4EBA\uFF1A\u4F60\u53EF\u80FD\u5728\u7F16\u7A0B\u539F\u7406\u65B9\u9762\u6BD4\u5176\u4ED6\u4EBA\u66F4\u52A0\u719F\u7EC3\u3002\u5F53\u4E60\u60EF\u4E86 JavaScript \u548C HTML \u4E4B\u540E\uFF0C\u4F60\u5C31\u53EF\u4EE5\u6109\u5FEB\u5730\u548C\u6211\u4E00\u8D77\u5B66\u4E60 React \u4E86\u3002</p></li><li><p>\u5982\u679C\u4F60\u7684\u4E13\u4E1A\u662F\u5728\u8BBE\u8BA1\u65B9\u5411\uFF0C\u7528\u6237\u4EA4\u4E92\u8BBE\u8BA1\u662F\u4F53\u9A8C\u8BBE\u8BA1\uFF0C\u4E5F\u65E0\u9700\u62C5\u5FC3\u3002\u4F60\u53EF\u80FD\u5DF2\u7ECF\u5BF9 HTML \u548C CSS \u5F88\u719F\u6089\u4E86\uFF0C\u53EA\u9700\u8981\u518D\u5B66\u4E60\u4E00\u4E9B JavaScript \u7684\u57FA\u7840\u77E5\u8BC6\uFF0C\u5C31\u80FD\u591F\u9605\u8BFB\u672C\u4E66\u4E86\u3002\u73B0\u5982\u4ECA\u7684 UI/UX \u548C\u5B9E\u73B0\u65B9\u5F0F\u7684\u5173\u7CFB\u8D8A\u6765\u8D8A\u5BC6\u5207\uFF0C\u4E86\u89E3 React \u76F8\u5173\u7684\u77E5\u8BC6\u4F1A\u8BA9\u4F60\u7684\u5DE5\u4F5C\u83B7\u76CA\u532A\u6D45\uFF0C\u672C\u4E66\u5C06\u5F88\u597D\u5730\u5E26\u4F60\u4E86\u89E3\u5B83\u7684\u5DE5\u4F5C\u539F\u7406\u3002</p></li><li><p>\u5982\u679C\u4F60\u662F\u7814\u53D1\u56E2\u961F\u7684\u4EA7\u54C1\u8D1F\u8D23\u4EBA\u6216\u662F\u4EA7\u54C1\u7ECF\u7406\uFF0C\u672C\u4E66\u4F1A\u4E3A\u4F60\u8BE6\u7EC6\u5256\u6790 React \u5E94\u7528\u4E2D\u7684\u6240\u6709\u4E0D\u53EF\u6216\u7F3A\u7684\u90E8\u5206\u3002\u6BCF\u4E00\u7AE0\u8282\u90FD\u8BB2\u89E3\u4E86\u4E00\u4E2A React \u6982\u5FF5\u3001\u6A21\u5F0F\u3001\u6280\u672F\uFF0C\u5E76\u7528\u5176\u6765\u589E\u52A0\u65B0\u529F\u80FD\uFF0C\u6216\u662F\u63D0\u5347\u6574\u4F53\u67B6\u6784\u3002\u8FD9\u662F\u4E00\u4E2A\u5168\u9762\u7684 React \u6559\u7A0B\u3002</p></li></ul>',27),i=[c];function n(o,l,s,h,d,S){return e(),r("div",null,i)}const u=a(p,[["render",n]]);export{g as __pageData,u as default};
