import{_ as e,o as r,c as t,a}from"./app.436197f7.js";const f=JSON.parse('{"title":"\u5927\u7EB2","description":"","frontmatter":{},"headers":[],"relativePath":"road-to-react-chinese/outline.md","lastUpdated":1677316569000}'),o={name:"road-to-react-chinese/outline.md"},n=a('<h1 id="\u5927\u7EB2" tabindex="-1">\u5927\u7EB2 <a class="header-anchor" href="#\u5927\u7EB2" aria-hidden="true">#</a></h1><p>\u6211\u4EEC\u7684 React \u4E4B\u8DEF\u5DF2\u7ECF\u8D70\u5230\u4E86\u5C3D\u5934\uFF0C\u5E0C\u671B\u4F60\u559C\u6B22\u8FD9\u672C\u4E66\uFF0C\u5E76\u5E0C\u671B\u4F60\u80FD\u4ECE\u4E2D\u83B7\u5F97\u4E00\u4E9B\u5E2E\u52A9\u3002\u5982\u679C\u4F60\u559C\u6B22\u8FD9\u672C\u4E66\uFF0C\u8BF7\u4E0E\u6709\u5174\u8DA3\u4E86\u89E3\u66F4\u591A React \u7684\u670B\u53CB\u5206\u4EAB\u3002\u6B64\u5916\uFF0C\u5728<a href="https://amzn.to/2JHlP42" target="_blank" rel="noreferrer">Amazon</a> \u6216 <a href="https://www.goodreads.com/book/show/37503118-the-road-to-learn-react" target="_blank" rel="noreferrer">Goodreads</a>\u4E0A\u53D1\u8868\u8BC4\u8BBA\uFF0C\u5C06\u6709\u52A9\u4E8E\u6211\u6839\u636E\u4F60\u7684\u53CD\u9988\u63D0\u4F9B\u521B\u4F5C\u7684\u5185\u5BB9\u3002</p><p>\u4ECE\u8FD9\u91CC\u5F00\u59CB\uFF0C\u6211\u5EFA\u8BAE\u4F60\u5728\u53C2\u4E0E\u53E6\u4E00\u672C\u4E66\u3001\u8BFE\u7A0B\u6216\u6559\u7A0B\u4E4B\u524D\uFF0C\u5148\u6269\u5C55\u5E94\u7528\u6765\u521B\u5EFA\u81EA\u5DF1\u7684 React \u9879\u76EE\u3002\u8BD5\u7528\u4E00\u5468\uFF0C\u901A\u8FC7\u90E8\u7F72\u5C06\u5176\u5E26\u5165\u751F\u4EA7\uFF0C\u5E76\u8054\u7CFB\u6211\u6216\u5176\u4ED6\u4EBA\u6765\u5C55\u793A\u5B83\u3002\u6211\u603B\u662F\u6709\u5174\u8DA3\u770B\u5230\u6211\u7684\u8BFB\u8005\u521B\u9020\u7684\u4E1C\u897F\uFF0C\u5E76\u4E86\u89E3\u6211\u5982\u4F55\u5E2E\u52A9\u4ED6\u4EEC\u4E00\u8D77\u3002</p><p>\u5982\u679C\u4F60\u6B63\u5728\u4E3A\u4F60\u7684\u5E94\u7528\u5BFB\u627E\u6269\u5C55\uFF0C\u6211\u63A8\u8350\u4F60\u5728\u638C\u63E1\u57FA\u7840\u77E5\u8BC6\u540E\u7684\u51E0\u79CD\u5B66\u4E60\u9014\u5F84\uFF1A</p><ul><li><p><strong>\u8FDE\u63A5\u6570\u636E\u5E93\u6216\u9274\u6743\uFF1A</strong> \u4E0D\u65AD\u58EE\u5927\u7684 React \u5E94\u7528\u6700\u7EC8\u4F1A\u9700\u8981\u6301\u4E45\u6027\u7684\u6570\u636E\u3002\u8FD9\u4E9B\u6570\u636E\u5E94\u8BE5\u5B58\u50A8\u5728\u6570\u636E\u5E93\u4E2D\uFF0C\u8FD9\u6837\u53EF\u4EE5\u5728\u6D4F\u89C8\u5668\u4F1A\u8BDD\u540E\u4FDD\u6301\u6570\u636E\u7684\u5B8C\u6574\u6027\uFF0C\u4EE5\u4FBF\u4E0E\u4E0D\u540C\u7684\u7528\u6237\u5171\u4EAB\u3002Firebase \u662F\u5F15\u5165\u6570\u636E\u5E93\u7684\u6700\u7B80\u5355\u7684\u65B9\u6CD5\u4E4B\u4E00\uFF0C\u800C\u65E0\u9700\u7F16\u5199\u540E\u53F0\u5E94\u7528\u7A0B\u5E8F\u3002\u5728\u6211\u90A3\u672C\u540D\u4E3A<a href="https://www.roadtofirebase.com/" target="_blank" rel="noreferrer">&quot;Firebase \u5B66\u4E60\u4E4B\u9053&quot;</a>\u7684\u4E66\u4E2D\uFF0C\u4F60\u4F1A\u53D1\u73B0\u4E00\u4E2A\u5173\u4E8E\u5982\u4F55\u5728React\u4E2D\u4F7F\u7528Firebase\u8BA4\u8BC1\u548C\u6570\u636E\u5E93\u7684\u5206\u6B65\u6307\u5357\u3002</p></li><li><p><strong>\u8FDE\u63A5\u540E\u7AEF\uFF1A</strong> React \u5904\u7406\u524D\u7AEF\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5230\u76EE\u524D\u4E3A\u6B62\uFF0C\u6211\u4EEC\u53EA\u4ECE\u7B2C\u4E09\u65B9\u540E\u7AEF\u7684API\u8BF7\u6C42\u6570\u636E\u3002\u4F60\u4E5F\u53EF\u4EE5\u7528\u8FDE\u63A5\u5230\u6570\u636E\u5E93\u5E76\u7BA1\u7406\u8BA4\u8BC1/\u6388\u6743\u7684\u540E\u7AEF\u5E94\u7528\u7A0B\u5E8F\u5F15\u5165API\u3002\u5728<a href="https://www.roadtographql.com/" target="_blank" rel="noreferrer">&quot;GraphQL \u5B66\u4E60\u4E4B\u9053&quot;</a>\u4E2D\uFF0C\u6211\u6559\u4F60\u5982\u4F55\u4F7F\u7528 GraphQL \u8FDB\u884C\u5BA2\u6237\u7AEF-\u670D\u52A1\u5668\u901A\u4FE1\u3002\u4F60\u5C06\u5B66\u4E60\u5230\u5982\u4F55\u5C06\u4F60\u7684\u540E\u7AEF\u8FDE\u63A5\u5230\u6570\u636E\u5E93\uFF0C\u5982\u4F55\u7BA1\u7406\u7528\u6237\u4F1A\u8BDD\uFF0C\u4EE5\u53CA\u5982\u4F55\u901A\u8FC7 GraphQL API \u4ECE\u524D\u7AEF\u5230\u540E\u7AEF\u5E94\u7528\u7A0B\u5E8F\u7684\u5BF9\u8BDD\u3002</p></li><li><p><strong>\u72B6\u6001\u7BA1\u7406\uFF1A</strong> \u4F60\u5728\u8FD9\u6B21\u5B66\u4E60\u7ECF\u5386\u4E2D\u4E13\u95E8\u4F7F\u7528 React \u6765\u7BA1\u7406\u672C\u5730\u7EC4\u4EF6\u72B6\u6001\u3002\u5BF9\u4E8E\u5927\u591A\u6570\u5E94\u7528\u6765\u8BF4\uFF0C\u8FD9\u662F\u4E00\u4E2A\u597D\u7684\u5F00\u59CB\uFF0C\u4F46React\u4E5F\u6709\u5916\u90E8\u72B6\u6001\u7BA1\u7406\u89E3\u51B3\u65B9\u6848\u3002\u6211\u5728<a href="https://www.roadtoredux.com/" target="_blank" rel="noreferrer">&quot;Redux \u5B66\u4E60\u4E4B\u9053&quot;</a>\u4E00\u4E66\u4E2D\u63A2\u8BA8\u4E86\u6700\u6D41\u884C\u7684\u4E00\u79CD\u3002</p></li><li><p><strong>\u4F7F\u7528 Webpack \u548C Babel \u5DE5\u5177\uFF1A</strong> \u6211\u4EEC\u5728\u672C\u4E66\u4E2D\u4F7F\u7528 <em>create-react-app</em> \u6765\u8BBE\u7F6E\u5E94\u7528\u7A0B\u5E8F\u3002\u5728\u67D0\u4E9B\u65F6\u5019\uFF0C\u4F60\u53EF\u80FD\u4F1A\u60F3\u5B66\u4E60\u5B83\u7684\u5DE5\u5177\uFF0C\u5728\u6CA1\u6709 <em>create-react-app</em> \u7684\u60C5\u51B5\u4E0B\u521B\u5EFA\u9879\u76EE\u3002\u6211\u63A8\u8350\u4F7F\u7528 <a href="https://www.robinwieruch.de/minimal-react-webpack-babel-setup/" target="_blank" rel="noreferrer">Webpack</a> \u8FDB\u884C\u6700\u4F4E\u9650\u5EA6\u7684\u8BBE\u7F6E\uFF0C\u4E4B\u540E\u4F60\u53EF\u4EE5\u5E94\u7528\u66F4\u591A\u7684\u5DE5\u5177\u3002</p></li><li><p><strong>\u4EE3\u7801\u7EC4\u7EC7\uFF1A</strong> \u56DE\u60F3\u4E00\u4E0B\u5173\u4E8E\u4EE3\u7801\u7EC4\u7EC7\u7684\u7AE0\u8282\uFF0C\u5E76\u5E94\u7528\u8FD9\u4E9B\u53D8\u5316\uFF0C\u5982\u679C\u4F60\u8FD8\u6CA1\u6709\u3002\u8FD9\u5C06\u6709\u52A9\u4E8E\u5C06\u4F60\u7684\u7EC4\u4EF6\u7EC4\u7EC7\u6210\u7ED3\u6784\u5316\u7684\u6587\u4EF6\u548C\u6587\u4EF6\u5939\uFF0C\u5B83\u5C06\u5E2E\u52A9\u4F60\u7406\u89E3\u4EE3\u7801\u62C6\u5206\u3001\u53EF\u91CD\u7528\u6027\u3001\u53EF\u7EF4\u62A4\u6027\u548C\u6A21\u5757API\u8BBE\u8BA1\u7684\u539F\u5219\u3002\u4F60\u7684\u5E94\u7528\u7A0B\u5E8F\u6700\u7EC8\u4F1A\u6210\u957F\u5E76\u9700\u8981\u7ED3\u6784\u5316\u7684\u6A21\u5757\uFF0C\u6240\u4EE5\u6700\u597D\u73B0\u5728\u5C31\u5F00\u59CB\u3002</p></li><li><p><strong>\u6D4B\u8BD5:</strong> \u6211\u4EEC\u53EA\u5BF9\u6D4B\u8BD5\u8FDB\u884C\u4E86\u7B80\u5355\u7684\u4ECB\u7ECD\u3002\u5982\u679C\u4F60\u5BF9\u6D4B\u8BD5 Web \u5E94\u7528\u7A0B\u5E8F\u4E0D\u719F\u6089\uFF0C<a href="https://www.robinwieruch.de/react-testing-tutorial" target="_blank" rel="noreferrer">\u6DF1\u5165\u4E86\u89E3\u5355\u5143\u6D4B\u8BD5\u548C\u96C6\u6210\u6D4B\u8BD5</a>\uFF0C\u7279\u522B\u662F React \u5E94\u7528\u7A0B\u5E8F\u3002<a href="https://www.robinwieruch.de/react-testing-cypress" target="_blank" rel="noreferrer">Cypress</a> \u662F React \u4E2D\u7AEF\u5230\u7AEF\u6D4B\u8BD5\u7684\u4E00\u4E2A\u6709\u7528\u7684\u63A2\u7D22\u5DE5\u5177\u3002</p></li><li><p><strong>\u7C7B\u578B\u68C0\u67E5\uFF1A</strong> \u524D\u9762\u6211\u4EEC\u5728 React \u4E2D\u4F7F\u7528\u4E86 TypeScript\uFF0C\u8FD9\u662F\u4E00\u4E2A\u597D\u7684\u9632\u6B62 bug \u548C\u63D0\u9AD8\u5F00\u53D1\u8005\u4F53\u9A8C\u7684\u5B9E\u8DF5\u65B9\u5F0F\u3002\u6DF1\u5165\u7814\u7A76\u8FD9\u4E2A\u8BDD\u9898\uFF0C\u8BA9\u4F60\u7684 JavaScript \u5E94\u7528\u66F4\u52A0\u5F3A\u5927\u3002\u4E5F\u8BB8\u4F60\u6700\u7EC8\u4F1A\u4E00\u76F4\u4F7F\u7528 TypeScript \u800C\u4E0D\u662F JavaScript\u3002</p></li><li><p><strong>UI\u7EC4\u4EF6\uFF1A</strong> \u5F88\u591A\u521D\u5B66\u8005\u5728\u9879\u76EE\u4E2D\u8FC7\u65E9\u5F15\u5165 Bootstrap \u7B49 UI \u7EC4\u4EF6\u5E93\u3002\u5728 React \u4E2D\u4F7F\u7528\u6807\u51C6 HTML \u5143\u7D20\u7684\u4E0B\u62C9\u3001\u590D\u9009\u6846\u6216\u5BF9\u8BDD\u6846\u662F\u6BD4\u8F83\u5B9E\u7528\u7684\u3002\u8FD9\u4E9B\u7EC4\u4EF6\u5927\u591A\u4F1A\u7BA1\u7406\u81EA\u5DF1\u7684\u672C\u5730\u72B6\u6001\u3002\u590D\u9009\u6846\u5FC5\u987B\u77E5\u9053\u5B83\u662F\u9009\u4E2D\u8FD8\u662F\u4E0D\u9009\u4E2D\uFF0C\u6240\u4EE5\u4F60\u5E94\u8BE5\u628A\u5B83\u4EEC\u4F5C\u4E3A\u53D7\u63A7\u7EC4\u4EF6\u6765\u5B9E\u73B0\u3002\u5728\u4F60\u6DB5\u76D6\u4E86\u8FD9\u4E9B\u5173\u952E\u7684UI\u7EC4\u4EF6\u7684\u57FA\u672C\u5B9E\u73B0\u4E4B\u540E\uFF0C\u5F15\u5165UI\u7EC4\u4EF6\u5E93\u5E94\u8BE5\u4F1A\u66F4\u5BB9\u6613\u3002</p></li><li><p><strong>\u8DEF\u7531\uFF1A</strong> \u4F60\u53EF\u4EE5\u7528 <a href="https://github.com/ReactTraining/react-router" target="_blank" rel="noreferrer">react-router</a> \u4E3A\u4F60\u7684\u5E94\u7528\u7A0B\u5E8F\u6DFB\u52A0\u8DEF\u7531\u3002\u5728\u6211\u4EEC\u521B\u5EFA\u7684\u5E94\u7528\u7A0B\u5E8F\u4E2D\uFF0C\u53EA\u6709\u4E00\u4E2A\u9875\u9762\uFF0C\u4F46\u8FD9\u4E2A\u9875\u9762\u4F1A\u589E\u957F\u3002React Router\u53EF\u4EE5\u5E2E\u52A9\u7BA1\u7406\u8DE8\u591A\u4E2A URL \u7684\u591A\u4E2A\u9875\u9762\u3002\u5F53\u4F60\u5C06\u8DEF\u7531\u5F15\u5165\u5230\u4F60\u7684\u5E94\u7528\u7A0B\u5E8F\u4E2D\u65F6\uFF0C\u4E0D\u4F1A\u5411 Web \u670D\u52A1\u5668\u53D1\u51FA\u4E0B\u4E00\u4E2A\u9875\u9762\u7684\u8BF7\u6C42\u3002\u8DEF\u7531\u5668\u4F1A\u5728\u5BA2\u6237\u7AEF\u5904\u7406\u8FD9\u4E2A\u95EE\u9898\u3002</p></li><li><p><strong>React Native:</strong> <a href="https://facebook.github.io/react-native/" target="_blank" rel="noreferrer">React Native</a> \u5C06\u4F60\u7684\u5E94\u7528\u5E26\u5230 iOS \u548C Android \u7B49\u79FB\u52A8\u8BBE\u5907\u4E0A\u3002\u4E00\u65E6\u4F60\u638C\u63E1\u4E86 React\uFF0CReact Native \u7684\u5B66\u4E60\u66F2\u7EBF\u5E94\u8BE5\u4E0D\u4F1A\u90A3\u4E48\u9661\u5CED\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u6709\u7740\u76F8\u540C\u7684\u539F\u7406\u3002\u4E0E\u79FB\u52A8\u8BBE\u5907\u7684\u552F\u4E00\u533A\u522B\u662F\u5E03\u5C40\u7EC4\u4EF6\u3001\u6784\u5EFA\u5DE5\u5177\u548C\u4F60\u7684\u79FB\u52A8\u8BBE\u5907\u7684 API\u3002</p></li></ul><p>\u6211\u9080\u8BF7\u4F60\u8BBF\u95EE\u6211\u7684<a href="https://www.robinwieruch.de" target="_blank" rel="noreferrer">\u7F51\u7AD9</a>\uFF0C\u5BFB\u627E\u66F4\u591A\u5173\u4E8E\u7F51\u7EDC\u5F00\u53D1\u548C\u8F6F\u4EF6\u5DE5\u7A0B\u7684\u6709\u8DA3\u8BDD\u9898\u3002\u4F60\u4E5F\u53EF\u4EE5<a href="https://www.getrevue.co/profile/rwieruch" target="_blank" rel="noreferrer">\u8BA2\u9605\u6211\u7684\u7535\u62A5</a>\u6216<a href="https://twitter.com/rwieruch" target="_blank" rel="noreferrer">\u63A8\u7279</a>\u6765\u83B7\u53D6\u6700\u65B0\u7684\u6587\u7AE0\u3001\u4E66\u7C4D\u548C\u8BFE\u7A0B\u3002</p><p>\u611F\u8C22\u9605\u8BFB <em>\u300AReact \u5B66\u4E60\u4E4B\u9053\u300B</em>\u3002</p><p>Regards,</p><p>Robin Wieruch</p>',9),p=[n];function i(c,s,l,h,g,_){return r(),t("div",null,p)}const b=e(o,[["render",i]]);export{f as __pageData,b as default};
